}
.postbitlegacy .postfoot .textcontrols a.editpost, .postbit .postfoot .textcontrols a.editpost, .eventbit .eventfoot .textcontrols a.editevent {
    background:url(https://www.mpgh.net/forum/mpghv2/buttons/edit_40b-hover.png) no-repeat #252525 left;
}
.postbitlegacy .postfoot .textcontrols a.report, .postbit .postfoot .textcontrols a.report {
    background:url(https://www.mpgh.net/forum/mpghv2/buttons/report-40b-hover.png) no-repeat #252525 left;
}
.postbitlegacy .postfoot .textcontrols a.infraction, .postbit .postfoot .textcontrols a.infraction {
    background:url(https://www.mpgh.net/forum/mpghv2/buttons/add-infraction_sm-hover.png) no-repeat #252525 left;
}
.postbitlegacy .postfoot .textcontrols a.reputation, .postbit .postfoot .textcontrols a.reputation {
    background:url(https://www.mpgh.net/forum/mpghv2/buttons/reputation-40b-hover.png) no-repeat #252525 left;
}
.postbitlegacy .postfoot .textcontrols a.ip, .postbit .postfoot .textcontrols a.ip {
    background:url(https://www.mpgh.net/forum/mpghv2/buttons/ip-40b-hover.png) no-repeat #252525 left;
}
.postbitlegacy .postfoot .textcontrols a.multiquote, .postbit .postfoot .textcontrols a.multiquote {
    background:url(https://www.mpgh.net/forum/mpghv2/buttons/multiquote_40b-hover.png) no-repeat #252525 left;
}
.postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
