/* By OCHOA*/

@-moz-document domain(lawebdelprogramador.com) {
	body{
	    background-color:#222222 !important;
	}
	nav div ul li{
	    color: white !important;
	}
	nav div ul li ul li a{
	    color: white !important;
	}
	.button {
	     background-color:#333333 !important;
             color: #13c31e !important;
	}
	.wrapper_full{
	    background-color:#333333 !important;
	}
	section{
	    background-color:#333333 !important;
	}
	.forosListRow{
	    background-color:#333333 !important;
	}
	aside {
	    background-color:#333333 !important;
	}
	.content_right {
	    background-color:#333333 !important;
	}
	.breadcrumb {
	    background-color:#222222 !important;
	}
	.rellover {
	    background-color:#333333 !important;
	}
	pre {
	    background-color:#333333 !important;
	}
	.white {
	    display: none !important;
	}
	h1 {
	    color: white !important;
	}
	span {
	    color: white !important;
	}
	a {
	    color: white !important;
	}
	.link2 {
	    color: teal !important;
	}
	form {
	    color: white !important;
	}
	p {
	    color: white !important;
	}
	.title {
	    color: white !important;
	}
	#encuesta{
	    color: white !important;
	}
	div{
	    color: white !important;
	    background-color:#222222 !important;
	}
	.withImg h2, div h2 {
	    color: white !important;
	}
	td, tr {
	    background-color: #333333 !important;
	}
	.tabGroup ul {
	    background-color: #333333 !important;
	}
	.legend {
		color: lightblue;
	}
	.forosMessage .chatsUser {
		margin: 5px 5px;
		background-color: #2b2b2b;
	}
	.chatsUser.tags li:first-child {
    	background-color: inherit;
    	padding-left: 0px;
    	color: #ffff;
	}
    a {
        color: #13c31e !important;
    }
    footer {
        clear: both;
        overflow: hidden;
        margin-top: 20px;
        background-color: #222222;
    }
    .forosListIndex .new::after{
        border-color: transparent #d11414 transparent transparent;
    }
 
    .forosListIndex .new{
        background: #d11414;
    }
    .content_right li:first-child {
        background-color: #222222;
    }
    .formulario {
        font-family: Arial;
        font-size: 12px;
        color: white;
        background-color: #222222;
        border: 1px solid #C9D6F5;
        padding: 3px;
    }
    .formulario:focus {
        font-family: Arial;
        font-size: 12px;
        color: white;
        background-color: #222222;
        border: 1px solid #C9D6F5;
        padding: 3px;
    }
    .recommendForm input {
        background-color: #333;
    }
}
