/* ==UserStyle==

@name        	SuperAnimes, SuperMangas, SuperHentais UserCSS from Stake2
@namespace		https://github.com/stake2/sa-style/
@version     	1.0.0
@license     	unlicense
@preprocessor 	default
@author         Stake2

==/UserStyle== */

@-moz-document regexp("https?://www\\.(superanimes|supermangas|superhentais)\\.(org|com|site).*") {
.rows_list,
.ajax_box .ajax_title .close-icon,
.friend_sms_list,
.actionMenuUser,
.actionMenu,
.menu_btns,
#myNotify,
.actionSearch,
.next_video,
.closeSearch,
.right label img,
.friend_box_box,
.friend_box,
.friend_box_cover,
.friend_box_list_iten_box,
.friend_box_list_iten,
.perfil_sms_btn,
ul.menu li,
.btn_totaVideo,
div.epsBox div.epsBoxSobre,
div.epsBox,
.top10Link,
.favoritar,
.comment_action,
.commentPostBtn,
.commentFieldSelect,
textarea,
input,
div.menu_btns,
.socialSidebarItens,
.socialSidebarItens span,
.tPlayer,
.sendPlayer,
.genero_btn,
.last_content,
.bt_change_last_content,
.menuClienteAction,
.lineContent,
.lineContent .button,
.ajax_more_load,
.search_item_btn,
ul.menuRodape li,
.select,
.delNotify,
.js_close_alert,
.buttonLink,
.viewMoreLink,
.btVideo,
.btVideo small,
.boxAnimeSobre a li,
li.icon,
.logoRodape,
.menu_widget,
.itens:not(.active) {
    animation: animatebrighter1 0.3s;
    animation-fill-mode: forwards;
    -webkit-animation-fill-mode: forwards;
}

.rows_list:hover,
.ajax_box:hover .ajax_title:hover .close-icon:hover,
.friend_sms_list:hover,
.actionMenuUser:hover,
.actionMenu:hover,
.menu_btns:hover,
#myNotify:hover,
.actionSearch:hover,
.next_video:hover,
.closeSearch:hover,
.right label img:hover,
.friend_box_box:hover,
.friend_box:hover,
.friend_box_cover:hover,
.friend_box_list_iten_box:hover,
.friend_box_list_iten:hover,
.perfil_sms_btn:hover,
ul.menu li:hover,
.btn_totaVideo:hover,
div.epsBox:hover,
div.epsBox:hover div.epsBoxSobre,
.top10Link:hover,
.favoritar:hover,
.comment_action:hover,
.commentPostBtn:hover,
.commentFieldSelect:hover,
textarea:hover,
input:hover,
div.menu_btns:hover,
.socialSidebarItens:hover,
.socialSidebarItens:hover span,
.tPlayer:hover,
.sendPlayer:hover,
.genero_btn:hover,
.last_content:hover,
.bt_change_last_content:hover,
.menuClienteAction:hover,
.lineContent:hover,
.lineContent .button:hover,
.ajax_more_load:hover,
.search_item_btn:hover,
ul.menuRodape li:hover,
.select:hover select,
.delNotify:hover,
.js_close_alert:hover,
.buttonLink:hover,
.viewMoreLink:hover,
.btVideo:hover,
.btVideo:hover small,
.boxAnimeSobre a li:hover,
li.icon:hover,
.logoRodape:hover,
.menu_widget:hover,
.itens:not(.active):hover {
    animation: animatebrighter2 0.3s;
    animation-fill-mode: forwards;
    -webkit-animation-fill-mode: forwards;
    color: white!important;
}

.lineContent:hover {
    border: 3px dashed #494949!important;
}

.close-icon {
    position: fixed!important;
    width: 30px!important;
    height: 30px!important;
    right: 35px!important;
    top: 9px!important;
}

textarea.friend_sms_text,
input.friend_sms_text,
textarea.comment,
textarea.comment-edit,
input.comment,
input.barrBusca,
textarea.comment-reply,
textarea.commentPost,
textarea.comment-reply,
.perfil_box_photo span,
input {
    color: white!important;
    font-weight: bold!important;
    font-size: 16px!important;
}

button[type="submit"] {
    color: white!important;
    font-weight: bold!important;
    font-size: 13px!important;
}

a {
    text-decoration: none!important;
}

.perfil_box_photo h1 {
    font-weight: bold!important;
    font-style: normal!important;
    font-size: 19px!important;
}

li.btVideo.active,
li.btVideo.active small {
    background-color: #919191!important;
    color: white!important;
}

.comment_action,
.box_content {
    color: white!important;
    font-weight: bold!important;
}

textarea.friend_sms_text::-moz-selection,
textarea.comment::-moz-selection,
textarea.comment-edit::-moz-selection,
input.comment::-moz-selection,
input.barrBusca::-moz-selection,
comment-reply::-moz-selection,
commentPost::-moz-selection,
textarea.comment-reply::-moz-selection {
    /* Code for Firefox */
    color: #fff!important;
    background: #000!important;
}

textarea.friend_sms_text::selection,
textarea.comment::selection,
textarea.comment-edit::selection,
input.comment::selection,
input.barrBusca::selection {
    color: #fff!important;
    background: #000!important;
}

::placeholder {
    color: #c5c5c5!important;
}

h2.grid_title a,
a,
ul li strong,
ul li,
p,
label,
input.search_item {
    color: #fff!important;
    font-size: 15px!important;
}

div.menuActionContentBT div.active,
div.socialSidebar div.active,
div.comment_action_box small.active,
.right label img,
.buttonLink,
.genero_btn:hover {
    border-bottom: 5px solid #914ecd!important;
}

article div.grid_box,
.infoTela,
.infoTela a,
.searchBox form input {
    animation: animatebrighter1 0.3s;
    animation-fill-mode: forwards;
    -webkit-animation-fill-mode: forwards;
    font-size: 14px!important;
}

article div.grid_box:hover,
.infoTela:hover,
.infoTela:hover a,
.searchBox:hover form input {
    animation: animatebrighter2 0.3s;
    animation-fill-mode: forwards;
    -webkit-animation-fill-mode: forwards;
    color: white!important;
    font-size: 14px!important;
}

article div.grid_box a img {
    animation: animatebrighter1 0.3s;
    animation-fill-mode: forwards;
    -webkit-animation-fill-mode: forwards;
}

article div.grid_box:hover a img {
    animation: animatebrighter2 0.3s;
    animation-fill-mode: forwards;
    -webkit-animation-fill-mode: forwards;
    color: white!important;
}

article div.grid_box {
    animation: animatebrighter1 0.3s;
    animation-fill-mode: forwards;
    -webkit-animation-fill-mode: forwards;
}

article div.grid_box:hover a {
    color: #fff!important;
    font-weight: bold!important;
    font-size: 15px!important;
}

@keyframes animatebrighter1 {
    from {
        background: #626262;
    }

    to {
        background: #232323;
    }
}

@keyframes animatebrighter2 {
    from {
        background: #232323;
    }

    to {
        background: #626262;
    }
}
}
