css:
@-moz-document domain('github.com') {
/* Repo layout */

.repository-content {
  max-width: calc(1012px - 32px * 2);
  margin: auto;
  width: auto;
}

.repository-content > .gutter-condensed {
	display: flex;
	flex-direction: column-reverse !important;
}

.repository-content > .gutter-condensed > * {
	width: 100%;
}

.repository-content > .gutter-condensed .BorderGrid {
	display: flex;
	flex-wrap: wrap;
	margin-bottom: 1em;
}

/* Pagehead */

main > div:first-child {
  display: flex;
  flex-direction: column;
  align-items: center;
}

main > div:first-child > * {
  max-width: 1012px;
  width: 100%;
}

main > div:first-child nav .UnderlineNav-item.selected {
	border-bottom-color: #fff;
	border-top: 2px solid #f9826c;
	border-left: 1px solid #e1e4e8;
	border-right: 1px solid #e1e4e8;
	background: #fff;
	z-index: 2;
	border-radius: 3px 3px 0 0;
}

main > div:first-child::before {
	content: '';
	width: 100%;
	height: 1px;
	background: #e1e4e8;
	position: absolute;
	bottom: 0;
	z-index: 0;
}

/* Repo sections */

.repository-content > .gutter-condensed .BorderGrid .BorderGrid-row {
	flex: 1;
}

@media (max-width: 768px) {
	.repository-content > .gutter-condensed .BorderGrid .BorderGrid-row {
		flex-basis: 100%;
		margin: .4em auto;
	}
}

.repository-content > .gutter-condensed .BorderGrid .BorderGrid-cell {
	padding-top: 0;
	padding-bottom: 0;
	display: block;
	border: none;
}

.repository-content > .gutter-condensed .BorderGrid .BorderGrid-cell > .mt-3 {
	margin-top: 3px !important;
}

/* About section */

.repository-content > .gutter-condensed .BorderGrid .BorderGrid-row:first-child {
	display: block;
	position: relative;
	width: 100%;
	margin: 1em 0 2em 0;
	z-index: 2;
	flex-basis: 100%;
}

.repository-content > .gutter-condensed .BorderGrid .BorderGrid-row:first-child .BorderGrid-cell {
	min-width: 100%;
}

.repository-content > .gutter-condensed .BorderGrid .BorderGrid-row:first-child .mt-3 {
	margin-top: 16px !important;
}

.repository-content > .gutter-condensed .BorderGrid > div:first-child h2,
.repository-content > .gutter-condensed .BorderGrid > div:first-child h3 {
	display: none;
}

.repository-content > .gutter-condensed .BorderGrid > div:first-child p {
	display: inline !important;
	font-size: 16px !important;
}

.repository-content > .gutter-condensed .BorderGrid > div:first-child p+div {
	display: inline !important;
	margin-left: .5em;
	font-size: 16px !important;
}

.repository-content > .gutter-condensed .BorderGrid > div:first-child p+div a {
	font-weight: 400 !important;
}

.repository-content > .gutter-condensed .BorderGrid > div:first-child p+div svg {
	display: none;
}

.repository-content > .gutter-condensed .BorderGrid > div:first-child .mt-3 .muted-link {
	position: absolute;
	bottom: -16px;
	background: #fff;
	left: 0;
}

/* Releases section */

.repository-content > .gutter-condensed .BorderGrid .BorderGrid-row .BorderGrid-cell {
	max-width: 220px;
}

/* Used by section */

.repository-content > .gutter-condensed .BorderGrid .BorderGrid-row .BorderGrid-cell > h2+a.flex-items-center {
	flex-direction: column;
	align-items: flex-start !important;
}

/* Contributors section */

.repository-content > .gutter-condensed .BorderGrid .BorderGrid-row ul.flex-wrap.mb-n2 {
	overflow: hidden;
	height: 32px;
	margin-bottom: 7px !important;
}

.repository-content > .gutter-condensed .BorderGrid .BorderGrid-row .avatar-user {
	margin-bottom: 7px;
}

/* Sponsor section */

.repository-content > .gutter-condensed .BorderGrid a[aria-label^="Sponsor"] {
	margin-right: 12px;
}


/* Languages section */

.repository-content > .gutter-condensed .BorderGrid .BorderGrid-row:last-child {
	display: block;
	flex-basis: 100%;
	z-index: 1;
}

.repository-content > .gutter-condensed .BorderGrid .BorderGrid-row:last-child .BorderGrid-cell {
	min-width: 100%;
}

.repository-content > .gutter-condensed .BorderGrid .BorderGrid-row:last-child .BorderGrid-cell > h2 {
	opacity: 0;
	line-height: 0;
	user-select: none;
	pointer-events: none;
}
}
