/*左侧面板设置开始*/
.hint_common_restop,
.content_none/*没有找到*/
{
    margin-left:140px;
}
.result-op .t+div>canvas/*时间*/
{
    width:640px!important;
}
.c-group-top
,.c-group-middle
{
    margin-bottom:14px!important;
}
.hint_common_restop
,#container .result-molecule:nth-of-type(2)/*搜索工具*/
,#rs_new/*相关搜索*/
,#content_left>div[class*="result"]/*搜索结果*/
{
    background-color: rgba(255, 255, 255, .8);
    width: 640px!important;
    padding: 15px;
    border-radius: 15px;
    box-shadow: rgba(0, 0, 0, 0.3) 1px 1px 5px 1px;
}
.c-group-wrapper
{
    margin:0 0 20px 0;
}
.hit-toptip/*纠错提示*/
{
    margin-bottom:20px;
}
#container .result-molecule:nth-of-type(2)/*搜索工具*/
{
    margin-top:15px;
    margin-bottom:15px!important;
}
.c-group-wrapper>.new-pmd/*热搜榜结果*/
{
    margin-left:15px!important;
    margin-top:20px!important;
}
#op_wiseapp
{
    width:620px!important;
}
#content_left>div>div[id*="canvas"]/*头部商品广告*/
{
    margin-bottom:20px!important;
}
.c-group-wrapper
{
    box-shadow:none!important;
}
.c-container>.new-pmd
{
    padding:0!important;
    background:rgba(255, 255, 255, 0)!important;
    box-shadow:none!important;
    
}
.ec_tuiguang_pplink/*加大广告字样*/
{
    font-size:1.3em;
}
.aladdin__2AELi
,.wenda-abstract-wrap{
    border:none;
    box-shadow:none;
    background-color: rgba(255, 255, 255, 0.05);
}
#op_wiseapp_td
{
    padding:15px!important;
}
#content_left .result:hover
,#container .result-molecule:nth-of-type(2):hover/*搜索工具*/
,#content_left>div:hover/*搜索结果*/
{
    background-color: rgba(255,255,255,0.95);
    transition: background-color 1s;
}
#op_wiseapp_td:hover
{
    background-color: rgba(255,255,255,0.5);
    border-radius:25px!important;
    transition: background-color 1s;
}
/*左侧面板设置结束*/


/*右侧面板设置开始*/
#content_right{
    position:relative;
    border-style: none;
    display:block;/*隐藏右侧面板block换none*/
    top:-30px;
    left:95px;
}
.cr-content
,.ad-widget-header
,.hint_right_middle/*安全提示*/
,#content_right .c-result-content
,div[data-id]/*右侧登录面板*/
{
    background-color: rgba(255, 255, 255, .8) !important;
    padding: 25px 16px 25px 17px;
    border-radius: 15px 15px 15px 15px;
    min-width:272px;
    max-width:360px;
}
#content_right .c-result-content
,#content_right .new-pmd/*限制右侧面板宽度，防止撑大整体面板*/
{
    width:0!important;
    min-width:368px;
}
#content_right .c-result-content:hover
,.cr-content:hover
,.ad-widget-header:hover
,.hint_right_middle:hover
{
    background-color:white!important;
    transition:background 1s;
}
#content_right>div:first-child{/* 去广告 */
    display:none;
}
/*右侧面板设置结束*/


/*底部页码设置开始*/
#page{
    background-color: transparent !important;
    position:relative;
}
.page-inner {
    width:640px;
    padding-left:0!important;
    position:relative;
    left:50%;
    transform:translateX(-50%);
}
#page strong{
    background-color: rgba(51, 136, 255,0.9) !important;
    height: auto;
    border-radius: 18px;
    border: 1px solid rgb(225, 226, 227);
    box-shadow: rgba(0, 0, 0, 0.3) 1px 1px 2px 0;
}
#page strong{
    border-color: rgb(51, 136, 255);
}
#page a:hover{
    border-color: rgb(51, 136, 255);
}
#foot{
    width:calc(100% + 140px)!important;
}
/*底部页码设置结束*/

#content_left>div:not(.new-pmd)/*广告*/
{
    display:none;
}
}

