.vip-diversion-dialog-one-wrap/*vip广告*/
{
    width:580px!important;
    padding:15px;
    border-radius:15px;
}
.search-filter-wrapper:hover/*筛选面板*/
,.normal-result>div:hover/*结果列表*/
,.search-result-list-wrap>a:hover
,.search-relative-wrapper:hover/*相关搜索*/
,.baidu-search-tip-wrap:hover
,.head-recom:hover/*为您推荐*/
,.dwTXOl:hover/*广告*/
{
    background-color:rgba(244, 244, 244, 1);
    transition: background-color 1s;
}
/*左侧面板结束*/

/*右侧面板开始*/
.base-layout-content-wrap
{
    background-color:rgba(255,255,255,0.95);
    border:1px solid white;
    border-radius:15px;
    padding:15px;
    width:auto;
}
/*右侧面板结束*/

/*页码设置开始*/
.page-content a
,.page-content>span
{
    border:1px solid white;
    border-radius:15px;
    background:rgba(103, 172, 198, .8)!important;
    color:white;
}
/*页码设置结束*/


.zsj-topbar
,.mini
,#lastcell-dialog/*右下角弹窗广告*/
,.fc-first-result-wrap/*第一条广告*/
{
    display:none;
}
.bd-wrap
{
    margin-top:12px!important;
}
}
