css:
@-moz-document url(https://shield.land/) {

.main_wrapper {
    background: #272727;
}
.postCard, .dayPopularPosts {
    background:  #272727;
    border-radius: 3px;
    box-shadow: 0 0 15px rgb(92, 92, 92);
}
.postCard_comments {
    color: #f3f7fb;
}
.detailPost, .postCommentBlock {
    background: #272727;
}
.postCard_autor {
    color: #2bd681;
}
a {
    color: #f3f7fb;
}
a:hover {
    color: #f3f7fb;
}
.postCard_theme {
    color: #f3f7fb;
}
.postCard_theme:hover {
    color: #f3f7fb;
}
body, html {
    color: #f3f7fb;
}
.blogInfoTop {
    background: #272727;
    box-shadow: 0 0 15px rgb(92, 92, 92);
}
.contentBlock [data-v-204028a0] {
    background: #272727;
    box-shadow: 0 0 15px rgb(92, 92, 92);
}
.subBlogElement, .unSubBlogElement {
    background: #272727;
    box-shadow: 0 0 15px rgb(92, 92, 92);
}
.top_wrapper {
    background: #272727;
 	box-shadow: 0 0 15px rgb(92, 92, 92);
}
.blog_setting_element {
    background: #272727;
	box-shadow: 0 0 15px rgb(92, 92, 92);
}
.postSectionTop {
    background: #272727;
	box-shadow: 0 0 15px rgb(92, 92, 92);
}
.newPostOptions {
    background: linear-gradient(to bottom,#272727,rgba(246,246,247,.7));
}
.newPostSettings, .newPostContent, .publicationOptions {
    background: #272727;
}
.list-group-item {
    background-color: #272727;
}
.newPostSettings_sections_list, .newPostSettings_author_groupBlogs {
    background: #272727;
}
.newPostHeader {
    background: #272727;
}
.publicationOptionsBtns, .publicationShortPreview, .publicationShortPreview_header, .publicationDetailPreview_header, .publicationDetailPreview {
    background: #272727;
}
#mobileSidebar .modal-content {
        background: #272727;
    }
.postCard_settings_block {
    border: 2px solid rgb(92, 92, 92);
    background: #272727;
}
.postCard_settings_block.active:before {
    box-shadow: -2px -2px 0 0 rgb(92,92,92);
    background: #272727;
}


}
