css:
@-moz-document domain("baxnetwork.pl") {
:root{
    scrollbar-color: #41c5e6 #090e24;
}
.row--style-light:not(.row--background-type-image){
    background: #090e24;
}
.row--style-light .branding__link,.row--style-light .extendednavigation__item-name:hover{
    background: -webkit-linear-gradient(100deg, #39a8ff 0%, #41c5e6 54%, #49e4cb 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}
.navigation-item .item-name,.row--style-light .navigation-item:not(.navigation-item--child).selected > .extendednavigation__item-name{
    color: #fff;
}
.row--style-dark .widget .button:not(.navigation-toggle):not(.button--pagination):not(.next-button):not(.previous-button):not(.pause-toggle):not(.pswp__button):not(.row-add-button__button):not(.row-template-preview__header-close-button):not(.row-template-preview__groups-menu-item-control):not(.row-template-preview__template-add-button):not(.row-selector-panel__header-button):not(.row-collections__item-button):not(.navigation-basket-toggle):not(.paypalbuynow__button):not(.bk-cookie-button){
    border-image-source: linear-gradient(350deg, #39a8ff 0%, #41c5e6 54%, #49e4cb 100%);
    border-image-slice: 1;
}
.row--style-dark .widget .button:not(.navigation-toggle):not(.button--pagination):not(.next-button):not(.previous-button):not(.pause-toggle):not(.pswp__button):not(.row-add-button__button):not(.row-template-preview__header-close-button):not(.row-template-preview__groups-menu-item-control):not(.row-template-preview__template-add-button):not(.row-selector-panel__header-button):not(.row-collections__item-button):not(.navigation-basket-toggle):not(.paypalbuynow__button):not(.bk-cookie-button):hover{
    background: linear-gradient(350deg, #39a8ff 0%, #41c5e6 54%, #49e4cb 100%);
}
}
