css:
@-moz-document domain("www.inoreader.com")
{
/*基本 Basic*/
.article_full_contents,
.article_title {
    width: 98% !important;
}

.article_footer,
.article_content,
.article_content img {
    max-width: 100% !important;
}

/*阅读器 Article dialog*/
@media screen and (min-width: 1920px) {
    #article_dialog_wrapper {
        width: 55% !important;
        min-width: 800px;
    }

    #article_dialog {
        padding: 90px 0px 0px 0px;
    }

    #article_dialog .article_footer_placeholder_top {
        height: 80px;
        width: 52% !important;
        min-width: 760px;
    }

    #article_dialog .article_footer {
        top: 20px;
    }

    #article_dialog > .article_full_contents {
        width: 94% !important;
        margin: 0 auto;
    }
}
@media screen and (min-width: 2560px) {
    #article_dialog_wrapper {
        width: 60% !important;
        min-width: 800px;
    }

    #article_dialog {
        padding: 90px 0px 0px 0px;
    }

    #article_dialog .article_footer_placeholder_top {
        height: 80px;
        width: 57% !important;
        min-width: 760px;
    }

    #article_dialog .article_footer {
        top: 20px;
    }

    #article_dialog > .article_full_contents {
        width: 94% !important;
        margin: 0 auto;
    }
}

/*反广告 Anti-ads*/
.ad_title,
.ad_size_leaderboard,
#sinner_container,
.sinner,
.ad_footer_remove {
    width: 0px !important;
    height: 0px !important;
    padding: 0px !important;
    margin: 0px !important;
    overflow: hidden !important;
}

#reader_pane {
    padding-right: 0px !important;
}

/*列表视图 List view*/
.article_expanded:not(.article_card_nostripe):not(.article_tile) {
    width: 65% !important;
    min-width: 900px;
    margin: 0 auto !important;
}

.article_expanded:not(.article_card_nostripe):not(.article_tile) .article_content {
    max-width: 94% !important;
    min-width: 625px !important;
}

.article_expanded:not(.article_card_nostripe):not(.article_tile) .article_content img {
    max-width: 90% !important;
    min-width: 625px !important;
}

/*扩展视图 Expanded view*/
.article_card {
    width: 75% !important;
}

.article_card .article_content {
    max-width: 80% !important;
    min-width: 625px !important;
}

.article_card .article_content img {
    max-width: 75% !important;
    min-width: 625px !important;
}

/*卡片视图 Card view*/
@media screen and (min-width: 1920px) {
    div.article_tile {
        max-width: none;
        width: 380px !important;
        height: 480px !important;
    }
    .article_tile_content_wraper {
        height: 540px;
    }

    .article_tile_content_wraper .article_tile_picture {
        height: 380px;
    }
    .article_tile_content_wraper .article_tile_title {
        position: relative;
        padding: 0 10px;
        top: -4px;
        font-size: 16px;
    }

    .article_tile_content_wraper.no_pic .article_title_link {
        font-size: 18px;
    }
    .article_tile_content_wraper.no_pic {
        height: 440px;
    }
    .article_tile_content_wraper.no_pic .article_tile_content {
        font-size: 15px;
        padding: 0px 15px 0 15px;
        height: 360px;
    }
}

@media screen and (min-width: 2560px) {
    div.article_tile {
        max-width: none;
        width: 420px !important;
        height: 540px !important;
    }
    .article_tile_content_wraper {
        height: 580px;
    }

    .article_tile_content_wraper .article_tile_picture {
        height: 420px;
    }
    .article_tile_content_wraper .article_tile_title {
        position: relative;
        padding: 0 10px;
        top: -4px;
        font-size: 16px;
    }

    .article_tile_content_wraper.no_pic .article_title_link {
        font-size: 22px;
    }

    .article_tile_content_wraper.no_pic {
        height: 500px;
    }
    .article_tile_content_wraper.no_pic .article_tile_content {
        font-size: 16px;
        padding: 18px 15px 0 15px;
        height: 400px;
    }
}

.article_tile_footer {
    line-height: 12px;
    height: 32px;
    width: 100%;
    padding: 0 0 0 8px;
}

.article_tile_footer_right_buttons {
    height: 100%;
}

.article_tile_footer .icon16 {
    font-size: 12px;
}

.article_tile_footer .popularity_score_span {
    top: 0px;
}

/*杂志视图 Magazine view*/
.article_magazine {
    max-width: 1280px !important;
    height: 320px;
}

.article_magazine_picture {
    width: 350px;
    height: 280px;
}

.article_magazine_content {
    height: 280px;
    max-height: 280px !important;
}

.article_magazine_title_content {
    min-height: 256px;
}
}
