css:
@-moz-document domain("quora.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

.SiteHeader, .LoggedOutFooter,
#credential_picker_container,
.layout_2col_side,

.QuestionTopicHorizontalList,

.logged_out_related_questions_container,

#root > div > div > div:nth-child(3) > div > div > div:nth-child(1) > div:nth-child(2) > div.q-box.qu-pb--small.qu-borderBottom,
#root > div > div > div:nth-child(3) > div > div > div:nth-child(1) > div.q-box.qu-mt--small,

.qu-mb--tiny,

#root > div > div > div:nth-child(3) > div > div > div:nth-child(2),

.qu-ml--small *
{
  display: none;
}

.ContentWrapper
{
  padding-top: 30px;
}

.layout_2col_main
{
  border: none;
  margin: auto;
  width: 80%;
  float: none;
}

#mainbar, .mainbar
{
  width: 100% !important;
}

#root > div > div > div:nth-child(3) > div > div,
#root > div > div > div:nth-child(3) > div > div > div:nth-child(1)
{
    width: 70% !important;
    margin: auto;
}

/*----------------DARK THEME BELOW---------------*/

.s-btn-group,
.comments
{
  background: #323537 !important;
}

body, html,

div.q-box.qu-bg--white *,

.logged_out_page_big_signup .ContentWrapper .HomePageSignupForm
{
    background: #2d3032 !important;
}

.qu-bg--raised,

input
{
    background: #242729 !important;
}

.qu-bg--page--gray,

.FooterNav, .HomepageSignupNetworkRedirectLink:hover, .language_links:hover
{
    background-color: #2d3032 !important;
}

.signup_wall_prevent_scroll
{
  background: #0000 !important;
}

div.q-relative.spacing_log_answer_content > div > div > div > div.q-box.qu-cursor--pointer.QTextTruncated___StyledBox-sc-1pev100-0.ejgusd > div > div.q-absolute.qu-textAlign--center,

.qu-textAlign--center
{
    background: linear-gradient(rgba(255, 255, 255, 0) 0%, #2d3032 75%) !important;
}

body, h1, h2, h3, h4, h5, h6, input,
.ui_content_title--large, .QuestionPageAnswerHeader, .AnswerStandaloneActorHeader,
.ContentHeader .feed_item_answer_user .user, .ContentHeader .feed_item_answer_user .anon_user,

.Prompt .prompt_inner .prompt_title,

.qu-color--gray_dark,

.qu-fontSize--xlarge
{
  color: #ccc !important;
}

*
{
  border: none !important;
  box-shadow: none !important;
}
}
