css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.treasurydirect.gov") {

body, #searchbox label, #utilitynav a, p.maint a {color:#000!important;}

p.maint {border:solid #666666!important;color:#000!important;}

#header { border-bottom: 1px solid #333!important; }

#searchbox input { border:1px solid #333!important; }
#searchbox input.button { border:0px!important; }

#content a,#content h1, #content h2, #login a, #login li, .relatedlinks a {color:#000 !important;}

#persistentnav {background:#000 !important;}

#persistentnav li a, #persistentnav li a.nonactive, #persistentnav li a:link {background-image/**/:  url("http://www.treasurydirect.gov/images/persistentnav_link_monochrome.gif") !important; background-color:#000!important;}

#localnav { background: #969696!important; background-image: url("http://www.treasurydirect.gov/images/localnav_indiv_footer_monochrome.gif")!important;background-position: 0% 100%!important; background-repeat: no-repeat!important; }

#localnav #verticalline { background: #fff; background-image: url("http://www.treasurydirect.gov/images/monochrome_verticalline.gif")!important; background-position: 0% 0%!important;}

#localnav li a {color:#000!important;}

#localnav li.closed { background-image: url("http://www.treasurydirect.gov/images/monochrome_localnav_closed.gif")!important; background-position: 4px 5px; background-repeat: no-repeat; border-bottom: 1px dotted #999!important;
}

#localnav li.closed a, #localnav li.closedlast a, #localnav li.open .subindent a, #localnav li.closed a:visited, #localnav li.closedlast a:visited, #localnav li.open .subindent a:visited { color: #000!important; }

#localnav li.closedlast { background-image: url("http://www.treasurydirect.gov/images/monochrome_localnav_closed.gif")!important; background-position: 4px 5px; background-repeat: no-repeat; border-bottom: 1px solid #000!important;
}

#localnav li.open {
	background-image: url("http://www.treasurydirect.gov/images/monochrome_localnav_open.gif")!important; background-position: 4px 5px; background-repeat: no-repeat; border-bottom: 1px dashed #ccc!important; }

#localnav li.openlast { background-image: url("http://www.treasurydirect.gov/images/monochrome_localnav_open.gif")!important; background-position: 4px 5px; background-repeat: no-repeat; border-bottom: 1px solid  #ccc!important; }

#localnav li.open .subindent, #localnav li.openlast .subindent {
	background-image: url("http://www.treasurydirect.gov/images/monochrome_bluecarrot3.gif")!important; background-position: 2px 5px; background-repeat: no-repeat; color: #333!important; }

#localnav li.open .subindent2, #localnav li.openlast .subindent2 { background-image: url("http://www.treasurydirect.gov/images /monochrome_orangecarrot.gif")!important; background-position: 2px 1px; background-repeat: no-repeat; color: #333!important; }

#localnav li.open .subindent2 .subset, #localnav li.openlast .subindent2 .subset { background-image: url("http://www.treasurydirect.gov/images/monochrome_bluecarrot3.gif")!important; background-position: 2px 5px; background-repeat: no-repeat; color: #333!important; }

#localnav li.open .subindent2 .subsetactive, #localnav li.openlast .subindent2 .subsetactive { background-image: url("http://www.treasurydirect.gov/images /monochrome_orangecarrot.gif")!important; background-position: 2px 1px; background-repeat: no-repeat; color: #000!important; }

#localnav h1 {background-image: url("http://www.treasurydirect.gov/images/localnav_indiv_header_monochrome.gif") !important;}

#localnavacctcntr h1 {color:#fff!important;}

#localnav h2 {background-color: #eee!important; color:#000!important;}

#localnav h2.subhead {background:#ccc!important;}

#indxlst {background:#eee!important;}
#indxlst ul li {border-top:1px dashed #000!important;background-image: url(http://www.treasurydirect.gov/images/monochrome_bluecarrotx.gif)!important;}
#indxlst ul li#last {border-top:1px dashed #000!important;border-bottom:1px dashed #000!important;}
#indxlst ul li a, #indxlst ul li#last a {color:#000!important;}

#login ul#openacct {border-top:1px solid #000!important;}

#localnav #login ul li { background-image: url("http://www.treasurydirect.gov/images/monochrome_blt_orange.gif")!important; background-position: 0% 0%; background-repeat: no-repeat; color: #333; }

#persistentnav li a:hover {background-image/**/:  url("http://www.treasurydirect.gov/images/persistentnav_hover_monochrome.gif") !important;background-color:#969696!important;}

#persistentnav li a.active {background-image/**/:  url("http://www.treasurydirect.gov/images/persistentnav_active_monochrome.gif") !important;background-color:#888!important;}

#login {background:#fff!important;}

#content .indepth { border-collapse: collapse; border: 1px solid #666666!important; font-size: 0.8em; margin: 7px 0px 7px 0px; padding: 0px; width: 98%; }

#content .indepth th {  border-right: 1px dashed #ccc; border-bottom: 1px dashed #ccc; color: #000!important; padding: 2px; text-align: center;}

#content ul.ofsubhead li { list-style: none none; background-image:  url("http://www.treasurydirect.gov/images/monochrome_blt_orange.gif")!important; background-repeat: no-repeat; background-position: 0px 4px; padding: 2px 0px 0px 15px; margin: 0px; }

#content ul.datelist li { list-style: none; background-image:  url("http://www.treasurydirect.gov/images/monochrome_blt_orange.gif")!important; background-repeat: no-repeat;  	 background-position: 0px 2px; padding:0px 0px 0px 15px; margin: 0px 0px 0px 0px; }

#content table.pr1 { border:1px solid #666666!important; border-collapse: collapse; }

#content table.pr1 thead th { font-weight: bold; color: #fff; text-align: center; background:#999!important; border-left:1px dashed #ccc; border-bottom:1px solid #333; padding:4px;}

#content ul.orangebullet li { background-image:  url("http://www.treasurydirect.gov/images/monochrome_orangebullet.gif")!important; background-repeat: no-repeat; background-position: 0px 8px; line-height: 1.5em; padding: 0px 0px 10px 15px; }

#logo img { content: url("http://www.treasurydirect.gov/images/td_logo_monochrome.gif")!important; }



.note_orange {color:#000!important;}



#right ul.furtherinfo li { background-image: url("http://www.treasurydirect.gov/images/monochrome_blt_blwcarrot.gif")!important; background-repeat: no-repeat; background-position: 0% 5px; margin: 0px 0px 0px 5px; padding: 4px 0px 2px 10px; font-size: 0.9em; }

#right ul.furtherinfosub li { background-image: url("http://www.treasurydirect.gov/images/monochrome_blt_doubleblsolidcarrot.gif")!important; background-repeat: no-repeat; background-position: 0% 8px; margin: 0px 0px  0px 5px; padding: 4px 0px 2px 18px; font-size: 0.8em; }

#right ul.furtherinfosub li a {color:#000!important;}

#right ul.relatedlinks li { background-image: url("http://www.treasurydirect.gov/images/monochrome_blt_blwcarrot.gif")!important; background-repeat: no-repeat; background-position: 0% 5px; margin: 0px 0px 0px 0px; padding: 4px 0px 2px 10px; font-size: 0.8em; }
#currenttopics a, .exclaim a {color:#000!important;}
.exclaim { margin-top: 1em; background-image: url("http://www.treasurydirect.gov/images/monochrome_exclaim.gif")!important; background-position: 0% 0%; background-repeat: no-repeat; padding: 0 0 0 2em; line-height: 1.5em; font-size: .9em; font-weight: bold;}

input.action { background-color: #fff!important;  border: 1px solid #000!important; color: #000!important;  float: left; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: .9em; font-weight: bold; margin: 0px 7px 5px 0px; padding: 1px 2px 1px 2px; overflow: visible; }

input.danger { background-color: #f00!important;  border: 1px solid #f00!important; color: #fff!important;  float: left; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: .9em; font-weight: bold; margin: 0px 7px 5px 0px; padding: 1px 2px 1px 2px; overflow: visible; }


input.linkbutton {background-color: #000!important; border: 0px; color: #fff; font-family: Verdana, Arial, Helvetica, sans-serif; padding: 0px 3px 0px 3px; }

#content table.data1 { 	border-collapse: collapse; border: 1px solid #999!important; }
#content table.data0 { border-collapse: collapse; border: 1px solid #999!important; width: auto; }
#content table.data1 tr th, #content table.data0 tr th { background-color:#000!important; color: #fff; border-right:1px dashed #fff; border-bottom: 1px solid #ddd; font-size: 1.0em; padding: 5px 5px 5px 5px; border: none; text-align: center; }
#content table.data1 tr td.orange, #content table.data0 tr td.orange { color:#f00!important; }
#content table.data1 tr th.catbt, #content table.data0 tr th.catbt { background-color:#ddd; color: #000; text-align: left; border-right:1px dotted #fff; border-bottom: 1px solid #999!important; }




tr#rate_header {background:#eee!important;}
th.left_headers, th.other_headers {color:#000!important;}
a.rate_table {color:#000!important;}
a.rate_table:hover {color:#666666!important;}

a.save, a.save:link, a.save:visited, a.save:hover { background-color: #000!important;  color: #fff!important; display: block;  float: right; font-size: .9em; font-weight: bold; margin: 0px 7px 0px 0px; padding: 2px 3px 3px 3px; text-decoration: none; }

#footer a {color:#000!important;}

}
