css:
@-moz-document domain("start.firefoxchina.cn") {
    *{
        font-family: Helvetica, sans-serif;
    }
	body{
	    background:#d9d9d9;
	}
	.wrapper{
		width:760px;
	}
    .firefox-logo{
		width:102px;
		height:102px;
		padding:20px;
		border-radius: 112px;
		background: #d9d9d9;
		box-shadow:  11px 11px 22px #c5c5c5, -11px -11px 22px #ededed;
    }
	.search{
        padding:30px 20px;
    	height:100px;
		margin: 0 auto;
    	box-sizing:border-box;
		border-radius: 31px;
		background: #d9d9d9;
		box-shadow:  19px 19px 38px #d0d0d0, -19px -19px 38px #e2e2e2;
	}
    /* 输入组件容器 */
    .engine-wrapper{
        min-width:850px;
    	border:none;
    }
    .engine-key-wrapper{
    	width:500px;
		float:left;
        background:transparent;
    }
     /* 输入框容器 */
	.engine-action{
    	background:transparent;
    	width:auto;
		float:left;
    	margin-left:20px;
        box-sizing:border-box;                                
	}
	/* 输入引擎选择弹出框 */
	#engine-logos-list{
		width:44px;
		background:transparent;
		border:none;
		box-shadow:none;
		top:70px;	
	}
	#engine-logos-list p{
		margin-top:10px;
		padding:0;
		border-radius: 50%;background: #d9d9d9;
		box-shadow:  11px 11px 22px #c5c5c5, -11px -11px 22px #ededed;
	}
	.baidu-logo{
		background-position:3px 1px !important;
	}
	.google-logo{
		background-position: 3px -39px !important;
	}
	.taobao-logo{
		background-position: 3px -78px !important;
	}
	
    /* 输入框本体 */
    #search-key{
        box-sizing:border-box;
    	width:500px;
        text-align:center;
		padding: 12px 10px 10px 20px;
        font-size:20px;
		border-radius: 50px;
		background: linear-gradient(145deg, #cfcfcf, #e2e2e2);
		box-shadow:  14px 14px 28px #cccccc,  -14px -14px 28px #e6e6e6;
		overflow:hidden;
		white-space:nowrap;
		text-overflow:ellipsis;
		transition:0.5s ease-out;
    }
	#search-key:focus{
		border-radius: 31px;
		background: linear-gradient(145deg, #cfcfcf, #e2e2e2);
		box-shadow:  5px 5px 10px #cccccc, -5px -5px 10px #e6e6e6;
	}
    .engine-logos{
    	width:80px;
    	float:left;
		border-radius: 50px;
		background: #d9d9d9;
		box-shadow:  9px 9px 18px #cccccc,  -9px -9px 18px #e6e6e6;
    }


    /* 搜索按钮 */
    .btn-engine-bg{
    	position:static !important;
    	margin-left:20px;
        float:left;
		right:0;
		overflow:hidden;
        border-radius: 50px;
		background: #fe7815;
		box-shadow:  9px 9px 18px #cccccc,  -9px -9px 18px #e6e6e6;
    }

    #search-submit{
		text-align:center;
        text-indent:0;
		background-image:none;
		font-size:20px;
		font-weight:500;
    }
	#search-submit:active{
		background: linear-gradient(145deg, #db6812, #fe7815);
	}
	 /* 搜索候选框 */
	#engine-tip-show{
		top:50px;
		background:#d9d9d9;
		border-radius:10px;
		overflow:hidden;
	}
	/* 友情链接 */		
	.mod-delay-resource{
		display:none !important;
	}
}
