css:
@-moz-document domain("www.baidu.com") {
    #kw {
        font-size: 18px;
    }

    * {
        margin: 0;
        padding: 0;
        box-sizing: content-box;
    }

    html,
    body {
        height: 100%;
    }

    #wrapper {
        min-height: 100%;
    }
}

@-moz-document url("https://www.baidu.com/") {

    /* 百度首页背景图片 */
    #wrapper {
        background-image: url("/*[[background-image]]*/") !important;
        background-repeat: no-repeat;
        background-attachment: fixed;
        background-size: cover;
    }

    /* 头部 */
    .s-top-left .mnav {
        margin-top: 6px;
    }


    #s_top_wrap {
        height: 30px;
    }

    .s-skin-hasbg .s-top-wrap {
        background: rgba(255, 255, 255, .2);
    }

    /* 搜索框 */
    .bdsug-new {
        top: 37px;
        border: none !important;
    }

    .bdsug-new ul {
        border-top: none !important;
    }

    #kw {
        background: rgba(255, 255, 255, .8) !important;
        border: 2px solid rgba(255, 255, 255, 0) !important;
    }

    .soutu-btn,
    #bottom_layer,
    #u1 {
        display: none;
    }
}

@-moz-document url-prefix("https://www.baidu.com/s?") {

    /* 搜索结果背景图片 */
    #wrapper {
        background-image: url("/*[[background-image]]*/") !important;
        background-repeat: no-repeat;
        background-attachment: fixed;
        background-size: cover;
    }

    #s_tab {
        margin-bottom: 22px;
        background: rgba(255, 255, 255, .75);
    }

    #s_tab a {
        transition: all .25s;
    }

    .c-border {
        padding-top: 0 !important;
        box-shadow: none !important;
    }

    .c-gap-bottom-small.f13,
    table.result-op,
    .result.c-container,
    .result-op.c-container {
        width: 560px;
        background: rgba(255, 255, 255, .8);
        padding: 24px;
        border-radius: 3px;
        transition: all .15s;
    }

    .result.c-container:hover,
    .result-op.c-container:hover {
        box-shadow: 5px 5px 5px rgba(0, 0, 0, .15);
    }

    .content_none {
        width: 700px;
        background: rgba(255, 255, 255, .8);
        padding: 24px !important;
        border-radius: 3px;
        transition: all .15s;
        margin: 60px auto;
    }

    /* 搜索结果下划线 */
    .t a {
        font-size: 17px !important;
        font-weight: 500;
    }

    .t a,
    .t a em {
        text-decoration: none !important;
    }

    .t a {
        transition: all .1s ease-out;
        position: relative;
        box-sizing: content-box;
        padding-bottom: 2px;
        color: #3951b3;
    }

    .t a:hover {
        color: #4e6ef2;
    }

    .t a::after {
        transition: all .2s ease-out;
        content: '';
        position: absolute;
        background: #4e6ef2;
        bottom: 0;
        left: 0;
        height: 2.5px;
        width: 0;
        border-radius: 3px;
    }

    .t a:hover::after {
        width: 100%;
    }

    /* 去除推广 */
    #content_right,
    #rs,
    #foot,
    .sftip_com.sftip_blue,
    .wenda-abstract-showurl-video-new-style,
    .c-img-border.c-img-radius-large,
    .hint_common_restop,
    .c-recommend {
        display: none;
    }
}

@-moz-document url-prefix("https://www.baidu.com/s?") {

    #head,
    #s_tab,
    #page {
        background: rgba(255, 255, 255, .6) !important;
        backdrop-filter: blur(15px) !important;
    }

    .page-inner {
        padding-left: 0 !important;
        width: 560px !important;
        margin: 0 auto !important;
    }

    .s_form {
        padding: 0 !important;
    }

    .s_form_wrapper>form {
        float: none;
        margin: 20px auto 10px auto !important;
        width: 675px !important;
    }

    .s_form_wrapper>a {
        position: absolute !important;
        top: 0;
        left: 500px;
        margin-top: 20px !important;
    }

    #s_tab {
        padding: 90px 0 0 0 !important;
    }

    #s_tab>.s_tab_inner {
        width: 720px;
        margin: 0 auto;
    }

    .s_down {
        height: 80px;
    }

    #result_logo {
        display: none;
    }
}

@-moz-document url-prefix("https://www.baidu.com/s?") {
    .head_nums_cont_outer.OP_LOG {
        width: 1300px;
        margin: 0 auto;
    }

    .head_nums_cont_inner {
        top: 0 !important;
    }

    .nums,
    .search_tool_conter {
        width: 560px;
        background: rgba(255, 255, 255, .8);
        border-radius: 3px;
        transition: all .15s;
        padding: 0 24px;
    }

    .nums {
        margin-left: 0 !important;
    }

    .search_tool_conter {
        float: right;
    }

    .c-tip-timerfilter-si>ul {
        width: 208px !important;
    }

    .search_tool,
    .search_tool_close {
        display: none;
    }
}

@-moz-document url-prefix("https://www.baidu.com/s?") {
	/* 双列显示 */
	#container {
		width: 100%;
		margin: 20px 0 !important;
	}

	#content_left {
		box-sizing: content-box;
		padding: 0 !important;
		width: 1300px !important;
		margin: 0 auto !important;
		display: block !important;
		column-count: 2;
		column-gap: 80px;
		float: none;
	}

	.result,
	.result-op {
		display: inline-block;
		margin: 6px 0 !important;
	}
}
