css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bing.com") {

/* home no vertical center */
.layout {vertical-align: top !important;}

/* no sponsors */
#sidebar, .sb_adsW {display: none !important;}

/* larger search input */
#sb_form_q, #sb_form2_q {width:90% !important; min-width:28em !important;}
#sb_form_go, #sb_form2_go {float:right !important;}
.sw_bd {width:66% !important; }
.sw_b {width:100% !important; min-width:32em !important;}
#sw_hdr {min-width: 400px !important; width: 100% !important;}

/* no horizontal scroll bar !!! */
body.WideView, .Content {min-width: 400px !important; width: 100% !important;}
body {min-width: 400px !important;}

/* maps type search */
.scopeTabs {top: 24px !important;}
.scopeTabsSelectedNB, .SubScopeSwitch {color: #FFFFFF !important;}

/* better results render */
#sw_main #content, #results {max-width: 80% !important; padding:0 !important;}
#results_area, #results_container {width:100% !important;}

/* best result found */
.ansC {border:solid 1px; border-left-width:3px !important; border-color:#DDCC88; padding:10px; padding-bottom:1px; -moz-border-radius:10px !important; background-color:#FFFFDD; margin-left:-12px !important; margin-bottom:10px;}
.ans3 {max-width: 90% !important;}

/* round inputs and forms */
.sw_b {-moz-border-radius:6px !important;}
#sb_form_go, #sb_form2_go, li a, .ic a img, #scroll_panel {-moz-border-radius:4px !important;}

/* numbered results */
#results_container #results ul li {list-style-type:decimal !important;}

/* copyright photo */
#sh_rdiv span {position: relative !important; background: none !important; border: none !important; visibility: visible !important; top: 0.3em !important; padding: 0px !important; opacity:1.0 !important; font-weight: bold !important;}
#sh_rdiv {bottom: 0.5em !important; background: -moz-rgba(255,255,255,0.4) !important; -moz-border-radius:10px !important; white-space: nowrap !important; height: 20px !important; padding-right:30px !important;}
#sh_rdiv:hover { background: -moz-rgba(255,255,255,0.8) !important; }
#sh_cp {color: #000000 !important;}

/* Photo buttons */
#sh_igl {position: absolute !important; right: 16px !important; z-index: 10 !important;}
#sh_igl span {display: none !important;}
#sh_igl div {border: none !important; margin: 0px !important; padding: 2px 0px !important; color: -moz-rgba(0,0,0,0.6) !important;}
#sh_igl div:hover {color: -moz-rgba(255,120,0,1.0) !important;}
#sh_igl div.sc_lightdis:hover {color: -moz-rgba(0,0,0,0.5) !important;}

#sh_igr {position: absolute !important; right: 2px !important; z-index: 10 !important;}
#sh_igr span {display: none !important;}
#sh_igr div {border: none !important; margin: 0px !important; padding: 2px 0px !important; color: -moz-rgba(0,0,0,0.6) !important;}
#sh_igr div:hover {color: -moz-rgba(255,120,0,1.0) !important;}
#sh_igr div.sc_lightdis:hover {color: -moz-rgba(0,0,0,0.5) !important;}
}
