css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nytimes.com") {

/* Adblocking */
   *[class^="ad"],
   *[class*=" ad"],
   *[class*="Ad"],
   *[class*="Sponsor"],
   *[id^="ad"],
   *[id*=" ad"],
   *[id*="Ad"],
   *[class^="spon"],
   *[id^="spon"],
   *[class^="Spon"],
   *[id^="Spon"]
	{display: none !important;}

/* Positioning */
   body {padding: 20px 0 !important;}

/* Remove clutter from header */
   #TP_container_shadow,
   #TP_container,
   #homepageTools,
   #lastUpdate,
   #login,
   #memberTools,
   #mainTabs,
   #toolbar
	{display: none !important;}
   #masthead {padding: 8px !important; border-bottom: 1px solid #D3D3D3 !important;}
   #footer {border-top: 1px solid #D3D3D3 !important;}

/* normalize special tabs */
   #navJobs,
   #navRealestate,
   #navAutos,
   #navAutomobiles
	{background: none !important; font-weight: normal !important; font-family: georgia,"times new roman",times,serif !important;}

/* Articles */
#article .columnGroup:not(.first),
.closing .cColumn {display: none !important;}
#abColumn {width: auto !important;}
.spanAB {background: none !important;}

}
