css:
@-moz-document domain("dmzj.com") {
body{
  /*background:url("https://s2.ax1x.com/2019/08/15/mAXYNt.jpg"); */
	background:#a7abb7;
		overflow: overlay;
}

.leftmiddle, .blue_bg, .read_mend_back, .week_foot, .wrap, .week_mend_nav, .topnav,.bbg, .footer, .intro_top{/*本体背景*/
    /*background: #272833;*/
	background: rgba(255,255,255,.6);
    backdrop-filter: blur(7px);
}

.read_mend, li.rank_tab, li.rank_tab2, .s-nav3, .h2_title,.tcaricature_block2 ul, .topnav li a,.topnav2 li img, .blank-more-important:hover, .book_open, .week_mend, .work_mend_mr_a, .odd_anim_title_l, .odd_anim_title_m, .odd_anim_title_r, .cartoon_online_button li.b1, .cartoon_online_border, .reply_content, .commentBox .textareaDiv,.commentBox .textareaDiv .textare{/*较浅背景*/
	background: rgba(228, 228, 228, 0.6);
}

.blank-more-important, .blank-more-important:hover, .h2_title2, .read_top_log{/*标题*/
	background: rgba(255,255,255,.6);
    color: #a90a0a;
    padding: 8px 0 0 8px;
    text-align: left;
    font-family: "微软雅黑";
    font-size: x-large;
    height: 20px;
    font-weight: 500;
    width: auto;
    border: 0px;
    margin-top: 20px;
    line-height: inherit;
    border-radius: 0 14px 14px 0;
}

.topnav2 li span,li.rank_tab2 a, li.rank_tab, 
.top_list a,
a,
.book_con_bottom,
.btm_chapter_btn,.tcaricature_block
{
    color: #484848;
    font-family: "PingFang SC","蘋方-簡 中粗體","微软雅黑";
    font-size: 13.2px;
    font-weight: 800;
}

.tcaricature_see1_a2_b, .today_recommended_bigpic2_a2, .gray12, .black_font12, .mainNav_in_l a, .mainNav_in_r ul li a, .book_open .book_open_tabs>li.font, 
.book_open .book_con_li li a.book_title,
.path_lv3, .subscribe_num, .linehidden2, .odd_anim_title_m, .line_height_content, .graybold, .adivhidden, .content_r .text,input.search_sr,.commentBox .textareaDiv .textare
{color: #595959;}

.tcaricature_block ul li img, .topnav2 li img, .footer, .book_con_bottom, .book_open .book_open_tabs, .slider-wrap,
.today_recommended_bigpic2_a1, .today_recommended_bigpic2_a, .today_recommended_bigpic2_b2, .cartoon_desc, .cartoon_online_border, .cartoon_online_button, .commentBox, .btm_chapter_btn{border:0px}/*清除边框*/
.mainNav_inner{
	width:auto;
}
.book_wrap{
	right:13px;
	left: unset !important;
}
/*::-webkit-scrollbar {
    width: 5px;
    height: 0px;
}

::-webkit-scrollbar-thumb {
    background: linear-gradient(90deg, rgb(97, 97, 97) 0%, rgb(119, 119, 119) 50%, rgba(255, 255, 255, 0) 50%);
}*/
a.update_link:link, a.update_link:visited {
	color: #ded6da;}
}

@-moz-document regexp("https?://(www\\.)?(manhua\\.)?dmzj.com.*") {
.read_mend, ul.topnav.switch_tab{margin-bottom:10px;}
.read_top, .top10_top, .blank-more-important, .read{width:228px}
.red_font12{font-family:"思源黑体","微软雅黑";line-height:22px;}

.right{margin-top: 56px;width: 239px;float: left;background-color: #fcfcfc;padding: 0px 1px 0 1px; }
.right_mr{margin:0 !important;}

.mainNav, .book_wrap{position:fixed; z-index: 10;
    background: rgba(255,255,255,.6);
    backdrop-filter: blur(7px);}
.mainNav_in_l{margin-left: 295px;}
.search_b_box{    
	position: fixed;
    top: 0px;
    margin-left: -34px;
    z-index: 11;
    width: 270px;}
.mainNav_in_l a:hover {
    color: #1b203c;
    height: auto;
    line-height: unset;
    margin: 0 0;
    padding: 2px 4px 0px 4px;
    border-bottom: #1b1b1b solid 5px;
    border-radius: 3px;
    text-decoration: unset;
}
.mainNav_in_l a{margin:0 4px;}
.week_foot{height:0px; width:auto;}

.h2_title {margin-bottom: 21px;}
.h2_title h2{
font-size: x-large;
    position: relative;
    bottom: 21px;
    left: 5px;
}
.h2_title a{font-size:5px;}
.today_recommended>.h2_title a {font-size: 17px;position: absolute;}

.tcaricature_block ul li img:hover, .topnav2 li img:hover{
position: relative;
    z-index: 200;
    top: 0;
    left: -24px;
    height: auto;
    max-height: 247px;
    width: auto;
}

.tcaricature_block2 ul{margin: 1px;width: 100px;padding:0;height: 242px;}
.tcaricature_block2 ul:hover {
	background: #efefef;
}
.tcaricature_block{margin: 18px 0 0 42px;}

.topnav2, #category_recom_0{margin:0 0 52px 42px;}
.topnav li a{border:unset;}
#category_recom_0>ul>li, .topnav2 li{padding:0;}
.topnav2>li{height: 189px;}
ul.topnav.switch_tab {margin-left: 25px;}

.twoer_index-li {background-color: #e3e3e3;}

.blank-more-important:hover{text-align:left;}
.week_mend_nav .s-nav3{background:unset;}
.week_mend_nav2>ul>.rank_tab2{width:52px;background:unset;}
.week_mend_nav li{width:70px;}

input.search_sr:focus {outline: none;}
input.search_sr {
    height: 19px;
    width: 184px;
    padding-left: 12px;
    border: none;
    border-top-left-radius: 9px;
    border-bottom-left-radius: 9px;
}
.search_input{
border-radius: 0 12px 12px 0;
    border-width: 0px;
    height: 19px;
    background: coral;
    top: 0px;
    left: 2px;
}
.mainNav{
    height:37px;
    border:0px;
		width:101%;
}


.book_open .book_con_li li{color:#8f97c3}
.book_open .book_con_li li a.book_title.wid {width: 140px;color: inherit;}
.book_open .book_con_li li.li_bg, .book_open .book_con_li li:hover {
    background: #ebeff2;
    color: #29101c;}
.book_open .top{left:0;}


.week_mend_back{background:#dcdde2}

.msg>a, .rss{font-size: small;}

.middleright{margin-top:14px;}
.anim_title_text h1 {
    font-size: 100%;
}

.margin_top_10px, div#type_comics{margin-top:5px;}

.cartoon_online_button li.b1 {
    border-style: none none solid none;
    border-bottom-color: #383a4c;
}

.part_collection_bnt{background:#dedede;}
.part_collection_bnt:hover{background:#e6200e}

div#type_comics, div#type_comics>ul{width: auto;}
div#type_comics ul li img:hover{width:160px;left:0;}

#hd,.impunity{border:0 !important;}

.display_graybg {
    position: absolute;
    top: -200px;
}
.newpic_bgno {
    background-color: #eeefff;
        height: 154px;
}
.tcaricature_new .h2_title ,.caricature_see .h2_title ,.today_recommended .h2_title{
    background: linear-gradient(0deg, rgba(21, 77, 255, 0.85) 0%, rgba(8, 127, 245, 0.75) 20%, rgba(72, 180, 251, 0.3) 17%, rgba(31, 184, 255, 0) 100%);
    height: 6px;
	width: 243px;
}
.anim-main_list td, .anim-main_list th{
	border:unset;
}
.anim-main_list table ,.anim-main_list th{
    background: #dedede none repeat scroll 0 0;
}
.leftmiddle {
    margin-top: 56px;
}
body{min-height:1920px;}
}

@-moz-document domain("dmzj.com") {
/**广告,常年无变化的东西**/
.caricature_nav,div.search_b_box>form>span>label,div.search_b_box>form>.search_botton,.float_code>img,iframe.share_self,.header-box,.display_graybg funcdiv,.hotrmbox,
.footer,.recom_one,.icorss_acg,.h2_title2, .h2_icon,.flink-box,div#bdshare,.today_recommended_bigpic2_b2 .gray12,.today_recommended_bigpic2_b1 .gray12,.today_recommended_bigpic2_b .icon20,.rss,div#type_comics+a,
.h2_title > span:nth-of-type(2),#type_comics,.intro_top,.read_week:nth-of-type(1),.footer+div,div#note,iframe,.read_week:nth-of-type(2),.header-box+script+div
,#HMrichA,#HMRichBox,.btmBtnBox+div,.btmBtnBox+div+script+div,.tcaricature_new
{
	display:none !important;
}

.toppic_content
{top: -500px;position: absolute;}
.weiboShow .weiboShow_wrap{background: #272834;}
.read,.read_top,.read_mend_mr_a,.top10_top{background:#fff0}
.book_open .top{background: unset;}
/**********************************/
}

@-moz-document regexp("https?://manhua.dmzj.com/[0-9a-zA-Z]+/\\d+.*") {
.btm_chapter_btn:hover{opacity: 1;}
.btm_chapter_btn{
    position: fixed;
    bottom: 0px;
    top: 0px;
    width: 200px;
	height:auto;
    line-height: unset;
    text-align: center;
    background: rgba(255,255,255,.6);
    backdrop-filter: blur(7px);
	opacity:0;
	color:#fff0;
}
.btm_chapter_btn.fl  {left:0;} 
.btm_chapter_btn.fr {right: 0;}
.btm_chapter_btn:visited {
  color: #992957;
}


.display_middle:hover {
	background: rgba(255,255,255,.6);
    backdrop-filter: blur(7px);
    height: 29px;
    opacity: 1;
}

.display_middle {
    bottom: 0px;
    position: fixed;
    color: #ec136f;
    font-size: 14px;
    font-weight: bold;
    z-index: 101;
    left: 0;
    right: 0;
    margin: auto;
    line-height: 19px;
    height: 29px;
    opacity: 0;
    transition: all 1s;
    transition-timing-function: cubic-bezier(0.28, 1.01, 0.64, 1.03);
}

.display_graybg+*>div:nth-of-type(1){
    position: fixed;
    z-index: 20;
    left: 7px;
    top: 8px;
color: #272a39;
}
#center_box{
	margin-top:30px;
}
div#center_box img {
    padding:0 !important;
    border:0 !important;
    border-radius: 4px;
    box-shadow:0 0 14px #a7abb7;
}
.mainNav {
    opacity: 0;
    transition: opacity 0.5s;
    background: rgba(255,255,255,.6);
    backdrop-filter: blur(7px);
}
.mainNav:hover {
  opacity: 1;
}
.inner_img{
    margin-top: 0px !important;
}
.curr_page {
    height: 20px;
    line-height: 20px;
}
#app_manhua{display:none !important;}

.footer+*,div#note {
    height: 19px !important;
    bottom: -32px !important;
}
body{min-height:1920px;}
}

@-moz-document url-prefix("https://i.dmzj.com/") {
body{
	min-width:unset;
}
.ucenter_wrap,.mainNav_inner,.ucenter_warp_r{
	width:auto;
}
.mainNav, .book_wrap{
	position:fixed;
	z-index: 10;
    background: rgba(255,255,255,.6);
    backdrop-filter: blur(7px);
}
.ucenter_warp_l,.ucentop,.foot,.sub_content .Tip{display:none;}
.ucenter_wrap .autoHeight{
	margin:100px 0 0 90px;
	border-top-left-radius: 5px;
}
.ucenter_warp_r .autoHeight{
	margin:0px 0 0 0px;
}
.sub_center_h,.sub_potion,.ucenter_warp_r .autoHeight,.letter_option li,.sub_potion li,.dy_content_li .dy_img{
	border:unset;
		width: auto;
}
.sub_content .letter_option {
	width: 958px;
}
.letter_option li {
    background: #ecf6ff;
	margin:0 2px 0 4px;
	border-radius: 3px;
}

.independNav .mainNav_in_l a, .independNav .mainNav_in_r ul li, .independNav .mainNav_in_r ul li a,.independNav .mainNav_in_r a.c_1{
	color:#202124;
}
}

@-moz-document regexp("https://www.dmzj.com/view/\\w+/\\d+.html(.*)?"), regexp("https://www.manhua\\w+.com/.+") {
.comic_last.autoHeight,.img_info{
    display:none;
}
.wrap_last_head .head_wz,.wrap_last_mid,.wrap_last_head,.foot-detail .foot-detail-wrap,.mainNav_inner{
    width:auto;
}
.pre>a, .next>a {
    box-shadow: 0px 0px 6px #adadad;
    background: rgba(255,255,255,.6);
    backdrop-filter: blur(7px);
	width: 200px;
    color: #f5f5dc00 !important;
	position:fixed;
	top:0px;
    bottom:0px;
    z-index:20;
	opacity:0;
}
.pre>a:hover,.next>a:hover{
    opacity:1;
}
.next>a{right:0px;}
.pre>a{left:0px;}

/*国漫版*/
body{
    min-width:100px;
}
.comic_wraCon #images img {
    margin: 10px 0 10px 0;
    padding: 0 !important;
    border: 0 !important;
    border-radius: 6px;
    box-shadow: 0 0 14px #f1f1f1;
    width: auto;
}
.comic_gd{
	width:auto;
}
.btmBtnBox+*+* {
    width: auto !important;
}
.side_public{
	z-index:21;
}
.ad.ad_tushang ,.ad_nith{
    width: auto;
}
div#bd-hl-content{
	opacity:0;
}
.ad{display:none;}
::-webkit-scrollbar {
    width: 5px;
    height: 0px;
}

::-webkit-scrollbar-thumb {
    background: linear-gradient(90deg, rgb(97, 97, 97) 0%, rgb(119, 119, 119) 50%, rgba(255, 255, 255, 0) 50%);
}


/*屏蔽点击就滚动到顶的问题*/
.independNav{
    position: fixed;
    top: 0px;
    background-color: #fff0;
    width: 100%;
    height: 100%;
    z-index: 2;
	backdrop-filter: unset;
}
#images{z-index: 1;}
.head_wz, .btmBtnBox{position: relative;z-index: 3;}
/*屏蔽点击就滚动到顶的问题*/
}

@-moz-document regexp("https://m.manhua.+.com/.+") {
/*手机版*/
.chapter-view #images img {
    width: auto;
	box-shadow: 0 0 14px #f1f1f1;
	border-radius: 6px;
}
.imgBox,.autoHeight,.mode-pagination #panel-title,.loading+div+script+script+script+div{
	display:none !important;
}
#images{
	margin-top:20px;
}

.action-list li:nth-of-type(2)>a,.action-list li:nth-of-type(3)>a{/*上一页,下一页*/
	position: fixed;
	top:0px;
    bottom: 0px;
    z-index: 20;
    box-shadow: 0px 0px 6px #adadad;
    background: rgba(255,255,255,.6);
    backdrop-filter: blur(7px);
    width: 200px;
	height:auto;
    color: #f5f5dc00 !important;
	text-shadow: 0 0 #ffffff00;
	opacity:0;
	display:block;
}
.mode-pagination #m_r_bottom,.action-list{height:0px;}
.action-list li:nth-of-type(2)>a:hover,.action-list li:nth-of-type(3)>a:hover{opacity:1;}

.action-list li:nth-of-type(2)>a{left: 0px;}
.action-list li:nth-of-type(3)>a{right: 0px;}
body{background:#fff !important;}
.action-list{border:unset;}
a.iconRet {
    position: fixed;
    left: 462px;
    right: 0px;
    top: 0px;
}
}
