css:
@-moz-document domain("www.nitrotype.com") {
body
  {
    background-color:  #2d2d2e;
    overflow-y: scroll;
    background:none;
    overflow-x: hidden;
    position: relative;
}
path:nth-child(7), path.logo-slash {
    fill:#77A5FF !important;}

.theme--pDefault.is-gold .profile-goldBorder {background:#0000;}
.profile-goldLogo {opacity:0}
  html
  {
    background: fixed url("https://wallpaperplay.com/walls/full/b/f/9/44412.jpg") no-repeat, #003466 !important;
    box-sizing: border-box;
    color: #ffffff;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
  }
.profile-bg {display:none;}
.profile-goldBorder {display:none;}
.nav {background: fixed url("https://wallpaperplay.com/walls/full/b/f/9/44412.jpg"); border:none;}
.profile-fg {background:url("https://i.ibb.co/s21MDt5/New-video.gif")no-repeat; border-radius:20px; position:relative; background-size: 1024px 552px } 
.profile-carContainer.card.card--a.card--shadow-l {background-color:#fff0;}

.card-cap.bg--gradient {background:url("https://lh3.googleusercontent.com/proxy/vs6rNM3tHLM7Hhpxsqtzn5Km9x8fcHDya0R7z5Dyv3I-joJNwzyx1qfjQ8DywRPz_uzLpWigtMmHxT_iKDLX_pYOz-IcX9dSQx0X-REF5g");}
.well--p.well--l_p {background:#2c2f33}
.card--e.card--sq.card--shadow-l.card--bc-brick.dhf.stat-box.stat-box--speed {background:none; border:none;}
.card.card--e.card--sq.card--bc-dodger.dhf.stat-box.stat-box--races {background:none;border:none;}
.card.card--e.card--sq.card--shadow-l.card--bc-emerald.dhf.stat-box.stat-box--money {background:none; border:none;}
.card.card--e.card--sq.card--f.card--shadow-l {border:none; box-shadow:none; background:none;}
.profile-goldBorder {opacity:0;}
.garage {background-color: #a4d9f2;}
.card.card--b.card--login.card--shadow.card--grit.card--f.card--o.cell.cell--s {background:#2c2f33}
#username.input-field {background:#77A5FF; border:none;}
#password.input-field {background:#77A5FF; border:none;}
.dais-car {background:#23272A; border:none;}
.footer {background:#23272A;}
path.rv-xy-plot__series.rv-xy-plot__series--line.chart-area
  {background: linear-gradient(to top, #fff,#000) !important;}
  .rv-xy-plot__grid-lines
  {
    color: #23272a;
    background: #000;
    background-color: #000;
    fill: #000;
  }

  #chart-gradient
  {
    color: red !important;
  }

  .chart--a .chart-gradient .chart-gradient1
  {
    stop-color: #77a5ff;
    stop-opacity: 0.6;
  }

  .chart--a .chart-gradient .chart-gradient2
  {
    stop-color: #23272a;
    stop-opacity: 0.3;
  }

  .chart--a .chart-gradient .chart-gradient3
  {
    stop-color: #23272a;
    stop-opacity: 0.5;
  }

  .chart--a .chart-line
  {
    stroke: rgb(0, 0, 0, 0) !important;
    stroke-width: 2 !important;
  }

  .chart--a .chart-line circle
  {
    fill: #000 !important;
    stroke-width: 0 !important;
    stroke: #fff !important;
    display: none;
  }
.dash-copyContainer {
    background:url("https://external-content.duckduckgo.com/iu/?u=https%3A%2F%2Fwallpapercave.com%2Fwp%2Fwp2454030.jpg&f=1&nofb=1");; 
    border-radius: 2px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
    border: none;    
   }

  .dash-copy
  {
    color: #006eff;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
	font-size: 25px;
    
  }
.h2.tbs {color:#2e3141; text-shadow:none;}
.tsl.tlh-1.dib {color:#000;}
  ::selection
  {
    background-color: #77a5ff;
    color: #fff;
  }
.tsxs.tc-ts.ttu.mrxs {color:#000;}
.tsxs.tc-ts.ttu {color:#fff;}
  .dash-bg
  {
      content: url("https://iksynod.org/wp-content/uploads/2016/01/3_Expand-and-Deepen-with-words1024-1024x220.jpg"); }

.logo-deco {fill:#77a5ff;}
}
