css:
@-moz-document domain(surviv.io)
{

  #background
  {
    background-image: url(https://vignette.wikia.nocookie.net/animal-jam-clans-1/images/7/7d/Blue_mountains_animation_by_grayamaroq-d9vbn8y.gif/revision/latest?cb=20160721075412) !important;
  }

  #start-row-header
  {
    background-image: url(https://upload.wikimedia.org/wikipedia/commons/7/7f/Surviv.io.png) !important;
  }

  #start-top-left
  {
    visibility: visible !important;
  }

  #featured-streamers
  {
    visibility: visible !important;
  }

  #start-bottom-left
  {
    visibility: visible !important;
  }

  #start-bottom-middle
  {
    visibility: visible !important;
  }

  .menu-option
  {
    border-radius: 0px !important;
    border-color: #40436d !important;
  }

  .btn-green
  {
    border-bottom: 0px !important;
    background: #6c70b7 !important;
    color: #40436d !important;
    box-shadow: inset 0 -2px #202020;
  }

  .btn-team-option
  {
    border-bottom: 0px !important;
    background: #6c70b7 !important;
    color: #40436d !important;
    box-shadow: inset 0 -2px #202020;
  }

  .account-details-button-wrapper
  {
    border-bottom: 0px !important;
    background: #6c70b7 !important;
    color: #40436d !important;
    box-shadow: inset 0 -2px #202020 !important;
  }

  #btn-pass-locked
  {
    border-bottom: 0px !important;
    background: #6c70b7 !important;
    color: #40436d !important;
    box-shadow: inset 0 -2px #202020;
  }

  #btn-customize
  {
    border-bottom: 0px !important;
    background: #6c70b7 !important;
    background-image: url(http://surviv.io/img/emotes/surviv.svg) !important;
    background-position: 8px 5px !important;
    background-repeat: no-repeat !important;
    background-size: 28px !important;
    color: #40436d!important;
    box-shadow: inset 0 -2px #202020;
  }

  #btn-help
  {
    border-bottom: 0px !important;
  }
}
