css:
@-moz-document domain("html.duckduckgo.com") {
.body--html,
body,
input,
select,
textarea {
    background: #222;
    color: #ccc;
}
.header__logo-wrap {
    opacity: .7;
}
.search:hover .search__button,
.search__input:focus ~ .search__button,
.search--header.has-text.search--hover .search__button,
.search--header.has-text.search--focus .search__button,
.search--home.has-text .search__button {
    background-color: #347552;
}
#search_form_input_homepage {
    color: #eee;
}
.search--header,
.search,
.search--adv,
.search_form_homepage {
    background: #204545;
}
.frm__select {
    background: #243440;
    border: 3px outset #244;
}
.frm__select:hover {
    background: #2e624e;
}
.header.header--html  {
    border-bottom: none;
}
.zci-wrapper {
    background: #241e2e;
    border-style: dashed;
    border-color: #636;
    border-width: 2px 0px;
}
.zci__heading {
    color: #eee;
}
.zci__result a,
a,
.result__a {
    color: #4ad;
}
.result__a:hover,
a:hover,
a:focus {
    color: #7bd;
}
.result__a:visited,
.result__a:visited:hover {
    color: #aa84b0;
}
.result:hover {
    background: #202b2b;
}
.result .result__snippet:hover,
.result .result__snippet:active,
.result__snippet b,
.result__snippet strong {
    color: #b3b3b3;
}
.result__snippet {
    color: #aaa;
}
.result__url,
.result__url:active,
.result__url:visited:focus,
.result__url:hover {
    color: #6c6;
}
}
