css:
@-moz-document domain("vider.info") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 0px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 0px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #728da8;
        }

body, .topnav_desktop, .outer_wrap, #content, .site_footer, .extra, .boxed, .footers_footer, html, .featherlight .featherlight-content {
	background: #0e0e0e;
	border-color: #111;
}
h1, h2, h3, h4, h5, h6 {
    color: #728da8;
}
.txt_fade {
    color: #63666a !important;
}
.pager_overflow span.txt_md {
    color: #3c75ae;
}
[class^=responsive_border_] {
	background-color: #111;
	border-color: #151515;
}
.pager_wrapper, .pager_overflow img, .browse_videos_details li, #info {
	border-color: #111;
}
[class^=responsive_border_].animated::after {
	border-color: #151515;
}
.badge_curation, .badge_support {
    color: #ff8a3c !important;
    background-color: #48372d;
    border: 1px solid #4d3b2d;
}
.badge, .badge_alum, .badge_black, .badge_favourites, .badge_beta, .badge_curation, .badge_featured, .badge_hd, .badge_info, .badge_mod, .badge_partner, .badge_plus, .badge_pro, .badge_rating, .badge_sponsor, .badge_staff, .badge_support, .badge_vod {
    text-shadow: 1px 1px rgba(0, 0, 0, .5);
}
.badge_rating.white, .browse_collections_details .duration, .browse_videos_details .duration {
	background: #111;
	border-color: #151515;
	color: #777;
}
input[type=email], input[type=number], input[type=password], input[type=text], textarea, .topnav_menu_search_submit {
	background: #111;
	border-color: #151515;
	color: #eee;
}
.topnav_menu_search_input {
	border-color: #151515 !important;
}
.topnav_menu_search_input:focus, .topnav_menu_search_input:hover {
    border-color: #4c5760 !important;
    color: #8aa9b7 !important;
}
.topnav_menu_search_input:focus ~ .topnav_menu_search_submit, .topnav_menu_search_input:hover ~ .topnav_menu_search_submit {
	border-color: #4c5760 !important;
    color: #8aa9b7 !important;
}
.faux_link, a, .link {
    color: #4483c1;
}
.browse_collections_details .duration, .browse_videos_details .duration {
    text-shadow: 1px 1px rgba(0, 0, 0, .5);
}
body {
	color: #aaa;
}
div#LoadGeneralContent {
	background-color: #0f0f0f !important;
}
.embed-header {
	background: #111;
	border-color: #111;
}
.boxed-hr, hr {
	border-color: #111;
}
.comment_wrapper {
	border-color: #121212 !important;
}
.pager_container_clip .pager_arrow[data-direction=left], .pager_container_clip .pager_arrow[data-direction=right] {
	background-color: #0e0e0e;
}
.pager_container_clip .pager_arrow span.replace_txt {
	border-color: #eee;
}
.badge_favourites {
	background: #151515;
	border-color: #222;
	color: #aaa !important;
}
input.w-100-p:hover, input.w-100-p:active, input.w-100-p:focus, textarea:active, textarea:hover, textarea:focus {
	border-color: #202020;
}
.topnav_desktop_logo, form > img {
	filter: invert(100);
}
.comment_add {
	background: #131313;
}
.text-class {
	background: #0e0e0e !important;
	color: #eee;
	position: relative;
	top: 10px;
}
.menu_small, .bubble_list {
	background: #0f0f0f;
	color: #ccc;
}
.bubble_list > .list_item > div > .inner, .bubble_list > .list_item > div > a, .bubble_list > li > div > .inner, .bubble_list > li > div > a, p.w-100-p, img.radius-5 {
	border-color: #111 !important;
}
.menu_small:hover {
	background: #151515;
}
.featherlight .featherlight-close-icon {
	background: #11111150;
	color: #ccc;
}
input[type=email], input[type=number], input[type=password], input[type=text], textarea, .topnav_menu_search_submit {
	border-color: #111 !important;
}
#legal_doc #page_header {
	border-color: #111;
}
.topnav_desktop_menu_items:hover > a {
    color: #3c6679;
}
}
