css:
@-moz-document domain("youtube.com") {
/* YouTube OldOnNew v1.0.0 Beta 3 */


[style] * {

    --ytd-rich-grid-items-per-row: 6;

    --ytd-rich-grid-posts-per-row: 4;

    --ytd-rich-grid-movies-per-row: 8;
}



@media (max-width: 1650px) {

    [style] * {

        --ytd-rich-grid-items-per-row: 5;

        --ytd-rich-grid-posts-per-row: 3;

        --ytd-rich-grid-movies-per-row: 7;
    }
}



@media (max-width: 1389px) {

    [style] * {

        --ytd-rich-grid-items-per-row: 4;

        --ytd-rich-grid-posts-per-row: 3;

        --ytd-rich-grid-movies-per-row: 6;
    }
}



@media (max-width: 1329px) {

    [style] * {

        --ytd-rich-grid-items-per-row: 5;

        --ytd-rich-grid-posts-per-row: 3;

        --ytd-rich-grid-movies-per-row: 6;
    }
}



@media (max-width: 1209px) {

    [style] * {

        --ytd-rich-grid-items-per-row: 4;

        --ytd-rich-grid-posts-per-row: 3;

        --ytd-rich-grid-movies-per-row: 5;
    }
}



@media (max-width: 960px) {

    [style] * {

        --ytd-rich-grid-items-per-row: 3;

        --ytd-rich-grid-posts-per-row: 2;

        --ytd-rich-grid-movies-per-row: 4;
    }
}



#rich-shelf-header.ytd-rich-shelf-renderer {

    margin: 10px 0;
}



yt-chip-cloud-chip-renderer {

    height: 24px;

    min-width: 12px;

    padding: 0 7px;

    border-radius: 16px;

    margin: 8px;

    margin-left: 0;

    font-size: 10pt;
}



#container.ytd-masthead {

    height: 47px;
}



html:not(.style-scope) {

    --ytd-toolbar-height: 47px;
}



#container.ytd-searchbox {

    padding: 0 4px;
}



paper-item.ytd-compact-link-renderer {

    height: 27px;
}



ytd-compact-link-renderer {

    --paper-item-min-height: 32px;
}



ytd-guide-section-renderer {

    --paper-item-min-height: 32px;
}



img#img {

    width: 100%;

    height: 100%;
}



yt-img-shadow.style-scope.ytd-channel-renderer.no-transition {

    height: 90px;

    width: 90px;
}



div#content-section {

    height: 90px;
}



#info.ytd-channel-renderer {

    padding: 0 16px;
}



paper-button.ytd-subscribe-button-renderer {

    padding: 6px 10px;

    text-transform: unset;
}



paper-button {

    min-width: 5px;

    text-transform: unset;
}



ytd-button-renderer.style-suggestive[is-paper-button] paper-button.ytd-button-renderer {

    padding: 4px 8px;

    text-transform: unset;
}



paper-button.ytd-button-renderer {

    width: auto;

    min-width: 5px;
}



#avatar.ytd-channel-renderer {

    width: 100px;
}



#avatar-link.ytd-rich-grid-video-renderer {

    display: none;
}



#contents.ytd-rich-grid-renderer,
#grid-title.ytd-rich-grid-renderer {

    width: 89%;
}



#chips-below.ytd-rich-grid-renderer {

    width: 88%;

    margin-top: -2px;
}



ytd-playlist-sidebar-renderer {

    width: 320px;
}



ytd-playlist-thumbnail.ytd-playlist-sidebar-primary-info-renderer {

    width: 250px;

    height: 140px;

    margin-bottom: 16px;
}



#avatar.ytd-video-owner-renderer {

    width: 30px;

    height: 30px;
}



ytd-inline-form-renderer[style=INLINE_FORM_STYLE_TITLE] #text-displayed.ytd-inline-form-renderer {

    overflow: hidden;

    font-size: 1.8rem;

    font-weight: 400;

    line-height: 2.4rem;
}



ytd-browse[page-subtype=playlist] ytd-two-column-browse-results-renderer.ytd-browse {

    padding-left: 320px;
}



#search-icon-legacy.ytd-searchbox {

    width: 70px;
}



#center.ytd-masthead {

    margin-right: auto;

    margin-left: -5px;
}



ytd-expander.ytd-video-secondary-info-renderer {

    margin-left: 47px;
}



#top-row.ytd-video-secondary-info-renderer {

    display: block;
}



paper-button.ytd-subscribe-button-renderer {

    margin: auto 0;
}



ytd-rich-grid-renderer:not([sticky-chips])[spacing-between-masthead-and-chips] #chips-below.ytd-rich-grid-renderer {

    margin-top: -2px;
}



ytd-rich-grid-renderer[fixed-header] ytd-feed-filter-chip-bar-renderer.ytd-rich-grid-renderer {

    top: 47px;

    border-top: none;
  }
}
