css:
@-moz-document domain("leakreality.com"), domain("gorefix.com") {
body {
	background-color: #232323;
	color: #232323;
}
.well-filters {
	-webkit-border-radius: 0;
	-moz-border-radius: 0;
	border-radius: 0;
	-moz-box-shadow: inset 0 -2px 0 #e5e5e5;
	-webkit-box-shadow: inset 0 -2px 0 #e5e5e5;
	box-shadow: inset 0 -2px 0 #232323;
	padding: 0 !important;
	border: none;
	background-color: transparent !important;
}
.comments-list .comment-item .comment-info .comment-text {
	display: block;
	margin: 5px 0;
	color: #fff;
	line-height: 20px;
	overflow-wrap: break-word;
	word-wrap: break-word;
	font-size: 14px;
}
.comments-list .comment-item .comment-info .comment-user-info .comment-username {
	font-weight: 500;
	line-height: 20px;
	color: #b1b1b1;
}
.content-rating {
	font-size: 14px;
	color: #fff;
}
h1 {
	font-size: 20px;
	color: #ddd;
	font-weight: 700;
	font-family: open sans,helvetica neue,Helvetica,Arial,sans-serif;
}
body {
	color: #fff;
}
a {
	color: #b1b1b1;
}
.content-row a, .content-row a:focus {
	color: #fff;
	font-weight: 500;
}
.text-highlighted {
	color: #fff !important;
	font-weight: 500;
}
.well-filters a {
	color: #b1b1b1 !important;
	cursor: pointer;
}
.content-views {
	color: #999;
	font-size: 14px;
}
.well-info {
	color: #b1b1b1;
	margin: 5px 0 10px;
}
a.list-group-item {
	color: #b1b1b1;
}
.page-link {
	font-weight: bold;
	margin: 0 3px;
	color: #c6c6c6;
	background-color: #000;
	border: 1px solid #000;
	padding: .5rem .8rem;
	-webkit-border-radius: 8px !important;
	-moz-border-radius: 8px !important;
	border-radius: 8px !important;
}
.form-horizontal .form-control {
	background-color: #000;
	border: 1px solid #070606;
	color: #b1b1b1;
}
.form-horizontal label {
	color: #fff;
}
fieldset {
	color: #fff;
}
h5 {
	color: #f00;
}
#comments_input {
	margin: 10px 0 10px;
	width: 80%;
	resize: none;
	background-color: #000;
	border: 1px solid #b1b1b1;
}
.user-info a {
	color: #f00;
}
a.show-hide {
	padding: .15rem 1.5rem;
	color: #b1b1b1;
	font-size: 18px;
}
.btn-secondary, .btn-secondary:active {
	background-color: #000;
	border-color: #e5e5e5;
	color: #b1b1b1 !important;
}
.comments-section .vote-up span, .comments-section .vote-down span {
	position: relative;
	top: 0;
	font-size: 12px;
	color: #f00;
	margin: 0 5px 10px;
}
.user-menu a {
	padding: 4px 10px 5px;
	float: left;
	background-color: #000;
	color: #f00;
}
.active .sw-left, .active .sw-right {
	border: 1px solid #fff;
}
.sw-right {
	border: 1px solid #232323;
	-webkit-border-top-right-radius: 3px;
	-webkit-border-bottom-right-radius: 3px;
	-moz-border-radius-topright: 3px;
	-moz-border-radius-bottomright: 3px;
	border-top-right-radius: 3px;
	border-bottom-right-radius: 3px;
	padding: 0 5px !important;
	-moz-box-shadow: none !important;
	-webkit-box-shadow: none !important;
	box-shadow: none !important;
}
.sw-left {
	border: 1px solid #232323;
	-webkit-border-top-left-radius: 3px;
	-webkit-border-bottom-left-radius: 3px;
	-moz-border-radius-topleft: 3px;
	-moz-border-radius-bottomleft: 3px;
	border-top-left-radius: 3px;
	border-bottom-left-radius: 3px;
	padding: 0 5px !important;
	-moz-box-shadow: none !important;
	-webkit-box-shadow: none !important;
	box-shadow: none !important;
}
h2 {
	font-size: 24px;
	color: #b1b1b1;
	font-weight: 500;
	font-family: open sans,helvetica neue,Helvetica,Arial,sans-serif;
	padding: 15px 0;
}
.list-group-item {
	background-color: #000;
	padding: 8px 15px;
	border: 1px solid #333;
}
.content-row a:hover {
	color: #b1b1b1;	
	text-decoration: none;
}
.well-filters a:hover, .well-filters>a:focus {
	color: #666!important;
    text-decoration: none!important;
}
.well-filters .dropdown-menu {
	background-color: #000;
	margin: 0;
	border: 0;
	padding: .3rem 0;
	-webkit-border-radius: .25rem;
	-webkit-border-top-right-radius: 0;
	-webkit-border-top-left-radius: 0;
	-moz-border-radius: .25rem;
	-moz-border-radius-topright: 0;
	-moz-border-radius-topleft: 0;
	border-radius: .25rem;
	border-top-right-radius: 0;
	border-top-left-radius: 0;
	z-index: 999;
	padding: 5px 0 8px;
	width: 180px;
	left: -14px !important;
	-webkit-box-shadow: 0px 2px 5px 1px rgba(0,0,0,0.12);
	-moz-box-shadow: 0px 2px 5px 1px rgba(0,0,0,0.12);
	box-shadow: 0px 2px 5px 1px rgba(0,0,0,0.12);
}
.well-filters .dropdown-menu .active a {
	color: #fff !important;
	font-weight: 500;
}

.modal-content {
	background-color: #020202;
}
.modal-title {
	color: #fff;
}
.modal-content a {
	color: #b3b3b3;
}
.what-is h5 {
	color: #fff;
}
.user-actions .dropdown-menu {
	top: 3px !important;
	background-color: #000;
	margin: 0;
	border: 0;
	padding: .3rem 0;
	-webkit-border-radius: .25rem;
	-moz-border-radius: .25rem;
	border-radius: .25rem;
	z-index: 999;
	font-size: 14px;
	-webkit-box-shadow: 0px 2px 5px 1px rgba(0,0,0,0.12);
	-moz-box-shadow: 0px 2px 5px 1px rgba(0,0,0,0.12);
	box-shadow: 0px 2px 5px 1px rgba(0,0,0,0.12);
}
.user-actions .dropdown-menu a {
	color: #fff;
}
.language-active {
	color: #f00;
	font-weight: 500;
}
.modal-content a:hover {
	color: #fff;
}
a:hover, a:focus, a:active {
	color: #fff;
	text-decoration: none;
}
a.replies-show-more:hover, a.replies-hide:hover {
	color: #fff;
}
a:hover.comments-show-more, a:hover.comments-hide {
	color: #fff;
}
.comments-section .comment-reply a:hover {
	color: #fff;
}
.comments-section .comment-reply a:hover i {
	color: #fff;
}
.vote-up .fa-thumbs-up:hover, .vote-down .fa-thumbs-down:hover {
	color: #fff;
	cursor: pointer;
}
.navbar-dark .navbar-nav .nav-link:focus, .navbar-dark .navbar-nav .nav-link:hover {
	color: rgb(255, 255, 255);
}
.well-filters a:hover, .well-filters > a:focus {
	color: #fff !important;
}
.comments-section .dropdown-menu a:hover {
	background-color: #888;
	color: #0d0d0d;
}
.comments-section .dropdown-item.active {
	color: #fff !important;
	font-weight: 900;
	text-decoration: none;
	background-color: transparent;
}
.modal-body label {
	color: #b3b3b3;
}
.form-control:disabled, .form-control[readonly] {
	color: #fff;
	background-color: #000;
}
.video-actions .dropdown-menu {
	top: 3px !important;
	background-color: #000;
	margin: 0;
	border: 0;
	padding: .3rem 0;
	-webkit-border-radius: .25rem;
	-moz-border-radius: .25rem;
	border-radius: .25rem;
	z-index: 999;
	font-size: 14px;
	-webkit-box-shadow: 0px 2px 5px 1px rgba(0,0,0,0.12);
	-moz-box-shadow: 0px 2px 5px 1px rgba(0,0,0,0.12);
	box-shadow: 0px 2px 5px 1px rgb(0, 0, 0);
}
.video-actions .dropdown-menu a {
	color: #fff;
}
.comments-list .comment-item .comment-actions a {
	color: #fff;
}
a.replies-show-more, a.replies-hide {
	margin: 0 15px 0 0;
	color: #b1b1b1;
	font-size: 16px;
	margin-top: 5px;
}
.comments-list .comment-item .comment-info .comment-user-info .comment-username {
	font-weight: 500;
	line-height: 20px;
	color: #b3b3b3;
}
input.form-control, textarea.form-control {
	color: #fff;
	background-color: #000;
	border: 1px solid #ccc;
}
.comments-section .dropdown-menu {
	background-color: #000;
	margin: 0;
	border: 0;
	padding: .3rem 0;
	-webkit-border-radius: .25rem;
	-moz-border-radius: .25rem;
	border-radius: .25rem;
	z-index: 999;
	font-size: 14px;
	-webkit-box-shadow: 0px 2px 5px 1px rgba(0,0,0,0.12);
	-moz-box-shadow: 0px 2px 5px 1px rgba(0,0,0,0.12);
	box-shadow: 0px 2px 5px 1px rgba(0,0,0,0.12);
}
.comments-list .comment-item .comment-actions a:hover {
	color: #fff;
}
.comments-section .dropdown-menu a:hover {
	background-color: #555;
	color: #0d0d0d;
}
.modal-body {
	color: #b1b1b1;
}
a.comments-show-more, a.comments-hide {
	margin: 0 15px 0 0;
	color: #b3b3b3;
	font-size: 18px;
}
.form-horizontal .custom-file-label {
	color: #fff;
	background-color: #000;
	border: 1px solid #fff;
}
.user-info-box {
	color: #cecece;
}
.table-dark {
	color: #fff;
	background-color: #0f0f0f !important;
}
.table-dark.table-striped tbody tr:nth-of-type(2n+1) {
	background-color: rgba(100, 100, 100, 0.5) !important;
}
}
