css:
@-moz-document domain(www.gamefaqs.com) {

/*Text Colors*/

a{
     color:#FF3433 !important;
}

body {
     color:#CCC !important;
     background:#232323 none !important;
}
a:visited {
     color: #B10F13 !important;
}





/*Fix Textboxes*/

select, textarea, input[type=text], input[type=password] {
	-moz-appearance: none !important;
}
textarea {
     color:#DF8B8B !important;
     background-color: #1F0C0C !important;
     border: 1px inset #909090 !important;
     padding: 2px 2px 2px 2px !important;
     -moz-border-radius: 1em 4em !important;
     padding: 13px !important;
     width: 93% !important;
}

select, input[type=text], input[type=password] {
	color:#FFFFFF !important;
	background-color: #000000 !important;
}





/*shrinks the header*/

#masthead {
     width: 932px !important;
     background: url(data:image/gif;base64,) repeat-y -4px 0px !important;
}
#header, #searchbox, #quicknav, #platformlist {
     width: 900px !important;
     margin-left: 16px !important;
}
.mh_wrap {
     padding-left: 10px !important;
     padding-right: 10px !important;
     width: 880px !important;
}
#content {
     border-left: none !important;
     border-right: none !important;
     border-top: 1px solid #999999 !important;
}



/*Header*/

#header {
     background: #A10B0B !important;
     border-bottom: 1px solid #650D0D !important
}
#header .mh_wrap {
     background-image: none !important;
     background-color: #A10B0B !important;
}
#logo {
     background: url(data:image/gif;base64,) no-repeat 0 0 !important;

     margin: 1px 0 0 0 !important;
}
#loginbox {
     background-image: none !important;
     background-color: #601414 !important;
     padding-top: 0 !important;
     margin-top: 8px !important;
     -moz-border-radius: 8px !important;
}
#loginbox .nav a {
     border-right:1px solid #9C3C3B !important;
     color:#FFB01F !important;
}
#loginbox span.submit {
     border:1px solid #65C426 !important;
}
#loginbox span.submit span {
     background:#E5FFCF none repeat scroll 0 0 !important;
     border-color:#F5FFEF #E6FFD5 #E6FFD5 #F5FFEF !important;
}

#loginbox .nav a.last {
     border:none !important;
}

#loglink a {
     font-weight:normal !important;
}





/*Searchbox*/

#searchbox, #searchbox .mh_wrap {
     background-image: none !important;
     background-color: #666666 !important;
     border-bottom: 1px solid #888888 !important;
     height: 31px !important;
}
#searchbox .mh_wrap {
     height: 27px !important;
}
#searchbox .data {
     background-color: #000000 !important;
}

/*Quicknav*/

#quicknav, #quicknav .mh_wrap {
     background-image: none !important;
     background-color: #1D1D1D !important; 
     border-top: 1px solid #000000 !important;
     border-bottom: 1px solid #888888 !important;
     height: 32px !important;
}
#quicknav .mh_wrap {
     margin-top: -1px !important;
}
#quicknav a {
background:url(data:image/gif;base64,) no-repeat center right !important;
	color:#FFCC99 !important;
}
#quicknav .mh_wrap p{
background:url(data:image/gif;base64,) no-repeat center left !important;
	color:#FFCC99 !important;
}





/*Platform List */

#platformlist, #platformlist .mh_wrap {
 background: url(data:image/gif;base64,) repeat-y top center !important;
     color:#FFA699 !important;
     overflow: visible !important;
}
#platformlist .nav b {
     color:#999 !important;
}





/*Page BG Color & Gutters*/

#container{
     background-image: none !important;
}

#gutters {
     background: url(data:image/gif;base64,) repeat-y top center !important;
}

#gutter_top {
     background: url(data:image/gif;base64,) repeat-y top center !important;
     border-left: 1px solid #999999 !important;
     border-right: 1px solid #999999 !important;
}

#content {
     margin-bottom: 20px !important;
     margin-right: 5px !important;
}

#gutter_btm {
     background:url(data:image/gif;base64,) no-repeat bottom center !important;
}





/*This is a CSS that can be found in GameFOX, but you may not have, it's necessary to display the bottom gutter*/

    .ad, .adbox, .rubics_netxp1_main, .prWrap, #gne_nav, #sponsored_links, #mpu_wrap, #footer,
    iframe, img[alt="madison"], #content > div[style="text-align: center;"]
    {
        display: none !important;
    }





/*Main Page*/

#content {
     background: #000000 !important;
}
#main_col .today .head {
     border-bottom: 1px solid #434343 !important;
}

#main_col .today .head h1 {
     color: #CCCCCC !important;
}
#main_col .today .head .date {
     color: #FF5A00 !important;
}
#main_col .topten .pod .head {
     background-color: #A10B0B !important;
     border: 1px solid #650D0D !important;
}
#main_col .topten .pod .head h2 {
     color: #000000 !important;
}
#main_col .topten .pod ol li.alt {
     background-color:#222 !important;
}
#main_col .gs .pod .head {
     background-color: #333333 !important;
     border: none !important;
}
#main_col .gs .pod .head h2 {
     color: #CCCCCC !important;
}
#main_col .gs .pod .body {
     background-color: #1F1F1F !important;
}
#main_col .gs .pod .foot {
     background-color: #1F1F1F !important;
}





/*side column and pods within*/

#content #side_col {
     background-image: none !important;
}
#mpu_col_wrap, #sky_col_wrap {
background-image: none !important;
}
#sky_col_wrap .col_layout {
     padding: 0 10px !important;
}
#side_col .pod:not(.gs) .head {
     background-color: #A10B0B !important;
     border: 1px solid #650D0D !important;
}
#side_col .pod .head h2 {
     color: #000000 !important;
}
#side_col .pod .body {
     margin-top: 3px !important;
     padding-top: 3px !important;
     border: solid 1px #666666 !important;
     -moz-border-radius: 6px !important;
     background-color: #0F0F0F !important;
}
#side_col .pod {
     background-image: none !important;
}
#side_col .pod .foot {
     background-color: #000000 !important;
     border-left: 1px solid #666666 !important;
     border-right: 1px solid #666666 !important;
}
#side_col .pod .foot {
     border: none !important;
}
#side_col .pod ul.items li {
     background-image: none !important;
     border-bottom: 1px dotted #AFAFAF
}
#side_col .pod ul.items li + li + li + li + li {
     border: none !important;
}
#side_col .gs div.head {
     background-color: #333333 !important;
     border: 1px solid #0F0F0F !important
}
#side_col .gs div.head h2{
     color: #CCCCCC !important;
}





/*Table Changes*/

#content .head {
     border-bottom: 1px solid #434343 !important;
}
#content .head h1 {
     color: #CCCCCC !important;
}
#content #sky_col_wrap .head h1 {
     color: #000000 !important;
}
#content thead th, #content tr.head th {
     background:#C33737 !important;
     color:#000 !important;
}
#board_wrap .head {
     background-color: #A10B0B !important;
     border: 1px solid #650D0D !important;
}
#board_wrap .head  h2 {
     color: #000000 !important;
}
table:not(.poll) tr:not(.gamefox-highlight) td:not(.gamefox-highlight), table:not(.poll) tr:not(.gamefox-highlight) th:not(.gamefox-highlight) {
     background-color: #222 !important;
}
#content tr:not(.gamefox-highlight).even td:not(.gamefox-highlight),#content tr:not(.gamefox-highlight).even th:not(.gamefox-highlight) {
     background-color: #000000 !important;
}
#content #board_wrap tbody .head th, #content tbody .head td {
     background: #C33737 !important;
}
#content .board_nav .body {
     background-color: #222222 !important;
}
#content #board_wrap table.topics {
     color:#E0C166 !important;
}
.topics tbody td {
     color:#E0C166 !important;
}
#main_col .body .systems li.even {
     background-color: #222222 !important;
}
#main_col .contrib .body {
     background-color: #222222 !important;     
}
#full_col_wrap .pod .head {
     background-color: #A10B0B !important;
     border: 1px solid #650D0D !important;
}
#full_col_wrap .pod .head h2 {
     color: #000000 !important;
}
span[style="color: blue;"] {
     color: #FF0000 !important;
}





/*A betting looking topic search*/

.searchtopics {
     border: 1px solid #404040 !important;
     background-color: #111111 !important;
}

.searchtopics form {
     height: 34px !important;
     width: 465px !important;
     padding:0 1px 0 65px !important;
     border: 1px solid #787878 !important;
     background-color: #5D5D5D !important;
}
.searchtopics label {
     color: #000000 !important;
     font-weight: bold !important;
     margin: 10px 0 0 -65px !important;
     width: 95px !important;
}
.searchtopics .data {
     margin:5px 0 5px 2px !important;
     width: 330px !important;
}
.searchtopics .data span {
     border: none !important;
     height: 20px !important;
     background-color: #000000 !important;
}
.searchtopics .data input {
     font-size: 12px !important;
     padding: 2px 0 0 0 !important;
}
.searchtopics .btn {
     background-color: #212641 !important;
     background-image: none !important;
     background-position: left center !important;
     margin: 5px 0 0 0 !important;
     width: 98px !important;
     height: 24px !important;
     float: left !important;
}
.searchtopics .btn span{
     background-color: #212641 !important;
     background-image: none !important;
     padding: 0 0 0 15px !important;
     color: #FF3433 !important;
}
.searchtopics .btn input {
     color: #FF3433 !important;
     cursor: pointer !important;
}





/* content_nav tabs */

.content_nav li{
     background: url(data:image/gif;base64,) no-repeat top left !important;
}

ul.content_nav li a{
     background:url(data:image/gif;base64,) no-repeat top right !important;
     color: #FFA686 !important;
}

ul.content_nav a b.first{
     background:url(data:image/gif;base64,) no-repeat top left !important;
}

ul.content_nav a b.last{
     background: url(data:image/gif;base64,) no-repeat top right !important;
}

ul.content_nav li.hi a.last{
     background-image:url(data:image/gif;base64,) !important;
}

ul.content_nav li.hi a b.first{
     background-image:url(data:image/gif;base64,) !important;
}

ul.content_nav li.hi a b.last{
     background-image:url(data:image/gif;base64,) !important;
}

ul.content_nav li.hi, ul.content_nav li.hi a{
     background-image:url(data:image/gif;base64,) !important;
}

ul.content_nav li.hi a{
     color:#F7ECD7 !important;
}

ul.content_nav li a:hover, ul.content_nav li.hi a:hover{
     color:#FFD700 !important;
}

#main_col .pod .head {
     background-color: #A10B0B !important;
     border: 1px solid #650D0D !important;
}
#main_col .pod .head h2 {
     color: #000000 !important;
}


/*content_filter box*/

.content_filter .genre {
     border-top:1px solid #333333 !important;
}
.content_filter .wrap {
     background-image: none !important;
}
.content_filter .wrap .w1, .content_filter .wrap .w2, .content_filter .wrap .w3{
     background-image: none !important;
     
}
.content_filter .wrap .w1{
     background-color: #141414 !important;
     border: 1px solid #333333 !important;
     -moz-border-radius: 6px !important;
}





/*PotD*/
@-moz-document url-prefix("http://www.gamefaqs.com/poll/") {

.body img{
     margin-left: 17.5% !important;
}
}





/* Answers Pages */

#content .content_qna .header {
background: #000000 url(data:image/gif;base64,) repeat-x scroll left top !important;
     border:1px solid #2A2A2A !important;
}

#content .qna_hub #side_col .header {
background: #000000 url(data:image/gif;base64,) repeat-x scroll left top !important;
     border:1px solid #2A2A2A !important;
}

#content .content_qna.hub .header h2 {
     color:#FFFFFF !important;
}
#content .content_qna .module {
     background: #0F0F0F none repeat scroll 0 0 !important;
     border: 1px solid #404040 !important;
}

.content_qna .module .header h2 {
     color: #FFFFFF !important;
}
#content .content_qna fieldset.searchbox {
     background:#5D5D5D none repeat scroll 0 0 !important;
     border:1px solid #787878 !important;
}
#content .content_qna fieldset.searchbox label {
     color: #000000 !important;
}
#content .content_qna fieldset.searchbox span.data span {
background: #000000 none repeat scroll 0 0 !important;
}
#content .content_qna fieldset.searchbox input.submit_search {
     color: #FF3433 !important;
     background-color: #212641 !important;
}
#content .content_qna div.qna_table {
     background: #000000 none repeat scroll 0 0 !important;
}
#content .content_qna .qna_table th {
     background: #C33737 none repeat scroll 0 0 !important;
     color: #000000 !important;
}
#content .content_qna .qna_table td {
     border-top: 1px solid #292929 !important;
}
#content .content_qna .qna_table .grey {
     background:#1E1E1E none repeat scroll 0 0 !important;
     color:#CCCCCC !important;
}
#content .content_qna .view_block {
     background: #C33737 none repeat scroll 0 0 !important;
     border: none !important;
     color: #000000 !important;
}
#content .content_qna .view_block a {
     color:#000000 !important;
}

#content .content_qna .divider {
border-top:1px solid #292929 !important;
}

#content .qna_hub #side_col li.alt {
background:#222222 none repeat scroll 0 0 !important;
}

#content .content_qna form input.submit {
background:transparent url(data:image/gif;base64,) no-repeat scroll center top !important;
     color: #FFFFFF !important;
     padding: 0 0 2px 0 !important;
}





/*My Games*/
h1 a[href="/features/mygames/index.html"] {
     color: #FFFFFF !important;
}

/*GameFOX*/

#gamefox-quickpost-normal {
     text-align: center !important;
}
}
