css:
@-moz-document domain("subscribestar.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 2px rgba(0, 0, 0, .6);
             background: linear-gradient(280deg, #009f7c, #b00052)
        }

body, .top_bar, .top_bar:after, .top_bar:before, .top_bar-shadow, .goals-goal_inner, .goals-goal_meter, .is-locked .flat_button.for-subscribe-2c-more, .is-locked .flat_button.for-tier-unlock, .flat_button.for-subscribe-2c-more, .search_results, .home-section.is-features, .home-section.is-how, .home-section.is-why, .home-section.is-features:after, .home-section.is-features:before, .home-section.is-how:after, .home-section.is-how:before, .home-section.is-why:after, .home-section.is-why:before, .pricing-calculator_result-amount, .home-meet_the_stars, .home-section_sign, .home-samples_item-img, .overlay-bg, .signup-social_button.is-facebook:hover:not(.is-disabled), .textarea-input, .settings_timezone-select, .star-items_wrapper .star_item.is-featured .flipper-back, .star-items_wrapper .star_item.is-featured .flipper-front, .star_item .star_item-avatar img, .for-profile_settings .warning.is-social, .warning_modal-wrapper, .home-meet_the_stars-avatar img, .star_item .star_item-avatar img, .subscriber-avatar img {
	background: #0e0e0e;
}
.search_results .search_entry:hover {
	background: #111;
}
.textbox-field, .tier, .signup-role_star, .signup-role_subscriber, .textarea.for-profile_settings .textarea-field, .settings_login, .for-profile_welcome_sidebar .profile_features-item:not(:first-child), .star-items_wrapper .star_item.is-featured .flipper-back, .star_item {
	border-color: #151515;
}
.section-title, .tier-avatar_image, .search_results .search_entry, .home-meet_the_stars-avatar img, .card:not(:last-child), .card-notice.with-border, .star_item .star_item-avatar img, .for-tier_modal .tier-body {
	border-color: #222;
}
.flat_button.is-grey:focus, .flat_button.is-grey:hover {
	background: #222;
	border-color: #0e0e0e;
}
.flat_button.is-grey {
	border-color: #222;
	color: #666;
}
body, .section-title_text, .goals-goal_description, .subscribe-2c-more_title, .link-accented.for-all_features, .link-accented.for-all_stars, .settings_shipping, .star_item .star_item-info, .star_item-actions, .post-content h1, .post-content h2, .post-content h3, .post-content h4, .post-content h5, .post-content h6, .post-title h2 {
	color: #ccc;
}
.top_bar, .home-section_title, h2.home-section_subtitle {
	color: #c6c6c6;
}
.for-profile_welcome_sidebar .profile_features-item {
	color: #bbb;
}
.popup_cancel-link {
	color: #8e8e8e;
}
.star_link-types, .section-body.is-hint, .post-date, .search_results .search_results-item .star-types, .donate-description, .signup-description, .signup-role_role, .signup-role_description, .card-notice, .settings_media-formats, .tiers-settings_item-cost-period {
	color: #999;
