}
.post-body.is-locked {
    background-color: #0e0f0e;
}
.profile_main-warning {
	border-color: #0e0e0e;
}
.post-actions {
	border-top-color: #131313 !important;
	border-bottom-color: #222 !important;
}
.subscribe-2c-more {
    background-color: #0e0f0f;
}
.profile_features-introduction > .is-stars.md_icon > svg > circle, .top_bar-icon.is-stars.md_icon > svg > circle, .signup-step.is-finished path, .is-fa_envelope.md_icon > svg > path {
