}
.layout-footer {
	background: #333;
}
input, .textarea-input, .settings_timezone-select {
	color: #eee;
}
.profile_main-warning, .search_results-warning, .star_item-warning {
    color: #eee;
    background-color: #ff4700;
}
.home-section.is-money, .home-section.is-money:after, .home-section.is-money:before {
    background-color: #0e0e0e;
	background-image: url('https://ss-staging-assets.s3-us-west-1.amazonaws.com/watermarks_green.png')
}
.is-down.home-samples_item-img.with-tail:after, .is-down.home-success_item-quote.with-tail:after, .is-down.pricing-calculator_result-amount.with-tail:after {
    border-top: 1.4rem solid #0e0e0e;
}
.home-section.is-support, .home-section.is-support:after, .home-section.is-support:before {
    background-color: #0e0f0f;
}
.checkbox.for-signup {
	background: #0000 !important;
	color: #ccc;
}
.arrow_button {
	border-color: #aaa;
}
.arrow_button path {
	fill: #aaa;
}
.overlay-close {
	background: #0e0e0e;
}
.signup-step {
	border-color: #222;
}
.signup-step path {
	fill: #222;
}
.signup-step_arrow {
	background: #222;
}
.signup-step_arrow:after {
    border-left-color: #222;
}
.textbox .textbox-icon_wrap, [src="https://assets.subscribestar.com/assets/public/images/sample_userpic-729d57082377951bfb1bbac409c75804.png"] {
	filter: invert(100) !important;
}
.textarea.for-profile_settings:last-child, .checkbox.for-communication_settings:last-child, .post-actions {
	background: #0000 !important;
}
.warning-message, .notice-content {
	color: #000;
}
.settings_timezone-select:hover {
    background-color: #0f1111;
}
.croppie-container .cr-viewport {
    box-shadow: 0 0 2000px 2000px hsla(0, 0%, 0%, 0.7);
    border: 1px dotted #ff4081;
}
.edit_avatar-crop_area_inner {
    background: hsla(0, 0%, 0%, 0.3);
    border: 1px dashed #151515;
}
.star-items_wrapper .star_item.is-featured:after {
    border-color: transparent #0e0e0e #0e0e0e transparent;
}
.profile_main_info-userpic img {
	background-color: #222;
	border-color: #0e0e0e;
}
.top_bar-avatar img {
	background: #222;
}
.home-features_item {
	filter: invert(100);
	color: #000;
}
.login-link, .login p, .subscribe-categories {
	color: #8e8e8e;
}
.overlay-close {
    background: hsla(0, 0%, 0%, .3);
}
.overlay-close_inner:hover .overlay-close_line {
	background: #5f5f5f;
}
.overlay-close_line {
	background: #8c8c8c;
	box-shadow: 0 0 2px rgba(255, 255, 255, .1);
}
.signup-role_checkbox > i > svg > path {
	fill: #0f0f0f;
}
[src='https://assets.subscribestar.com/assets/public/images/icons/find-c33fb9b51ad0d8ec7c9db64cbdb39db3.png'] {
	filter: inherit;
}
.subscribe-user_info {
	background: #101213;
}
.post_form-select_tier_title, .subscription_form-select_tier_title {
	color: #363636;
}
.post_form-select_tier_title:after, .post_form-select_tier_title:before, .subscription_form-select_tier_title:after, .subscription_form-select_tier_title:before, .tier-select_item:not(:last-child), .tiers-settings_item:not(:last-child) {
	border-color: #363636;
}
.form_modal-yota_input {
	border-bottom-color: #363636;
}
.for-post_form .tier-select_item:hover, .for-post_form .tiers-settings_item:hover, .for-subscription_form .tier-select_item:hover, .for-subscription_form .tiers-settings_item:hover, .for-post_form .tier-select_item:hover, .for-subscription_form .tier-select_item:hover, .tiers-settings_item.is-selected {
	background: #151515;
}
.form_modal-row_title, .form_modal-column_title, .messages-title, .message-destroy_description, .messages-description, .message.for-donations span, .message.for-messages span, .message.for-donations .unsubscribe, .message.for-messages .unsubscribe {
	color: #ccc;
}
.checkbox {
	background: #0000;
}
.select select {
	border-color: #2d2d2d;
}
select, option {
	background: #0e0e0e;
	color: #fff;
	border-color: #2d2d2d;
}
.preview.is-video .preview-video_play {
	background: #0e0e0e;
}
.tooltip_question path {
	fill: #363636;
}
li.settings_nav_menu-item a:hover {
	transition: color .2s ease-out;
	color: #ff4081;
}
.home-success_item-quote blockquote:after, .home-success_item-quote blockquote:before {
	color: #8e8e8e;
}

div.home-success_item:nth-of-type(1) > .is-green.is-down.with-tail.home-success_item-quote > blockquote {
	visibility: hidden;
}
div.home-success_item:nth-of-type(1) > .is-green.is-down.with-tail.home-success_item-quote > blockquote:before {
	visibility: visible;
	quotes: "\201C" "\201D";
	content: '"W dupe w jape, w dupe w jape, w dupe w jape. Sperma jak manna z nieba."';
	position: relative;
	color: #ccc;
}
div.home-success_item:nth-of-type(1) > .home-success_item-star > .for-success.star_link-avatar {
	content: url('https://i.imgur.com/k9sVgkj.png');
	object-fit: cover;
}
div.home-success_item:nth-of-type(1) > .home-success_item-star > .for-success.star_link-info > .for-success.star_link-name {
	visibility: hidden;
}
div.home-success_item:nth-of-type(1) > .home-success_item-star > .for-success.star_link-info > .for-success.star_link-name:before {
	content: 'blyad';
	visibility: visible;
}
}

@-moz-document domain("js.checkout.com") {
.card-number, .expiry-date, .cvv {
	color: #ccc;
	-webkit-text-fill-color: #ccc;
}
.expiry-date--valid, .card-number--valid, .cvv--valid {
    color: green;
    -webkit-text-fill-color: green;
}
.card-number--invalid, .cvv--invalid, .expiry-date--invalid {
    color: red;
    -webkit-text-fill-color: red;
}
input {
	background: #0e0e0e;
}
.frames-element--autofilled {
	background: #181a1c;
}
}
