css:
@-moz-document domain("4chan.org") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 0px;
             background-color: #111;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #111;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 0px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #800;
        }

body {
	background: linear-gradient(180deg, #1c1a14, #000, #000, #000, #000, #000);
	color: #800;

}
html, .top-box {
	background: #000;
}
#announce {
	background: #000;
	color: #eee;
	border-color: #111;
}
#ft li.current, #ft li.fill {
	background: #040403;
}
#ft li {
    background: #171513;
}
#fp-menu {
	background: #000;
	border-color: #333;
}
.boxbar {
    background: #463327;
    color: #800;
}
a:visited, a, .boxcontent a, .yotsuba_new .postMenuBtn, .futaba_new .postMenuBtn {
	color: #008cff;
}
.boxcontent {
	color: #eee;
}
.left-box {
    background: #0c0f0c;
    color: #060;
}
.right-box.box-outer > .box-inner > .boxbar > h2 > a {
	color: #eee !important;
}
.right-box {
    background: #0f1313;
    color: #006;
}
dt.first, dt, .boxcontent h3 a {
    color: #008cff;
}
.stripe-button-el:disabled span, .stripe-button-el.disabled span {
	background: #111;
	border-color: #151515;
	color: #555;
	text-shadow: 1px 1px 1px #111;
}
input[type=text] {
	background: #111;
	border-color: #151515;
	color: #eee;
}
#disclaimer-dialog {
	background: #000;
}
button, input[type="submit"] {
	background: linear-gradient(180deg, #222, #111);
	color: #eee;
	border-color: #151515;
}
#backdrop {
    background-color: rgba(0, 0, 0, 0.5);
	backdrop-filter: blur(2px);
}
hr {
	border-color: #111;
}
div.reply {
    background-color: #131210;
    border: 1px solid #171413;
}
.quoteLink, .quotelink, .deadlink, .pageJump a, dd:nth-of-type(11) > p > span, dd:nth-of-type(10) > p:nth-of-type(1) > span, dd:nth-of-type(8) > p:nth-of-type(1) > span:nth-of-type(3), dd:nth-of-type(8) > p:nth-of-type(1) > span:nth-of-type(2) {
	color: #008cff !important;
}
.preview {
	border-color: #201a17 !important;
}
select, option {
	background: #050505;
	border-color: #111;
	color: #eee;
}
#fp-menu li:hover {
    background: #332922;
}
#fp-menu .fp-menu-sep {
	border-color: #222;
}
div.pagelist {
    background: #110f0e;
    border-right: 1px solid #221c1a;
    border-bottom: 1px solid #221c1a;
    color: #624837;
}
div.pagelist div.cataloglink {
	border-color: #221c1a;
}
}
