css:
@-moz-document domain("green-news.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #a6d154 #1c1c1c;
}
a,h1,h2,h3,h4{
    color: #a6d154 !important;
}
a:hover,p,span{
    color: #fff !important;
}
*{
    box-shadow: none !important;
	text-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],input[type="submit"],input[type="tel"],input[type="url"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #a6d154 !important;
}
input[type="submit"]:hover{
    background: #a6d154 !important;
    color: #fff !important;
}
/*HEADER*/
.green-news-logo-top,.scrolled-desktop .logo-container img[alt="Green News"]{
    background: #fff;
    border: 3px solid;
}
.carousel-inner{
    background: #a6d154;
    border-radius: 3px;
}
.menu-list.main-menu .dropdown-menu{
    background: #1c1c1c;
    border: 3px solid #a6d154;
}
.alert{
    color: red;
}
.scrolled-desktop .logo-container{
    background: #1c1c1c;
    border-bottom: 1px solid #a6d154;
}
#alertModal,#searchModal{
    background: #1c1c1c;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.category-tag a{
    color: #1c1c1c !important;
}
.sidebar .col-sm-12,.pagination > .disabled > a, .pagination > .disabled > a{
    background: #1c1c1c !important;
}
.greenmarket a{
    color: #a6d154 !important;
}
.pagination > li.active a{
    background: #a6d154;
}
.pagination > li.next-page::after{
    top: 30px;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.category-tag .twitter{
    border-radius: 3px;
}
.post-page-content .single-post-category .category-list{
    color: #1c1c1c !important;
}
