css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
    20091016:
        update for update
        add forum8
    20090715:
        @-moz-document for all forum
    20090625:
        first update
        update for admin change
        rewrite most of css
    20090625:
        add search page
*/

@-moz-document domain("hkgolden.com") {

    /* AD */
    #MsgInLineAd0, #MsgInLineAd1, #MsgInLineAd2, #MsgInLineAd3, #MsgInLineAd4, #MsgInLineAd5, #MsgInLineAd6, #MsgInLineAd7, #MsgInLineAd8, #MsgInLineAd9, #MsgInLineAd10, #MsgInLineAd11, #MsgInLineAd12, #MsgInLineAd13, #MsgInLineAd14, #MsgInLineAd15, #MsgInLineAd16, #MsgInLineAd17, #MsgInLineAd18, #MsgInLineAd19, 
    #HKGHeaderGoogleAd, #HKGBottomGoogleAd, #HKGTopGoogleAd {
        display: none !important;
    }
    span#HKGTopAd,
    span#MainPageAd1,
    span#ctl00_ContentPlaceHolder1_MiddleAdSpace1,
    span#ctl00_ContentPlaceHolder1_MainPageAd1,
    div#ctl00_ContentPlaceHolder1_MiddleAdSpace1
    {
        display: none !important;
    }

    /* left panel */
    .PageLeftPanel
    {
        display: none !important;
    }
    .ContentPanel {
        padding-left: 0 !important;
    }

    /* wrap */
    .PageWidthContainer {
        width: 90% !important;
    }
    .PageWidthContainer > img {
        display: none !important;
    }

    /* top menu */
    .MenuPanel {
        display: none !important;
    }

    input, select {
        -moz-border-radius: .1em;
    }
    input#searchstring {
        width: 15em !important;
    }

}


/* topic list */
@-moz-document url-prefix(http://forum1.hkgolden.com/topics.aspx),
               url-prefix(http://forum2.hkgolden.com/topics.aspx),
               url-prefix(http://forum3.hkgolden.com/topics.aspx),
               url-prefix(http://forum4.hkgolden.com/topics.aspx),
               url-prefix(http://forum5.hkgolden.com/topics.aspx),
               url-prefix(http://forum6.hkgolden.com/topics.aspx),
               url-prefix(http://forum7.hkgolden.com/topics.aspx),
               url-prefix(http://forum8.hkgolden.com/topics.aspx)
               {

    div#HotTopics td[height="52"] {
        display: none !important;
    }

    /* top table */
    .ContentPanel > script + table {
        width: 100% !important;
    }
    .ContentPanel > script + table > tbody > tr > td {
        width: 100% !important;
    }
    .Topic_ForumInfoPanel > table > tbody > tr:nth-child(3),
    .Topic_ForumInfoPanel > table > tbody > tr:nth-child(4) {
        display: none !important;
    }

    /* nav bar */
    div#HotTopics + table + table td[style="width: 27%;"] {
        width: 6em !important;
    }
    div#HotTopics + table + table td[style="width: 46%;"] {
        width: auto !important;
    }

    /* footer notice */
    div#HotTopics + table + table + br + script + br + table table {
        width: auto !important;
    }
    div#HotTopics + table + table + br + script + br + table td {
        width: auto !important;
        padding-right: 1em !important;
    }

    /* function panel */
    .Topic_FunctionPanel {
        margin-bottom: 1em !important;
    }

}


/* post */
@-moz-document url-prefix(http://forum1.hkgolden.com/view.aspx),
               url-prefix(http://forum2.hkgolden.com/view.aspx),
               url-prefix(http://forum3.hkgolden.com/view.aspx),
               url-prefix(http://forum4.hkgolden.com/view.aspx),
               url-prefix(http://forum5.hkgolden.com/view.aspx),
               url-prefix(http://forum6.hkgolden.com/view.aspx),
               url-prefix(http://forum7.hkgolden.com/view.aspx),
               url-prefix(http://forum8.hkgolden.com/view.aspx)
               {

    /* nav bar top */
    /* [ad space] do the same things */
    div#ctl00_ContentPlaceHolder1_view_form > script + table + table + table + table td[height="52"] {
        display: none !important;
    }

    /* floor */
    .repliers_left table {
        width: 8em !important;
    }

    /* ad space */
    div#ctl00_ContentPlaceHolder1_view_form table td[height="52"] {
        display: none !important;
    }

    /* info panel no ad */
    div#ctl00_ContentPlaceHolder1_view_form > script + table > tbody > tr > td > table > tbody > tr:nth-child(3),
    div#ctl00_ContentPlaceHolder1_view_form > script + table > tbody > tr > td > table > tbody > tr:nth-child(4) {
        display: none !important;
    }

}



/* search */
/* tags */
@-moz-document url-prefix(http://forum1.hkgolden.com/search.aspx),
               url-prefix(http://forum2.hkgolden.com/search.aspx),
               url-prefix(http://forum3.hkgolden.com/search.aspx),
               url-prefix(http://forum4.hkgolden.com/search.aspx),
               url-prefix(http://forum5.hkgolden.com/search.aspx),
               url-prefix(http://forum6.hkgolden.com/search.aspx),
               url-prefix(http://forum7.hkgolden.com/search.aspx),
               url-prefix(http://forum1.hkgolden.com/tags.aspx),
               url-prefix(http://forum2.hkgolden.com/tags.aspx),
               url-prefix(http://forum3.hkgolden.com/tags.aspx),
               url-prefix(http://forum4.hkgolden.com/tags.aspx),
               url-prefix(http://forum5.hkgolden.com/tags.aspx),
               url-prefix(http://forum6.hkgolden.com/tags.aspx),
               url-prefix(http://forum7.hkgolden.com/tags.aspx)
               {

    /* ad space */
    div#ctl00_ContentPlaceHolder1_topics_form table td[height="52"] {
        display: none !important;
    }

    /* footer notice */
    #ctl00_ContentPlaceHolder1_topics_form > script + table > tbody > tr > td > table + table + table + table + table + br + table table[width="196"] {
        width: auto !important;
    }
    #ctl00_ContentPlaceHolder1_topics_form > script + table > tbody > tr > td > table + table + table + table + table + br + table table[width="196"] td {
        width: auto !important;
        padding-right: 1em !important;
    }

    /* info panel 100% width */
    #ctl00_ContentPlaceHolder1_topics_form > script + table > tbody > tr > td > table > tbody > tr > td:nth-child(2),
    #ctl00_ContentPlaceHolder1_topics_form > script + table > tbody > tr > td > table > tbody > tr > td:nth-child(3) {
        display: none !important;
    }

    /* info panel no ad */
    #ctl00_ContentPlaceHolder1_topics_form > script + table > tbody > tr > td > table > tbody > tr > td > table > tbody > tr:nth-child(3),
    #ctl00_ContentPlaceHolder1_topics_form > script + table > tbody > tr > td > table > tbody > tr > td > table > tbody > tr:nth-child(4) {
        display: none !important;
    }

}
