@-moz-document domain("hkgolden.com") {

    body {
        background: #444444 !important;
        color: #cccccc !important;
    }
    .TopMenuPanel,
    .PageMiddleBox
    {
        background: transparent !important;
    }

    input, select {
        -moz-appearance: none !important;
        border-color: #111111 !important;
        border-style: solid !important;
        border-width: .02em !important;
        background: #222222 !important;
        color: #cccccc !important;
    }

    /* global link */
    a {
        color: #cccccc !important;
    }
    a:visited {
        color: #888888 !important;
    }
    a:hover {
        color: #6495ed !important;
    }

    /* logo */
    img#ctl00_TopBarHomeImage {
        width: 0 !Important;
        padding-right: 220px !important;
        height: 115px !important;
        background: url("data:image/png;base64,") !important;
        background-repeat: no-repeat !important;
    }


    p, td {
        color: #cccccc !important;
    }


    .FooterPanel .txt_11pt_1A3448 {
        color: lightblue !important;
    }

}





/* forum */
@-moz-document url(http://forum1.hkgolden.com/),
               url(http://forum2.hkgolden.com/),
               url(http://forum3.hkgolden.com/),
               url(http://forum4.hkgolden.com/),
               url(http://forum5.hkgolden.com/),
               url(http://forum6.hkgolden.com/),
               url(http://forum7.hkgolden.com/)
               {

    /* top info table */
    .ContentPanel > script + table td[style="background-color: white;"],
    .ContentPanel > script + table td[style="background-color: white; color: red; font-weight: bold;"],
    .ContentPanel > script + table td[width="302"]
    {
        background: #333333 !important;
    }

    .HitSearchText {
        color: #cccccc !important;
    }

    /* table */
    .ContentPanel > script + table + table + table td[style="width: 3%; background-color: rgb(248, 248, 248); text-align: center;"],
    .ContentPanel > script + table + table + table td[style="width: 44%; background-color: rgb(248, 248, 248);"],
    .ContentPanel > script + table + table + table td[style="width: 11%; background-color: rgb(248, 248, 248);"],
    .ContentPanel > script + table + table + table td[style="width: 8%; background-color: rgb(248, 248, 248);"],
    .ContentPanel > script + table + table + table td[style="width: 14%; background-color: rgb(248, 248, 248);"],
    .ContentPanel > script + table + table + table td[style="width: 20%; background-color: rgb(248, 248, 248);"]
    {
        background: #333333 !important;
    }
    .ContentPanel > script + table + table + table td[style="width: 3%; background-color: rgb(255, 255, 255); text-align: center;"],
    .ContentPanel > script + table + table + table td[style="width: 44%; background-color: rgb(255, 255, 255);"],
    .ContentPanel > script + table + table + table td[style="width: 11%; background-color: rgb(255, 255, 255);"],
    .ContentPanel > script + table + table + table td[style="width: 8%; background-color: rgb(255, 255, 255);"],
    .ContentPanel > script + table + table + table td[style="width: 14%; background-color: rgb(255, 255, 255);"],
    .ContentPanel > script + table + table + table td[style="width: 20%; background-color: rgb(255, 255, 255);"]
    {
        background: #444444 !important;
    }

    /* subtitle in table */
    .ContentPanel > script + table + table + table td[colspan="6"][style="background-color: rgb(163, 193, 224);"] {
        color: #333333 !important;
        background: slategray !important;
    }

    /* reformat date */
    .ContentPanel > script + table + table + table td > p > span[style="color: maroon;"] {
        display: block !important;
    }

    /* forum role */
    .ContentPanel > script + table + table + table + table td[style="background-color: rgb(255, 255, 255);"] {
        background: #333333 !important;
    }

}



/* topics */
@-moz-document url-prefix(http://forum1.hkgolden.com/topics.aspx),
               url-prefix(http://forum2.hkgolden.com/topics.aspx),
               url-prefix(http://forum3.hkgolden.com/topics.aspx),
               url-prefix(http://forum4.hkgolden.com/topics.aspx),
               url-prefix(http://forum5.hkgolden.com/topics.aspx),
               url-prefix(http://forum6.hkgolden.com/topics.aspx),
               url-prefix(http://forum7.hkgolden.com/topics.aspx)
               {

    .HitSearchText {
        color: #cccccc !important;
    }

    /* table */
    .Topic_ForumInfoPanel table td
    {
        background: #333333 !important;
    }
    /* ad space */
    /* can not find a fixed height, ignore */
    /* .Topic_TopRightAdPanel { */
        /* height: auto !important; */
        /* background: red !important; */
    /* } */

    /* topics table */
    #HotTopics td[style="width: 4%; background-color: rgb(248, 248, 248); text-align: center;"],
    #HotTopics td[style="width: 52%; background-color: rgb(248, 248, 248);"],
    #HotTopics td[style="width: 14%; background-color: rgb(248, 248, 248); text-align: center;"],
    #HotTopics td[style="width: 22%; background-color: rgb(248, 248, 248); text-align: center;"],
    #HotTopics td[style="width: 8%; background-color: rgb(248, 248, 248); text-align: center;"]
    {
        background: #333333 !important;
    }
    #HotTopics td[style="width: 4%; background-color: rgb(255, 255, 255); text-align: center;"],
    #HotTopics td[style="width: 52%; background-color: rgb(255, 255, 255);"],
    #HotTopics td[style="width: 14%; background-color: rgb(255, 255, 255); text-align: center;"],
    #HotTopics td[style="width: 22%; background-color: rgb(255, 255, 255); text-align: center;"],
    #HotTopics td[style="width: 8%; background-color: rgb(255, 255, 255); text-align: center;"]
    {
        background: #444444 !important;
    }

    /* reformat date */
    #HotTopics td span[style="color: rgb(128, 0, 0);"] {
        display: block !important;
    }

    /* bottom nav bar */
    .Topic_ListPanel > div + table + table td[style="width: 100%; background-color: rgb(247, 243, 247);"] {
        background: #333333 !important;
    }

}


/* post */
@-moz-document url-prefix(http://forum1.hkgolden.com/view.aspx),
               url-prefix(http://forum2.hkgolden.com/view.aspx),
               url-prefix(http://forum3.hkgolden.com/view.aspx),
               url-prefix(http://forum4.hkgolden.com/view.aspx),
               url-prefix(http://forum5.hkgolden.com/view.aspx),
               url-prefix(http://forum6.hkgolden.com/view.aspx),
               url-prefix(http://forum7.hkgolden.com/view.aspx)
               {

    /* user */
    /* man */
    .repliers_left a[style="font-weight: bold; color: rgb(0, 102, 255);"] {
        color: #5E97F1 !important;
    }
    /* women? */
    .repliers_left a[style="font-weight: bold; color: rgb(255, 0, 102);"] {
        color: #B6554F !important;
    }

    /* top table */
    #ctl00_ContentPlaceHolder1_view_form > script + table td[style="width: 100%; background-color: rgb(243, 242, 241);"],
    #ctl00_ContentPlaceHolder1_view_form > script + table td[style="width: 100%; background-color: rgb(255, 255, 255); font-weight: bold;"]
    {
        background: #333333 !important;
    }

    /* top nav bar */
    #ctl00_ContentPlaceHolder1_view_form > script + table + table + table + table td[style="width: 100%; background-color: rgb(247, 243, 247);"] {
        background: #333333 !important;
    }

    /* main content */
    .repliers_left,
    .repliers_left + td {
        background: #333333 !important;
    }

    /* quote */
    .repliers_left + td blockquote > div {
        color: #6699cc !important;
    }

    /* bottom nav bar */
    #ctl00_ContentPlaceHolder1_view_form table td[style="width: 100%; background-color: rgb(247, 243, 247);"] {
        background: #333333 !important;
    }

    /* fast reply */
    #ctl00_ContentPlaceHolder1_view_form table td[style="width: 100%; background-color: rgb(238, 238, 238);"] {
        background: #333333 !important;
    }


}



