    /*鼠标停留提示*/
    border: 0px;
    border-radius: 50px;
    padding-left: 15px;
    padding-right: 15px;
}
.am-dropdown-content:before {
    display:none;
}
.am-dropdown-content:after {
    display:none;
}
.am-input-group-label {
    /*输入框图标提示*/
    color: #3498DB;
    border: none;
    border-bottom: 2px solid #3498DB;
    background-color: rgba(0, 0, 0, 0);
}
