body{

    background-color: #E1FFE1 ! important;


}

  body>center {
   }
  div.g,body>table,body>center,body>form,#navbar {
    border: solid 2px #0000FF ! important;
    -moz-border-radius: 20px ! important;
    padding: 8px ! important;
    background-color: #FFF0FF ! important;
    margin: 8px 0px !important;
  }
  body>table.bt{
    margin-top:10px;
    background-color: #0000FF ! important;
    border-top: solid 2px #008000 ! important;
    -moz-border-radius: 0px ! important;
    padding: 0px ! important;
  }
  div.g{margin: 8px 0px !important;}
  div.g:hover {
    border: solid 2px #008000 ! important;
    -moz-border-radius: 20px ! important;
    padding: 8px !important;
  } 
  a {
    text-decoration: none ! important;
  }
  a:link {
    color: #008000 ! important;
  }
  a:visited {
    color: #990099 ! important;
  }
  a:hover {
    color: #0000FF ! important;
  }
  h2>a {
    text-decoration: underline ! important;
  }
  div.g[style~="2.5em;"]{
    margin-right: 2.5em !important;
    margin-left: 2.5em !important;
  }
  td.j {
    width: 100% ! important;
    padding-left: 2em ! important;
  }
  td.k, div.ch, table[align="right"], #navbar+center>br {
    display: none ! important;
  }
  #navbar+center>table {
    border:none !important;
    background: #FFFFFF!important;
  }
  input {
  }
  input:not([type="submit"]), select {
    background: #008000 !important;
    border: 1px solid #FF00FF !important;
    color: #660066 !important;
    -moz-border-radius: 20px ! important;
  }
  input:hover, select:hover,input[type="submit"]:hover {
    border: 1px solid #FF99CC !important;
  }
  input[type="submit"], input[type="button"] {
    background:#FFFFFF !important;
    border: 1px solid #008000 !important;
    -moz-border-radius: 20px ! important;
    color:#000000!important;
    cursor:pointer !important;
    margin:.3em .4em 0 !important;
  }
}
