@-moz-document domain(roblox.com)
  {

.people-list-container {

    height: 180px;

    background-color: rgba(0, 0, 0, .3);

    backdrop-filter: blur(10px);

    border-radius: 25px;

}

#fb-root {

    width: 100% !important;

    height: 100% !important;

    position: fixed !important;

    background-image: url(/*[[background-image]]*/) !important;

    background-size: fill !important;

    background-attachment: fixed !important;

}

.dark-theme .content {

    background-color: rgba(0, 0, 0, 0);

    color: #fff;

}

.light-theme .content {

    background-color: rgba(0, 0, 0, 0);

    color: #fff;

}

.places-list {

    height: 274px;

    background-color: rgba(0, 0, 0, .3);

    border-radius: 25px;

    backdrop-filter: blur(10px)

}

.dark-theme .chat-main {

    background-color: #080e1c;

    color: #fff;

}

.chat-main {

    -webkit-transform: translateZ(0);

    transform: translateZ(0);

    -ms-transform: none;

    position: fixed;

    border-top-right-radius: 4px;

    border-top-left-radius: 4px;

    box-shadow: 0 1px 3px hsl(0deg 0% 100% / 74%);

    bottom: 0;

    right: 6px;

    z-index: 1040;

    width: 280px;

    height: 360px;

}

.dark-theme .dialogs .dialog-container .dialog-body {

    background-color: rgb(8 14 28);

    color: #fff;

}

.dark-theme .dialogs .dialog-container .dialog-message-container.message-inbound .dialog-message {

    background-color: #40005d;

    color: #fff;

    border: 1px solid rgba(255, 255, 255, .2);

}

.dark-theme .dialog-container .dialog-input-container .dialog-input {

    background-color: rgb(1 1 2);

    border-color: hsla(0, 0%, 100%, .2);

    color: #bdbebe;

    border-radius: 8px;

}

.dark-theme .dialog-container .dialog-input-container {

    background-color: rgb(1 1 2);

    border-color: hsla(0, 0%, 100%, .2);

    color: #bdbebe;

}

.dark-theme .dialogs .dialog-container .dialog-message-container .dialog-message {

    background-color: #4f586b;

    border: 1px solid #65666833;

    color: #fff;

}

.dark-theme .chat-search {

    background-color: rgba(0, 0, 0, .7);

    border-color: hsla(0, 0%, 100%, .2);

    color: #bdbebe;

    border-bottom: 0;

}

