}

.premium-container {

    max-width: 970px;

    margin: 0 auto;

    backdrop-filter: blur(10px);

    background-color: rgba(0, 0, 0, .3);

}

.horizontally-scrollable {

    transition: left .2s ease;

    backdrop-filter: blur(10px);

    height: 240px;

    background-color: rgba(0, 0, 0, .3);

}

.horizontally-scrollable {

    float: left;

    min-height: 240px;

    position: relative;

    white-space: nowrap;

}

.dark-theme .scroller {

    background-color: #5d5d5d;

    border: none;

}

.light-theme .scroller {

    background-color: #5d5d5d;

    border: none;

}

.dark-theme .scroller.disabled {

    background-color: #505050;

    border: none;

    opacity: .5;

}

.light-theme .scroller.disabled {

    background-color: #505050;

    border: none;

    opacity: .5;

}

.item-cards-stackable {

    width: 100%;

    overflow: hidden;

    background-color: rgba(0, 0, 0, .3);

    border-radius: 25px;

    backdrop-filter: blur(10px);

}

.dark-theme .catalog-content .search-options .search-options-form {

    backdrop-filter: blur(10px);

    border-right: 1px solid #656668;

    background-color: rgba(0, 0, 0, .3);

}

