    backdrop-filter: blur(10px);

}

.profile-container {

    max-width: 970px;

    margin: 0 auto;

    background-color: rgba(0, 0, 0, .3);

}

.messages-container {

    backdrop-filter: blur(10px);

    float: left;

    max-width: 970px;

    padding: 0 15px;

    width: 100%;

    background-color: rgba(0, 0, 0, .3);

    min-width: 762px;

}
