css:
@-moz-document domain("youtube.com") {
:root {
  --cdyt-bg-color-dark: rgb(15, 15, 15);
  --cdyt-bg-color-medium: rgb(33, 33, 33);
  --cdyt-cell-bg-color: rgba(0, 0, 0, 0.2);
  --cdyt-menu-bg-color: rgba(21, 21, 21, 0.8);
  --cdyt-button-color: rgba(255, 255, 255, 0.1);
  --cdyt-button-color-hover: rgba(255, 255, 255, 0.22);
  --cdyt-text-main: rgb(192, 192, 192);
  --cdyt-text-secondary: rgb(142, 142, 142);
  --cdyt-text-highlight: rgb(255, 255, 255);
  --cdyt-text-link: rgb(18, 142, 233);
  --cdyt-color-red: rgb(204, 24, 30);
  --cdyt-color-blue: rgb(22, 122, 198);
  --cdyt-color-yellow: rgb(245, 213, 98);
  --cdyt-color-orange: rgb(255, 85, 0);
  --cdyt-color-purple: rgb(156, 39, 176);
  --cdyt-color-green: rgb(76, 175, 80);
}

.yt-uix-button,
.yt-uix-button:hover,
.guide-item,
.guide-item:hover {
  transition: background-color 0.3s!important;
}

.guide-flyout-trigger.on-hover,
.guide-item:hover,
.guide-item:hover .yt-deemphasized-text,
.guide-collapsible-item .yt-uix-expander-head:hover {
  background-color: var(--cdyt-button-color-hover)!important;
  color: var(--cdyt-text-highlight)!important;
}

#yt-masthead #logo-container span.content-region {
  display: none;
}

#yt-masthead-creation-button {
  width: auto !important;
  padding: 0px !important;
  margin: 0 0 0 0 !important;
}

.yt-ui-ellipsis-nowrap-single-line {
  background-color: transparent !important;
}

#old-browser-alert {
  display: none !important;
}

.ytr-offer-title {
  color: var(--cdyt-text-main) !important;
}

.comment-simplebox-renderer {
  border-bottom: 1px solid var(--cdyt-cell-bg-color) !important;
}

#remove_card {
  background-color: var(--cdyt-cell-bg-color) !important;
  border-color: var(--cdyt-cell-bg-color) !important;
  color: var(--cdyt-text-main) !important;
}

.standalone-collection-badge-renderer-text a {
  background-color: var(--cdyt-cell-bg-color) !important;
  border-color: var(--cdyt-cell-bg-color) !important;
  color: var(--cdyt-text-main) !important;
}

.standalone-collection-badge-renderer-text {
  color: var(--cdyt-text-main) !important;
}

.comment-section-header-renderer {
  color: var(--cdyt-text-main) !important;
}

::-moz-selection {
  background-color: var(--cdyt-color-blue)!important;
  color: var(--cdyt-text-highlight)!important;
}

::selection {
  background-color: var(--cdyt-color-blue)!important;
  color: var(--cdyt-text-highlight)!important;
}

body {
  color: var(--cdyt-text-main)!important;
}

#body-container {
  background: var(--cdyt-bg-color-dark)!important;
}

a,
a:hover {
  color: var(--cdyt-color-blue) !important;
}

h1,
h2,
h3,
h4,
.playlist-videos-list .yt-ui-ellipsis,
.yt-ui-ellipsis,
.yt-lockup,
.branded-page-module-title,
.branded-page-module-title a,
.yt-thumb {
  background-color: transparent!important;
}

.yt-lockup-dismissable .watched-badge {
  content: " \1F441"!important;
  color: var(--cdyt-text-highlight)!important;
  opacity: 0.6!important;
  padding: 0!important;
  font-size: 2em!important;
  background-color: transparent!important;
  height: 100%!important;
  width: 100%!important;
  text-align: center!important;
  line-height: 100px!important;
  text-shadow: 0 0 5px var(--cdyt-bg-color-dark)!important;
  top: 0!important;
  left: 0!important;
  transition: opacity 0.24s ease-in-out!important;
}

.yt-lockup-dismissable .watched-badge:hover {
  opacity: 0!important;
  transition: opacity 0.24s ease-in-out!important;
}

.yt-lockup-dismissable .watched .video-thumb {
  -webkit-filter: blur(2px)!important;
  filter: blur(2px)!important;
  transition: 0.48s!important;
}

.yt-lockup-dismissable .watched:hover .video-thumb {
  -webkit-filter: blur(0)!important;
  filter: blur(0)!important;
  transition: 0.48s!important;
}

.yt-card {
  background-color: var(--cdyt-bg-color-medium)!important;
  box-shadow: 0 1px 2px var(--cdyt-cell-bg-color)!important;
}

.yt-uix-button-text,
.branded-page-module-title,
.branded-page-module-title-text,
.branded-page-module-title a,
.view-count,
.yt-dialog-fg-content,
.yt-uix-overlay-fg-content,
.yt-dialog-fg-content .yt-dialog-title,
.yt-uix-overlay-fg-content .yt-dialog-title {
  color: var(--cdyt-text-main)!important;
}

.yt-dialog-fg,
.yt-uix-overlay-fg {
  border: none!important;
}

.yt-uix-button-default,
.yt-uix-button-default[disabled],
.yt-uix-button-default[disabled]:hover,
.yt-uix-button-default[disabled]:active,
.yt-uix-button-default[disabled]:focus {
  border-color: var(--cdyt-button-color)!important;
  background-color: var(--cdyt-button-color)!important;
  color: var(--cdyt-text-main)!important;
}

.yt-uix-button-default:hover,
.yt-uix-button-text:hover {
  background-color: var(--cdyt-button-color-hover)!important;
  box-shadow: 0 1px 0 var(--cdyt-cell-bg-color)!important;
}

#yt-masthead-container,
.feed-list,
.feed-item-container {
  background-color: var(--cdyt-bg-color-medium)!important;
  border: var(--cdyt-menu-bg-color)!important;
}

.masthead-search-terms-border {
  box-shadow: none!important;
}

.gsst_a {
  position: relative!important;
  right: 32px!important;
}

.gsfi {
  height: 32px!important;
  position: absolute!important;
}
@-webkit-keyframes scaleout {
  0% {
      -webkit-transform: scale(0.0);
  }

  100% {
      -webkit-transform: scale(1.0);
      opacity: 0;
  }
}

@keyframes scaleout {
  0% {
      transform: scale(0.0);
      -webkit-transform: scale(0.0);
  }

  100% {
      transform: scale(1.0);
      -webkit-transform: scale(1.0);
      opacity: 0;
  }
}

.yt-spinner-img {
  width: 20px;
  height: 20px;
  margin: 0 auto;
  background-image: none!important;
  background-color: var(--cdyt-color-red)!important;
  border-radius: 100%!important;
  -webkit-animation: scaleout 1.0s infinite ease-in-out;
  animation: scaleout 1.0s infinite ease-in-out;
}

#appbar-settings-button .yt-uix-button-arrow {
  border-top-color: var(--cdyt-bg-color-dark)!important;
}

#yt-masthead-user-displayname,
#yt-masthead-notifications-title {
  color: var(--cdyt-text-secondary)!important;
}

#yt-masthead-user #sb-button-notify {
  width: 26px!important;
  background-color: transparent!important;
  margin-right: 10px!important;
}

#yt-masthead-user .sb-notif-on .yt-uix-button-content {
  border-color: transparent!important;
}

#guide-container,
#appbar-guide-menu,
.guide-toplevel,
#guide,
#appbar-guide-button-container,
#watch-response {
  background-color: var(--cdyt-bg-color-medium)!important;
}

.branded-page-related-channels-item .yt-close {
  z-index: 2!important;
  cursor: pointer!important;
}

.guide-item.guide-item-selected,
.guide-item.guide-item-selected:hover,
.guide-item.guide-item-selected .yt-deemphasized-text,
.guide-item.guide-item-selected:hover .yt-deemphasized-text {
  background-color: var(--cdyt-color-red)!important;
}

.guide-flyout {
  background-color: var(--cdyt-bg-color-medium)!important;
  border-color: var(--cdyt-bg-color-medium)!important;
}

.guide-flyout-container .guide-view-more {
  cursor: pointer;
}

li.guide-section h3 a {
  color: var(--cdyt-text-secondary)!important;
}

#masthead-appbar {
  background-color: var(--cdyt-bg-color-medium)!important;
  border: var(--cdyt-cell-bg-color)!important;
}

#masthead-expanded-container {
  background-color: var(--cdyt-bg-color-medium)!important;
  border-bottom-color: var(--cdyt-cell-bg-color)!important;
}

#masthead-search label,
#masthead-search-terms,
.masthead-search-terms-border {
  height: 34px;
  top: -2px!important;
  border: none!important;
  background-color: var(--cdyt-bg-color-medium)!important;
  margin-bottom: 0!important;
  border-radius: 3px!important;
}

#masthead-search-term {
  background: var(--cdyt-button-color)!important;
  border: 1px solid var(--cdyt-bg-color-medium)!important;
  box-shadow: none!important;
  color: var(--cdyt-text-highlight)!important;
  border-radius: 2px!important;
  transition: background 0.5s!important;
}

#masthead-search-term:hover,
#masthead-search-term:focus {
  background: var(--cdyt-button-color-hover)!important;
  transition: background 0.5s!important;
}

#masthead-search-terms input {
  padding-left: 10px!important;
}

#search-btn {
  background-color: transparent!important;
  width: 50px!important;
  position: relative!important;
  left: -53px!important;
  bottom: 2px!important;
  border: none!important;
  margin-top: 2px!important;
  margin-left: 3px!important;
  border-radius: 2px!important;
  z-index: 1!important;
  box-shadow: none!important;
}

#yt-masthead-user .yt-uix-button-icon-bell,
#upload-btn.yt-uix-button-has-icon {
  -webkit-filter: invert(1)!important;
  filter: invert(1)!important;
}

#yt-masthead-user #yt-masthead-notifications-button {
  background-color: transparent!important;
}

#yt-masthead-user #yt-masthead-notifications-button:hover {
  box-shadow: none!important;
}

#appbar-settings-button {
  border-left: 1px solid var(--cdyt-bg-color-medium)!important;
}

.yt-uix-clickcard-card-reverse .yt-uix-card-body-arrow-vertical,
.yt-uix-clickcard-card-reverse .yt-uix-card-border-arrow-vertical {
  border-bottom-color: var(--cdyt-bg-color-medium)!important;
}

.yt-uix-clickcard-card.yt-masthead-multilogin .yt-masthead-picker-header,
.yt-uix-clickcard-card.yt-masthead-multilogin .yt-masthead-multilogin-users-header {
  background-color: var(--cdyt-bg-color-medium)!important;
}

.yt-uix-hovercard-card-body,
.yt-masthead-account-picker.yt-uix-clickcard-card-content,
.yt-uix-clickcard-card-border,
.yt-uix-hovercard-card-border {
  background-color: var(--cdyt-bg-color-medium)!important;
  color: var(--cdyt-text-main)!important;
  border-color: transparent!important;
}

.yt-masthead-account-picker-option {
  background-color: var(--cdyt-cell-bg-color)!important;
}

.yt-masthead-account-picker-option .yt-masthead-picker-name {
  color: var(--cdyt-text-main)!important;
}

.yt-uix-button-icon-icon-account-settings {
  -webkit-filter: invert(1)!important;
  filter: invert(1)!important;
}

.yt-masthead-account-picker .yt-masthead-picker-button {
  background-color: var(--cdyt-button-color-hover)!important;
}

.yt-masthead-picker-footer {
  border-top: 1px solid var(--cdyt-cell-bg-color)!important;
}

.yt-uix-card-body-arrow-vertical {
  border-top-color: var(--cdyt-bg-color-medium)!important;
}

.yt-uix-card-border-arrow-vertical {
  border-top-color: var(--cdyt-cell-bg-color)!important;
}

.yt-masthead-picker-footer {
  background-color: var(--cdyt-bg-color-medium)!important;
}

.guide-pinned .guide-pinning-enabled #appbar-guide-menu,
.yt-horizontal-rule {
  border-color: transparent!important;
}

.guide-item,
.yt-uix-redirect-link {
  color: var(--cdyt-text-main)!important;
}

.yt-uix-redirect-link:hover,
.multirow-shelf .yt-uix-expander-head {
  color: var(--cdyt-color-blue)!important;
}

.guide-section-separator {
  border: none!important;
}

.guide-item.guide-item-selected,
.guide-item:hover {
  color: var(--cdyt-text-highlight)!important;
}

.yt-uix-button-default,
.yt-uix-button-shelf-slider-pager,
.yt-uix-form-input-select,
.yt-uix-form-input-checkbox-element,
.yt-uix-button-subscribe-unbranded {
  text-shadow: none!important;
  background-color: var(--cdyt-bg-color-medium)!important;
  border: 1px solid transparent!important;
  color: var(--cdyt-text-main)!important;
}

.yt-uix-button-subscribe-unbranded:hover {
  background-color: var(--cdyt-button-color-hover)!important;
}

.yt-uix-shelfslider-prev {
  border-radius: 3px 0 0 3px!important;
}

.yt-uix-shelfslider-next {
  border-radius: 0 3px 3px 0!important;
}

.site-center-aligned .yt-card {
  background-color: var(--cdyt-bg-color-medium)!important;
}

.yt-ui-menu-content {
  background-color: var(--cdyt-menu-bg-color)!important;
  border-color: var(--cdyt-menu-bg-color)!important;
}

.yt-uix-button-menu {
  background-color: var(--cdyt-menu-bg-color)!important;
  color: var(--cdyt-text-main)!important;
  border: 1px solid transparent!important;
}

.yt-uix-button-menu-item {
  color: var(--cdyt-text-main)!important;
}

.yt-ui-menu-item {
  color: var(--cdyt-text-main)!important;
  transition: ease-in-out color 0.3s!important;
}

.yt-ui-menu-item:hover,
.yt-uix-menu-trigger-selected .yt-ui-menu-item {
  background-color: transparent!important;
  color: var(--cdyt-text-main)!important;
  transition: ease-in-out color 0.15s!important;
}

.yt-uix-button-opacity {
  transition: ease-in-out opacity 0.3s!important;
}

.yt-uix-button-opacity:hover {
  transition: ease-in-out opacity 0.15s!important;
}

.yt-uix-button-arrow {
  border-top-color: var(--cdyt-text-main)!important;
}

.yt-uix-button-subscribed-unbranded {
  background-color: var(--cdyt-button-color)!important;
  border-color: transparent!important;
  color: var(--cdyt-bg-color-dark)!important;
  opacity: 0.6!important;
}

.yt-uix-button-subscribed-unbranded:before {
  content: "\2713"!important;
  padding-left: 2px!important;
  color: var(--cdyt-text-main)!important;
}

a.yt-uix-button.yt-uix-button-epic-nav-item {
  color: var(--cdyt-text-secondary)!important;
  border-bottom: solid 3px transparent!important;
}

.epic-nav-item-heading {
  color: var(--cdyt-text-main)!important;
}

a.yt-uix-button-epic-nav-item:hover:not(.secondary-nav) {
  color: var(--cdyt-text-main)!important;
  border-bottom: 3px solid var(--cdyt-button-color)!important;
}

a.yt-uix-button-epic-nav-item.partially-selected:hover,
button.yt-uix-button-epic-nav-item:hover,
button.yt-uix-button-epic-nav-item.selected,
button.yt-uix-button-epic-nav-item.yt-uix-button-toggled,
.epic-nav-item:hover,
.epic-nav-item.selected,
.epic-nav-item.yt-uix-button-toggled,
.epic-nav-item-heading {
  border-color: var(--cdyt-color-red)!important;
}

.yt-uix-button-default.yt-uix-button-active,
.yt-uix-button-default.yt-uix-button-active,
.yt-uix-button-default.yt-uix-button-active:focus,
.yt-uix-button-default.yt-uix-button-toggled,
.yt-uix-button-default.yt-uix-button-toggled:hover {
  box-shadow: 0 0 3px black inset;
  border-color: var(--cdyt-bg-color-dark)!important;
}

#masthead-positioner:hover #appbar-guide-button:hover {
  background-color: transparent!important;
  border-color: transparent!important;
}

#masthead-positioner:hover .yt-uix-button-icon-appbar-guide {
  opacity: 1;
}

.yt-dialog-base .yt-dialog-header .yt-dialog-close {
  background-color: transparent!important;
}

.yt-uix-form-input-text,
.yt-uix-form-input-textarea {
  background-color: var(--cdyt-bg-color-dark)!important;
  border-color: transparent!important;
  color: var(--cdyt-text-secondary)!important;
}

.yt-uix-form-input-checkbox-container input:checked + .yt-uix-form-input-checkbox-element {
  background-color: var(--cdyt-color-blue)!important;
}

#footer-main {
  border-bottom: none!important;
}

#footer-container,
#footer {
  border: 1px solid transparent!important;
  background-color: var(--cdyt-bg-color-medium)!important;
}

#footer-links a {
  color: var(--cdyt-text-secondary)!important;
}

#footer-links a:hover {
  color: var(--cdyt-color-blue)!important;
}

#footer .yt-picker {
  background-color: var(--cdyt-bg-color-dark)!important;
}

#footer .yt-uix-button-icon-footer-history,
#footer .yt-uix-button-icon-questionmark,
.yt-uix-button-icon-footer-language {
  -webkit-filter: invert(1)!important;
  filter: invert(1)!important;
}

.yt-picker-header,
.yt-picker-hr,
#safety-form p.safety-submit {
  border: 0!important;
}

.yt-picker-header .yt {
  color: var(--cdyt-text-main)!important;
}

.yt-picker-content strong {
  color: var(--cdyt-text-main)!important;
}

.yt-dialog-base {
  background-color: var(--cdyt-bg-color-dark)!important;
}

.yt-dialog-fg {
  box-shadow: 0 0 20px var(--cdyt-bg-color-dark)!important;
}

.yt-dialog-fg,
.yt-dialog-title,
.yt-uix-overlay-actions {
  border-color: transparent!important;
}

.yt-dialog-fg {
  background-color: var(--cdyt-bg-color-medium)!important;
}

.yt-uix-overlay-actions {
  background-color: var(--cdyt-cell-bg-color)!important;
}

.overlay-confirmation-display-name,
.overlay-confirmation-content {
  color: var(--cdyt-text-main)!important;
}

.gssb_m {
  background-color: var(--cdyt-menu-bg-color)!important;
  color: var(--cdyt-text-main)!important;
}

.gssb_e {
  border-color: transparent!important;
}

.gssb_m tr {}

.gsq_a td:hover,
table .gssb_a.gsfs:hover,
.gssb_i td {
  background-color: var(--cdyt-button-color-hover)!important;
  cursor: pointer!important;
}

.sb-on,
.sb-card-border {
  box-shadow: none!important;
  border-color: var(--cdyt-bg-color-dark)!important;
}

.sb-card-arrow {
  border-bottom-color: black!important;
}

.sb-card-body-arrow {
  border-bottom-color: var(--cdyt-bg-color-medium)!important;
}

#sb-target {
  background-color: var(--cdyt-bg-color-medium)!important;
}

.feed-item-main {
  background-color: transparent!important;
}

.feed-header {
  border-color: var(--cdyt-cell-bg-color)!important;
}

.yt-lockup-title a,
.lohp-video-link,
.yt-lockup .yt-lockup-byline a {
  color: var(--cdyt-text-main);
}

.yt-lockup-title a:hover,
.lohp-video-link:hover,
.yt-lockup .yt-lockup-byline a:hover {
  color: var(--cdyt-color-blue);
}

.yt-lockup-notifications-container {
  border-color: transparent!important;
}

.yt-lockup-notification .yt-lockup-title a {
  color: var(--cdyt-text-main)!important;
}

.service-endpoint-replace-enclosing-action-notification {
  border: 2px dotted var(--cdyt-button-color)!important;
}

.yt-uix-button-icon-dismissal {
  background: none!important;
}

.yt-uix-button-icon-dismissal:before {
  content: '\2715'!important;
  color: var(--cdyt-text-main)!important;
  font-size: 16pt;
  line-height: 8pt!important;
  position: relative;
  width: 12px!important;
  height: 12px!important;
}

.branded-page-v2-secondary-col .branded-page-related-channels-see-more a {
  color: var(--cdyt-text-main)!important;
  opacity: 0.75!important;
}

.branded-page-v2-secondary-col .branded-page-related-channels-see-more a:hover {
  opacity: 1!important;
}

.feed-item-main,
.yt-uix-button-menu li.yt-uix-button-menu-new-section-separator {
  border-color: var(--cdyt-cell-bg-color)!important;
}

.feed-item-container .feed-item-main,
.yt-lockup-content,
.yt-lockup-deemphasized-text,
.yt-lockup .yt-lockup-meta a {
  color: var(--cdyt-text-secondary)!important;
}

.feed-item-container.legacy-style .feed-item-main {
  border-color: var(--cdyt-cell-bg-color)!important;
}

.yt-lockup .yt-lockup-meta a:hover {
  color: var(--cdyt-color-blue)!important;
}

.feed-item-content-wrapper.channel-lockup {
  background-color: var(--cdyt-cell-bg-color)!important;
  border-color: transparent!important;
  box-shadow: none!important;
}

.feed-item-channel-rec-text a {
  color: var(--cdyt-text-main)!important;
}

.yt-subscription-button-subscriber-count-unbranded-horizontal {
  background-color: var(--cdyt-cell-bg-color)!important;
  border-color: transparent!important;
  color: var(--cdyt-text-main)!important;
  margin-left: 0!important;
  border-radius: 2px 0 0 2px!important;
}

.yt-subscription-button-subscriber-count-unbranded-horizontal:after,
.yt-subscription-button-subscriber-count-unbranded-horizontal:before {
  border-right-color: var(--cdyt-button-color-hover)!important;
}

.yt-uix-button.yt-uix-button-subscribe-unbranded,
.yt-uix-button.yt-uix-button-subscribed-unbranded {
  border-radius: 2px 0 0 2px!important;
}

.pl-header .pl-header-title {
  color: var(--cdyt-text-main)!important;
}

.yt-lockup-playlist-item {
  border-bottom-color: var(--cdyt-button-color-hover)!important;
}

.yt-badge {
  background-color: var(--cdyt-cell-bg-color)!important;
  color: var(--cdyt-text-secondary)!important;
  border-color: transparent!important;
}

.yt-badge.yt-badge-live {
  color: var(--cdyt-text-main)!important;
}

