}

.interwiki {
    border: none;
    border-radius: 0 !important;
    box-shadow: none;
    background: none;
    background-color: #141414 !important;
    }

.interwiki__title {
        border-bottom: none;
        color: #333333;
        padding-left: 0;
        font-weight: lighter;
        padding-bottom: 2px;
    }

.interwiki__entry {
        color: #333;
    }

.interwiki__entry a, .interwiki__entry a:visited {
        color: #333333;
        font-weight: lighter;
    }

