css:
@-moz-document domain("df-walkthrough.readthedocs.io") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 0px;
             background-color: #151515;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #151515;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 0px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #3E4349;
        }

body, div.body {
	background: #0e0e0e;
	color: #e4f0ff;
}
div.sphinxsidebar p, div.sphinxsidebar h3 a, div.sphinxsidebar a {
	color: #bbb;
	border-color: #444;
}
div.sphinxsidebar ul {
	color: #eee;
}
div.sphinxsidebar h3, div.sphinxsidebar h4 {
	color: #bbb;
}
div.sphinxsidebar input {
	background: #0e0e0e;
	border-color: #151515;
	color: #eee;
}
div.sphinxsidebar #searchbox input[type="submit"] {
	background: linear-gradient(180deg, #151515, #111)
}
tt, code {
	background: #111;
	color: #eee;
}
a:hover {
    color: #e68900;
}
a {
    color: #0095d5;
}
.kbd {
    border: 1px solid #222;
    -moz-box-shadow: 0.1em 0.2em 0.2em #111;
    -webkit-box-shadow: 0.1em 0.2em 0.2em #111;
    box-shadow: 0.1em 0.2em 0.2em #111;
    background-color: #0f0f0f;
    background-image: linear-gradient(to bottom, #151515, #111, #0e0e0e);
}
input {
	background: #0e0e0e;
	border-color: #151515;
	color: #eee;
}
div.footer, div.footer a {
	color: #666;
}
a.headerlink:hover {
	color: #bbb;
	background: #151515;
}
a.headerlink {
	color: #333;
}
div.note, div.topic {
	background: #111;
	border-color: #151515;
}
pre {
	background: #111;
}
.highlight .n, .highlight .nn, .highlight .p {
	color: #eee;
}
.highlight .ow, .highlight .k, .highlight .kn, .highlight .nb {
    color: #0089c4;
}
.highlight .o {
    color: #ca5c00;
}
div.warning {
    background-color: #282020;
    border: 1px solid #241818;
}
tt.xref, code.xref, a tt {
    background-color: #0f0f0f;
    border-bottom: 1px solid #0e0e0e;
}
table.docutils td, table.docutils th {
	border-color: #333;
}
table.docutils {
    border-color: #333;
    -moz-box-shadow: 2px 2px 4px #111;
    -webkit-box-shadow: 2px 2px 4px #111;
    box-shadow: 2px 2px 4px #111;
}
}
