css:
@-moz-document domain("flote.app") {
:root {
    --dark-bg-color: #101010;
    --text-bg-color: #303030;
    --text-darker-bg-color: #202020;
    --text-bg-gray-color: #404040;
    --gray-border-color: #6b6b6b;
    --text-color: #c0c0c0;    
    --text-dim-color: #7f7f7f;    
    --text-dimmer-color: #a0a0a0;
    --text-less-color: #b0b0b0;
    --post-header-color: #5cb7d6;
    --post-timestamp-color: #0dce63;
    --post-rating-color: #88929c;
    --post-3dot-menu-color: #c0c0c0;
    --profile-text-color: #8a9ad4;
    --blue-tab-color: #053d63;
    --blueish-text: #386b7d;
    --blueish-text-medium: #397489;
    --blueish-text-lighter: #4e9cb8;
    --green-mint-text: rgb(17, 197, 97);
    --green-mint-dim-text: rgb(9, 127, 62);
    --text-dblue-bg-color: #1d3a6e;
    --text-orange-color: #b20;
    --dark-transparant-background-color: rgba(16,16,16,.8);
    --selected-nav-color: #18c2fd;        
}

a {
    text-decoration: none;
    color: var(--post-timestamp-color);
}

h4 {
    color: var(--blueish-text-lighter);
}

a:visited {
    color: var(--green-mint-dim-text);
}

.hover-tdu, bold quarter-margin-bottom half-margin-right one-line-ellipsis {
    color: var(--post-header-color);
}

.bold, half-margin-right one-line-ellipsis {
    color: var(--post-header-color);
}

.flex-1, notification-text {
    color: var(--text-orange-color);
}

/*blue-head icon left of notification row*/
.notification-type-icon, subscription {
    filter: invert(80%);
}

/*begin Top bar*/
.nav[_ngcontent-universal-cli-c93] {
    background: var(--text-darker-bg-color);
    border-bottom: 1px solid var(--text-darker-bg-color);
}

/*home button*/
.nav-buttons[_ngcontent-universal-cli-c96] .nav-button.on[_ngcontent-universal-cli-c96] {
    color: var(--green-mint-text);
}

/*Search*/
.nav-search[_ngcontent-universal-cli-c93] .nav-search-input[_ngcontent-universal-cli-c93] {
    color: var(--text-color);
    background: url(https://flote.app/assets/img/assets/search.svg) left 10px center/16px auto no-repeat,black;
}

.nav-search[_ngcontent-sc109]   .nav-search-input[_ngcontent-sc109]
{
    color: var(--text-color);
    background:        url(https://flote.app/assets/img/assets/search.svg) left 10px center/16px auto no-repeat,black;
}
.nav-search[_ngcontent-universal-cli-c93] .nav-search-input[_ngcontent-universal-cli-c93]:focus {
    border-color: var(--text-bg-gray-color);
    background: url(https://flote.app/assets/img/assets/search-blue.svg) left 10px center/16px auto no-repeat,black;
}
.nav-search[_ngcontent-sc109]   .nav-search-input[_ngcontent-sc109]
{ 
 background-color: black;   background:url(https://flote.app/assets/img/assets/search.svg) left 10px center/16px auto no-repeat,black;
}

/*end top bar*/

/*links*/
.blue-link, a.blue-link {
    color: var(--green-mint-text);
}

/*Top right hashtag list lines*/
.column-card-entry:not(:last-child) {
    border-bottom: 1px solid var(--text-bg-color);
}

/*Recommended lines*/
border:not(:last-child) {
    border-bottom: 1px solid var(--text-bg-color);
}
/*recommended lines bottom*/
.list-bottom-border-all, .list-bottom-border:not(:last-child) {
    border-bottom: 1px solid var(--text-darker-bg-color);
}

/*Follow buttons*/
[_nghost-universal-cli-c39] {
    color: var(--green-mint-dim-text);
    border: 1px solid var(--green-mint-dim-text);
}

.editor-container.inline[_ngcontent-universal-cli-c61] {
    background: var(--text-darker-bg-color);
}

.new-text-area[_ngcontent-universal-cli-c61] {
    background: var(--dark-bg-color);
}

[_nghost-universal-cli-c64] {
    background-color: var(--dark-bg-color);
}

.post[_ngcontent-universal-cli-c53] {
    background: var(--text-darker-bg-color);
    color: var(--text-color);
}

[_nghost-universal-cli-c53]:not(:last-child) {
    border-bottom: 1px solid var(--dark-bg-color);
}

/*profile name in post*/
.post-body-author[_ngcontent-universal-cli-c53] .post-author-display-name[_ngcontent-universal-cli-c53] {
    color: var(--post-header-color);
}

/*"show" link in post to see more*/
.post-read-more[_ngcontent-universal-cli-c53] {
    color: var(--green-mint-text);
}


/*main background*/
[_nghost-universal-cli-c98] {
    background: var(--dark-bg-color);
}

/*Header*/
.nav[_ngcontent-universal-cli-c96] {
    background: var(--text-bg-gray-color);
    border-bottom: 1px solid black;
    border-bottom-color: black;
}

/*Feed header*/
.editor-container.inline[_ngcontent-universal-cli-c64] {
    background: var(--text-bg-color);
}

/*new new post entry*/
.new-text-area[_ngcontent-universal-cli-c64] {
    background: #000000;
    background-color: rgb(0, 0, 0);
    color: #364d63;
}




/*BEGIN INDIVIDUAL POST PAGE*/

/*page background*/
[_nghost-universal-cli-c95] {
    background: var(--dark-bg-color);
}

/*END INDIVIDUAL POST PAGE*/


/*BEGIN NOTIFICATONS PAGE*/

/*END NOTIFICATIONS PAGE*/
}
