	text-align: center;
	margin: 2mm auto 0;
	max-height: 85px;
	overflow: hidden;
	opacity: .6;
	transition: .3s .2s
}

#_popIn_recommend:hover {
	max-height: 275px;
	opacity: 1
}

#_popIn_img3_pc ._popIn_recommend_art_img {
	overflow: hidden
}

.page_separator+.page_info {
	color: transparent;
	position: relative;
	height: 0;
}

.page_separator+.page_info .page_num {
	position: absolute;
	top: -1.83em;
	left: calc(50% - 2.4em);
	color: #555;
	background: #eee;
	white-space: nowrap;
	padding: 0 3mm;
	border-radius: 5mm;
	transition: .2s;

}

.page_separator+.page_info .page_num:hover {
	background: #fff;
	box-shadow: 0 0 1mm #666;
}

.page_separator+.page_info .page_num:before {
	color: #666;
	content: "第 ";
}

.page_separator+.page_info .page_num:after {
	color: #666;
	content: " 頁";
}

.page_separator+.page_info .page_num:hover,
.page_separator+.page_info .page_num:hover:before,
.page_separator+.page_info .page_num:hover:after {
	color: #833;
}

/* writing */
.l-form__content {
	padding: 0 10mm
}

.l-formRow__title--lg {
	width: 6em
}

.c-textarea {
	height: auto !important;
	min-height: 20em;
	max-height: 20em;
	padding: 2mm 3mm;
	transition: .5s
}

.c-textarea:focus {
	max-height: 100vh;
	overflow: scroll;
}

.emotion {
	height: auto !important;
	background-color: #f3f3ec;
	border-radius: 2mm;
	box-shadow: inset 0 0 5px 1px #777;
	padding: 2mm;
	max-height: 80px;
	transition: max-height .3s
}

.emotion:hover {
	max-height: 300px;
	transition: max-height .3s
}

.emotion a {
	margin: 1mm 2mm 0;
}

.c-textarea,
.l-articleReply {
	overflow: hidden;
	border-radius: 2mm;
	background-color: #eee;
	box-shadow: inset 0 0 5px 1px #777
}

.c-articleReply {
	height: auto;
	padding-bottom: 10mm;
	max-height: 250px;
	transition: max-height .6s .5s
}

.c-articleReply:hover {
	max-height: 1200px;
	transition: max-height .3s .2s
}

.u-overflow:after {
	height: 40px;
	background: linear-gradient(180deg,
			rgba(200, 200, 200, 0) 0,
			#bbb 100%)
}

.l-introduction__pick {
	position: relative
}

.l-introduction__pick .blockquote {
	position: absolute;
	top: 1em
}

/* search result */

.c-dropdown__select>li {
	margin: 0 1mm 0 0
}

.c-dropdown__select>li>span,
.c-btn {
	font-weight: 400;
	line-height: 1;
	padding: 1.5mm 2mm;
	border-radius: 2mm !important;
	transition: .2s;
}

.c-btn--secondary {
	background: #eee
}

.c-defaultBlock {
	background-color: #eee;
	border-radius: 3mm
}

.l-searchCard {
	position: relative;
	padding: 0 !important;
	margin: 2mm 0mm 5mm;
	background-color: rgba(130, 130, 180, .1);
	border: 1px solid #fff;
	overflow: hidden;
	transition: background-color .3s
}

.l-searchCard:hover {
	/*background-color: rgba(255, 255, 255, .7);*/
	box-shadow: 0 1px 5px #666;
	transition: background-color 0s
}

.l-searchCard__content .l-thumbDesc__pic--lg {
	padding: 0;
	background-color: #a1a1a1;
	height: auto;
	border-right: 1px solid #eee;
	z-index: 1;
}

.l-searchCard__content .l-thumbDesc__pic--lg>img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	opacity: .75;
	transition: opacity .3s
}

.l-searchCard__content:hover .l-thumbDesc__pic--lg>img {
	opacity: 1;
	transition: opacity .1s
}

.l-searchCard__content .c-link {
	color: #064
}

.gsc-control-cse {
	border: none;
	background: transparent
}

.gsc-webResult .gsc-result {
	background-color: #eaeaf2;
	border-radius: 2mm;
	padding: 2mm;
	margin: 3mm 0;
	box-shadow: 0 0 2mm #888
}

.l-thumbDesc__excerpt {
	padding: 1mm 4mm;
}

.l-excerpt__title+.l-excerpt__desc {
	color: #555;
	margin-top: 1mm;
	height: auto;
	line-height: 1.4em;
	-webkit-line-clamp: 3;
	padding: .2em 0;
}

.l-excerpt__title+.l-excerpt__desc .c-link {
	position: relative;
	display: block;
	float: right;
	margin-right: 1mm;
	opacity: .8
}

.l-excerpt__title+.l-excerpt__desc .c-link:hover {
	opacity: 1
}

.l-excerpt__title+.l-excerpt__desc .c-link:after {
	content: "...";
}

.l-excerpt__desc+.l-excerpt__nav {
	margin-top: 1.5em;
}

.l-searchCard .l-navigation,
.l-searchCard .l-navigation__item {
	display: inline-block;
	width: auto;
	padding: 0;
}

.l-searchCard__content .l-excerpt__nav .l-navigation:nth-of-type(1) {
	position: absolute;
	top: 3px;
	right: 5mm
}

.l-searchCard__content .l-excerpt__nav .l-navigation:nth-of-type(2) {
	position: absolute;
	right: 4px;
	bottom: 0;
	line-height: 1.1em;
	border-radius: 5mm;
	background-color: rgba(210, 210, 210, .6);
	z-index: 2
}

.l-listTable--msg .c-checkbox {
	padding: 3px 0 0 2mm;
}

.l-listTable--msg .l-listTable__td:nth-of-type(2) {
	padding: 1em 2mm
}

.l-listTable--msg .l-listTable__td:nth-of-type(3) {
	padding: 2mm 1mm
}

.l-listTable--msg .l-listTable__td:nth-of-type(4) {
	padding: 0 2mm
}

.l-listTable--msg .u-ellipsis {
	display: inline-block;
	line-height: 1.25;
}

.l-tab {
	padding: 2mm 0 0 0
}

.c-tabNav__wrap>li {
	background-color: rgba(250, 250, 245, .3);
	margin: 2mm 2mm 0 2mm;
	border-radius: 3mm 3mm 0 0;
	box-shadow: 0 0 0 1px rgba(0, 0, 0, .1);
}

.c-tabNav__wrap>li.is-active {
	background-color: rgba(250, 250, 245, .8);
	border-width: 2px;
	box-shadow: 0 0 2mm #777;
}

.c-tabNav__wrap>li a {
	display: inline-block;
	font-weight: 500;
	padding: 1.5mm 5mm;
}

.l-tab__container {
	display: none;
	padding: 3mm;
	margin: 0 0 2mm;
	border-radius: 0 0 3mm 3mm;
	background: linear-gradient(180deg,
			rgba(250, 250, 245, .8) 0,
			rgba(250, 250, 245, .5) 100%);
	box-shadow: 1px 1px 3mm #888
}

.l-tab__container[data-tab-id="score_record"] .l-listTable__td:first-child {
	width: 50%
}

.l-excerpt__title {
	font-weight: 500;
	height: auto;
}

.l-excerpt__title .c-link {
	display: inline-block;
	line-height: 1.4em;
}

/* market */
.l-wrap>div[itemprop="articleBody"] {
	background-color: rgba(255, 255, 255, .5);
}

.l-wrap>div[itemprop="articleBody"] .l-cols {
	padding: 3mm;
}

.c-iconBox__input {
	padding: 5px 3mm 5px 10mm
}

.c-iconBox__icon {
	top: 10px
}

.l-separationBg {
	width: calc(100% - 6mm);
	min-height: 30vh;
	padding: 3mm
}

.c-toggleTrigger--searchBar,
.l-searchBarToggle {
	background-color: rgba(255, 255, 255, .3)
}

.c-toggleTrigger--searchBar {
	border-radius: 2mm 2mm 0 0;
	margin: 0 1mm
}

.l-searchBarToggle {
	border-radius: 3mm
}

.u-gapBottom--sm,
.u-gapBottom--lg {
	overflow: hidden
}

.u-gapBottom--lg {
	margin: 0 0 5mm
}

.l-chosen .u-gapBottom--lg {
	background: #ccc
}

/* event */
.l-selectedThumb--specialTopic {
	background-size: cover;
	border-radius: 1.5mm;
	box-shadow: 0 0 0 1mm #aaa
}

.u-gapBottom--lg .l-selectedList {
	background-color: #e6e3dd;
	padding: 0 3mm
}

.c-selectItem {
	padding: 6px 0
}

/* footer */
.l-footer {
	position: absolute;
	left: 0;
	bottom: 0;
	width: 100vw;
	box-shadow: 0 0 2mm #888
}

.l-footerLinks {
	font-size: 0;
	display: block;
	text-align: center
}

.l-footerLinks>li {
	display: inline-block;
	margin: 0
}

.l-footer__copyright .list>span>a>img {
	margin-right: 1mm;
}

.u-gapRight--max {
	margin-right: 10mm;
}

/* round corners */
.c-mainCard,
.u-gapBottom--lg:first-child,
.u-gapBottom--sm,
.c-panel_content,
.c-dropdown__select ul,
#_popIn_img3_pc ._popIn_recommend_art_img {
	border-radius: 5px;
	box-shadow: 0 1px 1.5mm #888
}

.c-authorCard,
.l-wrap>.u-gapNextV--lg:nth-of-type(2),
.l-content__side .c-articleCard,
.c-group__options,
.c-dropdown--sort .c-dropdown__select ul,
.l-listTable,
.l-articlePage,
.l-searchCard,
.l-memberIntro,
.l-separationBg,
.l-wrap>div[itemprop="articleBody"] {
	border-radius: 2mm;
	box-shadow: 0 1px 2mm #666
}

.l-signInPage__content,
.l-content__main {
	border-radius: 3mm;
	box-shadow: 1px 2px 2mm #444
}

@media screen and (max-width: 1250px) {

	.t1,
	.t2 {
		font-size: 19px !important
	}

	.o-mainCardDesc {
		font-size: 16px
	}

	.c-mainCard__desc {
		padding: 3mm 5mm;
	}

	.l-content__side:hover {
		left: calc(-8vw - 300px);
	}
}

@media screen and (min-width: 1251px) {
	body {
		overflow-x: hidden;
		scrollbar-width: none;
		-ms-overflow-style: none
	}

	.t1,
	.t2 {
		font-size: 1.5vw !important
	}

	.o-mainCardDesc {
		font-size: 1.3vw
	}

	.c-mainCard__desc {
		padding: .8vw 1.5vw
	}

	.l-content__side:hover {
		left: calc(-8vw - 300px);
	}
}

@media screen and (min-width: 1600pt) {
	body {
		overflow-x: hidden;
		scrollbar-width: none;
		-ms-overflow-style: none
	}

	.t1,
	.t2 {
		font-size: 30px !important
	}

	.l-wrap {
		max-width: 1600pt
	}

	.l-content__main {
		max-width: calc(1600pt - 28mm)
	}
}


@media screen and (min-width: 1680pt) {
	.l-content__side:hover {
		left: calc(40vw - 1030pt);
	}
}
}
