css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("checkvist.com") {

td.tasks b {
background-color: #5B768A !important;
}

td.tasks p {
border: solid 1px #5B768A !important;
color: #5B768A !important;
}

a:link, a:active, a:visited { color: #213B4A !important;}

.comments a {
color: #5B768A !important;
}

ul.menuPopupList li.selected a, ul.menuPopupList li.selected a:visited, ul.optionsPopup li.selected {
color: #5B768A !important;
}

#toolbar {
border-color: #D9D9D9 #D9D9D9 #213B4A !important;
}

h1, h2.formName, h5.keyShortcuts {
color: #213B4A !important;
}

li.dndNode div.selectedTask {
background-color: #eee !important;
border-bottom: solid 1px #fff !important;
border-top: solid 1px #fff !important;
}

div.coreDiv span.fg1 {
color: #AB4E5A !important;
}

div.coreDiv span.fg2 {
color: #4678AB !important;
}

div.coreDiv span.fg3 {
color: #48ABA3 !important;
}

li.dndNode div.selectedTask {
color: #222 !important;
}

ul.menuPopupList li.selected {
background-color: #eee !important;
}

a.undo {
background-color: #FC7EA2 !important;
color: #fff !important;
font-weight: bold !important;
padding: 3px 10px !important;
}

.cl_nw, .cl_n, .cl_ne {
background-color: #213B4A !important;
}






}
