	background-image: linear-gradient(to bottom right, #545352, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.button--disabled:hover
 {
	background-image: linear-gradient(to bottom right, #656463, #000)!important;
	color: #ff0!important;
}

.button--disabled:active
 {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Green ________________________________________________________________________*/

#shopify-product-reviews .spr-button,
#shopify-product-reviews .spr-summary-actions-newreview,
.shopify-payment-button__button--unbranded
 {
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

#shopify-product-reviews .spr-button:hover,
#shopify-product-reviews .spr-summary-actions-newreview:hover,
.shopify-payment-button__button--unbranded:hover
 {
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

#shopify-product-reviews .spr-button:active,
#shopify-product-reviews .spr-summary-actions-newreview:active,
.shopify-payment-button__button--unbranded:active
 {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

.card,
.cbb-also-bought-container,
.form--main,
.page--no-banner .main,
.page--no-banner .sidebar,
.page__content,
.product-list
 {
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.card:hover,
.cbb-also-bought-container:hover,
.form--main:hover,
.page--no-banner .main:hover,
.page--no-banner .sidebar:hover,
.page__content:hover,
.product-list:hover
 {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/

#modal_content,
.content-box,
.drop_menu,
.mega-menu,
.mini-cart,
.multi_link,
.popover,
.product-item,
.value-picker,
div.snize-ac-results
 {
	background-color: #1c1c1c!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

#modal_content:hover,
.content-box:hover,
.drop_menu:hover,
.mega-menu:hover,
.mini-cart:hover,
.multi_link:hover,
.popover:hover,
.product-item:hover,
.value-picker:hover,
div.snize-ac-results:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Red Gradient _________________________________________________________________*/

.alert,
.empty-state,
div.snize li.snize-no-products-found
 {
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*___ Highlight Background _________________________________________________________*/

div.snize-ac-results li.snize-ac-over-nodrop, div.snize-ac-results li.snize-ac-over-nodrop a.snize-view-link,
div.snize-ac-results li:hover,
div.snize-ac-results li a.snize-view-link:hover
 {
	background: linear-gradient(to right, #383838, #0000)!important;
	border-left-color: #434343!important;
}

/*___ Radial Background _________________________________________________________*/

.block-swatch__radio:checked+.block-swatch__item
{
	background: radial-gradient(ellipse at center, rgba(5, 196, 81, .69) 0%,rgba(14,14,14,1) 75%)!important;
}

.block-swatch:hover
{
	background: radial-gradient(ellipse at center, rgb(237, 255, 5) 0%,rgba(14,14,14,1) 70%)!important;
}

ul.stamped-summary-keywords-list li:hover
{
	background: radial-gradient(ellipse at center, #00809d 0%,rgba(14, 14, 14, 0) 70%)!important;
}



div.snize-ac-results li:last-child:hover {
	border-bottom-left-radius: 22px;
	border-bottom-right-radius: 22px;
}







}

