css:
/* ++++ Changelog ++++
04-07-2006
 - fixed up the left column search box, default overlaps the border
   and looks funny
 - blocked a few more ads in the forums sections
30-04-2006
 - initial release
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anandtech.com") {

/* block ads */
  .adcontainer, .adcontainer + .HeaderSurround,
  #topmarq, #boxfooter, body > div > center ,
  .ftmessageheaderbar > center, body > br
  {
    display: none !important;
  }

/* fix up the "search AnandTech" box */
  #leftcolumn > .atheader, #leftcolumn > .atheader + div 
  {
    width: auto !important;
    padding-left: 0 !important;
    text-align: center !important;
  }
  .smallform_input:first-child
  {
    width: 90% !important;
    padding: 0 !important;
  }

/* increase font size */
/* applies font to all generic HTML tags */
html,
body,
span,
div,
td,
br,
input,
font,
button,
select,
option {
  font-family: Dustimo !important;
  font-size: 10pt !important;
  color: #310031;
  background-color: #cfcfdf;
}
.atsidenav {
  font-family: Dustimo !important;
  font-size: 9pt !important;
}
}
