    color: #444;
    transition: all 0.3s ease-in-out;
}

#logo {
	margin-top: 92px;
    margin-left: 150px;
	font-size: 8px;
	transform: translateZ(0);
}

#logo:before,
#logo:after {
	content: '';
	position: absolute;
	top: 0;
}

#logo:before,
#logo:after,
#logo {
	border-radius: 50%;
	width: 2em;
	height: 2em;
	animation: animation 1.7s infinite ease-in-out;
}

#logo {
	animation-delay: -0.16s;
}

#logo:before {
	left: -3.5em;
	animation-delay: -0.32s;
}

#logo:after {
	left: 3.5em;
}

@keyframes animation {
	0%,
	80%,
	100% {
		box-shadow: 0 2.5em 0 -1.3em #F34A53;
	}
	40% {
		box-shadow: 0 2.5em 0 0 #F34A53;
	}
}
