@-moz-document domain("file.net") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 0px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 0px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #4976D3;
        }


#GreyBox {
	background: linear-gradient(358deg,#111,#1e1e1e);
	color: #eee;
}
body {
	background: #0e0e0e;
}
#commentsblock {
    color: #9accff;
    background-image: linear-gradient(to right, #0e0e0e,#111,#111,#111,#0e0e0e);
}
.fform {
	border-color: #222;
    background-image: linear-gradient(355deg,#111,#0e0e0e);
}
body {
    color: #c9ffff;
}
#navigation {
    border-color: #222;
    background-image: linear-gradient(#222 40%,#0e0e0e 75%);
}
#navigation a:hover {
    color: #4181ff;
    background-image: linear-gradient(#0e0e0e,#222);
}
#navigation a {
    color: #007bff;
}
.linkBG1, .linkBG2 {
    background: #131517;
}
.uninstall {
    color: #b6f4f4;
    background-image: linear-gradient(#0f0f0f,#111);
}
h1, h2 {
	border-color: #222;
}
#footer {
    color: #ccc;
    background-image: linear-gradient(177deg,#0f0f0f,#111);
}
#footer a {
	color: #ccc;
}
#footer a:hover, #footer a:active, #footer a:focus {
	color: #eee;
}
textarea, input, option, select {
	background: #0e0e0e;
	color: #eee;
	border-color: #222;
}
input[type="submit"] {
	background: linear-gradient(180deg, #111 50%, #151515 100%);
}
input[type="submit"]:hover {
	background: linear-gradient(180deg, #141a1e 50%, #222c33 100%)
}
input[type="submit"]:active, input[type="submit"]:focus {
	background: linear-gradient(180deg, #222c33 50%, #2c3942 100%)
}
a {
    color: #00a0ff;
}
[src="img/logo/mspartner.png"], [src="img/powered_by_google.gif"], [src="../img/protect.gif"], [src="../img/logo/mspartner.png"], #adr-e, .hinweis {
	filter: invert(100);
}
.zitat {
	color: #bbb;
}
figcaption {
    color: #eee;
    border-top-color: #22272d;
    background: linear-gradient(to right, transparent, #131313,#131313,transparent);
}
.variants li p, .variants li span {
    color: #bfffff;
}
.variants li {
	color: #444;
}
#JedeZweiteZeile > *:nth-child(odd) {
	background: #111;
}
.usercomment {
	border-color: #222;
}
.gsc-above-wrapper-area {
	border-color: #151515;
}
.usercomment em {
    color: #88a0d0;
}
#lang .spr {
	background: #0e0e0e;
	border-color: #222;
}
.gsc-control-cse, .gsc-webResult.gsc-result, .gsc-results .gsc-imageResult, .gsc-webResult.gsc-result:hover {
	background: #0e0e0e;
	border-color: #0e0e0e;
}
.gsc-result-info, .gsc-orderby-label {
	color: #bbb;
}
.gs-webResult .gs-snippet, .gs-fileFormatType {
	color: #bbb;
}
.gs-webResult.gs-result a.gs-title:active, .gs-webResult.gs-result a.gs-title:active b, .gs-imageResult a.gs-title:active, .gs-imageResult a.gs-title:active b, .gs-webResult.gs-result a.gs-title:hover, .gs-webResult.gs-result a.gs-title:hover b, .gs-imageResult a.gs-title:hover, .gs-imageResult a.gs-title:hover b, .gs-webResult.gs-result a.gs-title:link, .gs-webResult.gs-result a.gs-title:link b, .gs-imageResult a.gs-title:link, .gs-imageResult a.gs-title:link b, .gs-webResult.gs-result a.gs-title:visited, .gs-webResult.gs-result a.gs-title:visited b, .gs-imageResult a.gs-title:visited, .gs-imageResult a.gs-title:visited b {
    color: #176bff;
}
.gsc-results .gsc-cursor-box .gsc-cursor-page {
	background: #0e0e0e;
	color: #555;
}
.gsc-selected-option-container {
	background: #111;
	color: #bbb;
}
.gsc-control-cse .gsc-option-menu {
	background: #0e0e0e;
	color: #aaa;
}
.gsc-option-menu-item-highlighted {
	background: #222;
	color: #ccc !important;
}
.gsc-option-menu-item {
	color: #aaa;
}
.gsc-input-box {
	background: #0e0e0e;
	border-color: #222;
}
#showBlockLink {
    color: #0080ff;
    background-color: #111;
    background-image: linear-gradient(transparent, #222);
	backdrop-filter: blur(1px);
}
small {
	color: #555;
}
#formular {
    background-image: linear-gradient(358deg,#0f0f0f,#111);
}
#formular textarea, #formular input[type=text] {
	border-color: #222;
}
#bgsh {
    background: #131313;
}
}

@-moz-document domain("file.uservoice.com") {
.main {
    background: #111 url(https://widget.uservoice.com/pkg/clients/widget2/background-1f4d3e8….png) 0 0 repeat;
    border-left: 1px solid #16181c;
}
.content {
    background: url(https://widget.uservoice.com/pkg/clients/widget2/translucent_90-5604c69….png);
    background: rgba(0, 0, 0, .9);
}
.main .uvField {
	background: #0e0e0e;
	border-color: #202328;
	color: #eee;
}
input, textarea {
	color: #eee;
}
.browse .meta, .browse .elsewhere {
    color: #6a6e77;
}
.uvStyle-button {
    background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#212326), to(#2f333c));
    -webkit-background-clip: padding-box;
    border-top: 1px solid #16181c;
    border-right: 1px solid #202328;
    border-bottom: 1px solid #1d1f24;
    border-left: 1px solid #202328;
    -moz-box-shadow: inset 0 1px 0 0 rgba(0, 0, 0, .5),0 1px 2px 0 rgba(255, 255, 255, .1);
    -webkit-box-shadow: inset 0 1px 0 0 rgba(0, 0, 0, .5), 0 1px 2px 0 rgba(255, 255, 255, .1);
    box-shadow: inset 0 1px 0 0 rgba(0, 0, 0, .5), 0 1px 2px 0 rgba(255, 255, 255, .1);
    color: #bcc9e8;
    text-shadow: 0 1px 0 rgba(0, 0, 0, .5);
}
}
