css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("animenewsnetwork.com") {*{/*$*/}
	a, a:hover, a:visited, a:focus { text-decoration: none !important; outline: none !important; }
	body, #page, #canvas, #header, #menu, #content, div.middle-area, span.top-area {
		color:			AliceBlue !important;
		background:		#111 !important;
	}
	.herald-boxes .herald.box .wrap .preview .full {
		color:			AliceBlue !important; }

	body.grid-mode-not-1 .grid-view .mainfeed-section .herald.box .preview:before {
		background-image:linear-gradient(rgba(17,17,17,0) 0%, rgba(17,17,17,0) 70%, rgba(17,17,17,1) 100%) !important; }
		#sidebar .herald.box .preview:before {
		background-image:linear-gradient(rgba(34,34,34,0) 0%, rgba(34,34,34,0) 70%, rgba(34,34,34,1) 100%) !important; }

	#aside, #sidebar div.herald-boxes, #aside a.more {
	background:			#222 !important; }

	div.iab.top[style="height: 116px;"], div.box.iab.side[style="height: 628px;"], div#link-exchange, div.iab.buttons {
		display:		none !important; }
	.side.gutter span {
		height:			56px !important; }
}
