@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("chrome://taboo/content/start.html") {

.grid {background: #404040 !important;}
.s125 > li {
-moz-box-shadow: 2px 2px 6px rgba(0, 0, 0, 0.7) !important;
margin: 5px 5px 20px 20px !important;}

#header {
border-top: 1px solid #dcdcdc !important;
border-bottom: 1px solid #404040 !important;
background:transparent url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAbCAYAAAC9WOV0AAAAU0lEQVQImU3EuQ0AIQwEwO2/ERAC8YhX4MKIluAc3AQDEbkQEX6dc4i9N7HW0uacxBiD6L1rrTWi1kqUUrScM5FSImKMWgjhn/eecM5p1lrCGHMfSCNTcRAlrXYAAAAASUVORK5CYII=) repeat-x !important;
padding: 3px !important;
-moz-box-shadow: 0 3px 3px rgba(0, 0, 0, 0.3) !important;}

#search {
background: #fff url(data:image/png;base64,) no-repeat 2px 1px !important;
-moz-border-radius: 10px !important;
-moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.2) !important;}

#footer {
background: transparent url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAeCAYAAADtlXTHAAAAfElEQVQImS3EbQ+BUACG4ftv2ijazno9OpKVSSTMSyt+8eOL68PFzFRiHh3EImyEFzbCi4/Ci9t/ftIKPz2LZXoSK9uJwF5EYDth1r0weS+Mu4rQ3UVU3ERSPES6fYqsfIls9xZ2N4isGkRej8LtJ+HqUeTNJMr2KzbtRz8kOUaQ9kihAwAAAABJRU5ErkJggg==) repeat-x !important;
border-top: 1px solid #383f4c !important;
padding: 5px !important;}

:focus {outline: 0 !important}

#grid, #calendar, #mosaic, #trash, #tools, #about {
width:20px !important;
height:12px !important;
float:left !important;
font-size: 0 !important;
border: none !important;
margin: 0 -2px 0 0 !important;}

#grid {background:transparent url(data:image/png;base64,) no-repeat !important;}
#calendar {background:transparent url(data:image/png;base64,) no-repeat !important;}
#mosaic {background:transparent url(data:image/png;base64,) no-repeat !important;}
#trash {background:transparent url(data:image/png;base64,) no-repeat !important;}
#tools {background:transparent url(data:image/png;base64,) no-repeat !important;}
#about {background:transparent url(data:image/png;base64,) no-repeat !important;}

a#grid:active {background-image: url(data:image/png;base64,) !important;}
#calendar:active {background-image: url(data:image/png;base64,) !important;}
#mosaic:active {background-image: url(data:image/png;base64,) !important;}
#trash:active {background-image: url(data:image/png;base64,) !important;}
#tools:active {background-image: url(data:image/png;base64,) !important;}
#about:active {background-image: url(data:image/png;base64,) !important;}

/* grid View */
.grid #content li span.title {
font-family: Verdana, Lucida Grande, sans-serif !important;
font-size: 10px !important;}

.grid #content li span.url {
font-family: Lucida Grande, sans-serif !important;
font-size: 10px !important;
text-decoration: none !important;}

/* calendar View */
#content {
color: #777 !important;
font-family: Tahoma, Lucida Grande, sans-serif !important;
font-size: 12px !important;}

/* details View */
#detail_url {
text-decoration: none !important;}

#detail_title {
font-family: Tahoma, Lucida Grande, sans-serif !important;
font-size: 14px !important;}

/* Footerbar */
#footer {
font-family: Trebuchet MS, sans-serif !important;
font-size: 12px !important;
color: #fff !important;
text-shadow: #363636 0 1px !important;
/*letter-spacing: 0.02em !important;*/}

a.button {
background: none !important;
border: none !important;
color: #FFF !important;
font-size: 12px !important;}
}
