css:
@-moz-document domain("youtube.com") {
ytd-button-renderer.style-suggestive[is-paper-button] paper-button.ytd-button-renderer {
    border: 1px solid #f00;
    padding: var(--yt-button-padding-minus-border);
}
ytd-button-renderer.style-suggestive[is-paper-button] {
    -yt-formatted-string-deemphasize-color: rgb(255 0 0 / .6);
    background-color: transparent;
    color: #fff;
}
ytd-mini-guide-entry-renderer[active] .guide-icon.ytd-mini-guide-entry-renderer {
    color: #f00;
}
ytd-guide-entry-renderer[active] .guide-icon.ytd-guide-entry-renderer {
    color: #f00;
}
paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button {
    background-color: #f00;
}
ytd-button-renderer.style-blue-text[is-icon-button] {
    color: #f00;
}
ytd-button-renderer.style-blue-text[is-paper-button] {
    -yt-formatted-string-deemphasize-color: var(--yt-spec-icon-disabled);
    color: #f00;
}
a.ytd-mini-guide-entry-renderer:hover, a.ytd-mini-guide-entry-renderer:focus {
    background-color: rgb(255 0 0 / .1);
}
ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer:not(:last-child) {
    border-bottom: 1px solid rgb(255 0 0 / 1);
}
yt-icon-button.ytd-masthead {
    -yt-icon-button-icon-width: 24px;
    -yt-icon-button-icon-height: 24px;
    color: #f00;
    width: 40px;
    height: 40px;
    padding: 8px;
    margin-right: 8px;
    box-sizing: border-box;
}
ytd-section-list-renderer:not([hide-bottom-separator]):not([page-subtype=history]):not([page-subtype=memberships-and-purchases]):not([page-subtype=ypc-offers]) #contents.ytd-section-list-renderer > *.ytd-section-list-renderer:not(:last-child):not(ytd-page-introduction-renderer):not([item-dismissed]).ytd-section-list-renderer {
    border-bottom: 1px solid rgb(255 0 0 / .1);
}
#sections.ytd-guide-renderer > *.ytd-guide-renderer:not(:last-child) {
    border-bottom: 1px solid rgb(255 0 0 / .1);
}
ytd-guide-collapsible-section-entry-renderer.ytd-guide-section-renderer:not(:first-child) {
    border-top: 1px solid rgb(255 0 0 / .1);
    margin-top: 12px;
    padding-top: 12px;
}
ytd-app:not([background-color-update_]) app-drawer.ytd-app:not([persistent]).ytd-app #header.ytd-app {
    border-bottom: 1px solid rgb(255 0 0 / .1);
}
ytd-notification-renderer.unread #new.ytd-notification-renderer {
    background-color: #f00;
}
#sections.ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer:not(:last-child) {
    border-bottom: 1px solid rgb(255 0 0 / .1);
}
#items.yt-multi-page-menu-section-renderer > *.yt-multi-page-menu-section-renderer:hover {
    background-color: rgb(255 0 0 / .1);
}
ytd-compact-link-renderer[compact-link-style=compact-link-style-type-settings-sidebar][active] {
    background-color: rgb(255 0 0 / .1);
}
.badge-style-type-featured.ytd-badge-supported-renderer {
    background: #f00;
    color: #fff;
}
yt-icon.ytd-badge-supported-renderer {
    width: var(--ytd-badge-icon-size, 14px);
    height: var(--ytd-badge-icon-size, 14px);
    color: #f00;
}
.ytd-menu-renderer[button-renderer].ytd-menu-renderer:hover yt-icon.ytd-menu-renderer, #button.ytd-menu-renderer:hover yt-icon.ytd-menu-renderer {
    color: #f00;
}
paper-ripple.yt-icon-button {
    opacity: 0.6;
    color: #f00;
}
ytd-multi-page-menu-renderer {
    display: inline-block;
    background: var(--yt-spec-brand-background-primary);
    border: 1px solid rgb(255 0 0 / .1);
    border-top: none;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
}
a.yt-simple-endpoint.yt-formatted-string {
    color: #f00;
    text-decoration: var(--yt-endpoint-text-regular-decoration, none);
}
a.yt-simple-endpoint.yt-formatted-string:visited {
    color: #f00;
}
a.yt-simple-endpoint.yt-formatted-string:hover {
    color: #f00;
}
paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button {
    background-color: #f00;
}
ytd-sentiment-bar-renderer[activated_] #like-bar.ytd-sentiment-bar-renderer {
    background-color: #f00;
}
ytd-toggle-button-renderer.style-default-active[is-icon-button] {
    color: #f00;
}
a:-webkit-any-link {
    color: #f00;
    cursor: pointer;
}
yt-icon.ytd-pinned-comment-badge-renderer {
    -iron-icon-height: 14px;
    -iron-icon-width: 14px;
    margin-right: 4px;
    margin-bottom: 1px;
    fill: #f00;
}
.badge-style-type-simple.ytd-badge-supported-renderer {
    background: rgb(255 0 0 / 1);
    color: #fff;
}
element.style {
    -ytd-author-comment-badge-background-color: #f00;
    -ytd-author-comment-badge-name-color: #ffffff;
    -ytd-author-comment-badge-icon-color: #ffffff;
}
paper-ripple.paper-button {
    color: rgb(255 0 0 / .6);
}
paper-button.ytd-subscribe-button-renderer {
    background-color: #f00;
    border-radius: 2px;
    color: var(--yt-spec-static-brand-white);
    padding: var(--yt-button-padding);
    margin: auto var(--ytd-subscribe-button-margin, 4px);
    white-space: nowrap;
    font-size: var(--ytd-tab-system_-_font-size);
    font-weight: var(--ytd-tab-system_-_font-weight);
    letter-spacing: var(--ytd-tab-system_-_letter-spacing);
    text-transform: var(--ytd-tab-system_-_text-transform);
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
}
.more-button.ytd-comment-replies-renderer, .less-button.ytd-comment-replies-renderer {
    color: #f00;
    -iron-icon_-_margin-left: -6px;
    -iron-icon_-_margin-bottom: initial;
    -iron-icon_-_margin-right: initial;
    -iron-icon_-_width: initial;
    -iron-icon_-_height: initial;
    -iron-icon_-_margin-top: initial;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    display: -ms-inline-flexbox;
    display: -webkit-inline-flex;
    display: inline-flex;
}
yt-button-renderer #button.yt-button-renderer {
    color: #f00;
    background-color: transparent;
    text-transform: inherit;
}
ytd-video-secondary-info-renderer {
    display: block;
    border-bottom: 1px solid rgb(255 0 0 / .1);
    margin-bottom: 24px;
    padding-bottom: 16px;
}
ytd-metadata-row-header-renderer[has-divider-line] {
    margin-top: 16px;
    border-top: 1px solid rgb(255 0 0 / .1);
}
ytd-video-primary-info-renderer {
    -yt-button-icon-size: 36px;
    display: block;
    border-bottom: 1px solid rgb(255 0 0 / .1);
    padding: 20px 0 8px 0;
}
#placeholder-area.ytd-comment-simplebox-renderer {
    cursor: text;
    padding-bottom: 4px;
    border-bottom: 1px solid rgb(255 0 0 / .1);
    -webkit-flex: 1;
    flex: 1;
}
ytd-author-comment-badge-renderer {
    -ytd-author-comment-badge-name-color: var(--yt-spec-text-primary);
    height: 20px;
    background-color: #f00;
    border-radius: 12px;
    margin-bottom: 2px;
    max-width: 100%;
    box-sizing: border-box;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    display: -ms-inline-flexbox;
    display: -webkit-inline-flex;
    display: inline-flex;
}
#search-icon-legacy.ytd-searchbox {
    cursor: pointer;
    width: 65px;
    border: 1px solid rgb(255 0 0 / .1);
    background-color: var(--ytd-searchbox-legacy-button-color);
    border-radius: 0 2px 2px 0;
    margin: 0;
    -yt-icon-button-icon-width: 24px;
    -yt-icon-button-icon-height: 24px;
}
.more-button.ytd-video-secondary-info-renderer, .less-button.ytd-video-secondary-info-renderer {
    background-color: #f00;
    border-radius: 12px;
    color: var(--yt-spec-static-brand-white);
    padding: 2px 8px;
    margin: auto var(4px);
    white-space: nowrap;
    font-size: 12px;
    font-weight: var(--ytd-tab-system_-_font-weight);
    letter-spacing: var(--ytd-tab-system_-_letter-spacing);
    text-transform: var(--ytd-tab-system_-_text-transform);
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
}
#shop-button.ytd-merch-shelf-item-renderer {
    background-color: #f00;
    border-radius: 2px;
    color: #ffffff;
    padding: 8px 12px;
    font-size: 1.3rem;
    font-weight: 500;
    letter-spacing: 0.007px;
    text-transform: uppercase;
}
#container.ytd-searchbox {
    position: relative;
    align-items: center;
    background-color: var(--yt-searchbox-background);
    border: 1px solid rgb(255 0 0 / .1);
    border-right: none;
    border-radius: 2px 0 0 2px;
    box-shadow: inset 0 1px 2px var(--ytd-searchbox-legacy-border-shadow-color);
    color: var(--yt-searchbox-text-color);
    padding: 2px 6px;
    -webkit-flex: 1;
    flex: 1;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
}
ytd-channel-list-sub-menu-renderer.ytd-section-list-renderer {
    display: block;
    padding: 16px 0;
    border-bottom: 1px solid rgb(255 0 0 / .1);
}
ytd-feed-filter-chip-bar-renderer {
    background-color: var(--yt-spec-brand-background-primary);
    border-top: 1px solid rgb(255 0 0 / .1);
    border-bottom: 1px solid rgb(255 0 0 / .1);
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
}
yt-chip-cloud-chip-renderer[chip-style="STYLE_DEFAULT"], yt-chip-cloud-chip-renderer[chip-style="STYLE_HOME_FILTER"] {
    background-color: #f00;
    border: 1px solid var(--yt-spec-10-percent-layer);
    color: var(--yt-spec-text-primary);
    padding: 0 var(--ytd-margin-3x);
    transition: background-color 0.5s cubic-bezier(0.05, 0.0, 0.0, 1);
}
ytd-guide-entry-renderer[active] {
    background-color: rgb(255 0 0 / .1);
}
#newness-dot.ytd-guide-entry-renderer {
    width: 4px;
    height: 4px;
    border-radius: 50%;
    background-color: #f00;
    margin: 0 6px;
    display: none;
}
}
