@-moz-document url-prefix("https://www.polygon.com/users/"), url-prefix("https://www.polygon.com/admin") {
body {
    background: #0f0f0d !important;
}
body #container {
	background: #0e0e0e !important;
}
.m-chorus-bar .m-chorus-bar__header, .nav-main {
	background: #0e0e0e;
	border-color: #131313;
}
a, a:visited, .editorial a, .editorial a:link, .editorial a:visited, .form-page .form-note {
	color: #bbb;
}
.nav-main li a:hover {
	background: #111;
}
.form-page label, .form-page span.label {
	color: #ccc;
}
#main input[type='text'], #main input[type='password'], #main textarea {
	background: #0a0a0a;
	border-color: #151515 !important;
	color: #eee;
}
[src='https://www.polygon.com/images/polygon/placeholders/profile/large.png'] {
	filter: invert(100);
}
.m-chorus-bar .m-chorus-bar__menu {
	background: #0e0e0e;
	border-color: #111;
}
.m-chorus-bar .m-chorus-bar__menu li a {
