css:
@-moz-document domain("typedrawers.com") {
body {
  padding: 0;
  font-size: 90%;
}

.Row {
  width: 1280px;
}

body > #Frame > .Head {
  padding: 16px 0 32px;
}


#Head {
  background: none;
}

#Head .Row {
  border-bottom: 1px solid #ddd;
  padding-bottom: 16px;
}

#Head a {
  color: grey;
}

#Head .SiteSearch {
  margin-top: 2px;
}

#Head .SiteSearch .InputBox {
  border: 1px solid #ddd;
  border-radius: 3px;
  padding: 4px 28px 4px 8px;
  font-size: 13px;
}

.SiteSearch .Button {
  top: 6px;
  right: 6px;
}

#Head .SiteTitle {
  margin-right: 156px;
  font-size: 26px;
  line-height: 29px;
  letter-spacing: -0.03em;
}

#Head .SiteTitle a {
  color: #38abe3;
}

.SiteMenu {
  display: inline-block;
  white-space: nowrap;
  top: -4px;
  position: relative;
}

.SiteMenu li {
  margin-right: 8px;
}

.SiteMenu a {
  font-size: 13px;
  padding: 6px 4px;
}

.ContentColumn {
  margin-left: 320px;
}

#Panel {
  width: 240px; 
}

.MeBox {
  margin-top: -18px;
}

.BoxNewDiscussion {
  margin-bottom: 32px;
}

.PanelInfo li {
  padding: 6px 4px;
}

.Count {
  background-color: transparent;
  color: inherit;
  letter-spacing: 0.5px;
}

#Body {
  padding-top: 8px;
}

.BreadcrumbsWrapper {
  margin-left: 320px;
}

.HomeCrumb {
  margin-right: 5px;
}

.Crumb {
  margin: 0 4px;
}

.Discussion .PageTitle h1,
.HomepageTitle {
  font-size: 28px;
  letter-spacing: -0.02em;
  margin-bottom: 12px;
}

.ItemDiscussion .Message,
.Message {
  font-size: 18px;
  line-height: 1.75;
}

.DataList .Item,
.NarrowList .Item {
  padding: 14px 0 20px;
}

.Item.Read {
  background: none;
}

.Item.Read .Title a {
  color: #344a54;
}

.HasNew {
  padding: 3px 6px;
  text-transform: uppercase;
  border-radius: 4px;
  letter-spacing: 0.5px;
}

.Reactions {
  font-size: 14px;
  margin-top: 18px;
}

.Reactions .Count {
  color: white;
  background: #38abe3;
}

.ReactButton {
  color: grey;
  margin: 0 6px;
}

.ReactSprite {
  vertical-align: bottom;
}

.Pager a, .Pager span {
  padding: 6px 8px;
  margin: 0 8px;
  font-size: 1.1em;
}

#Foot {
  padding: 64px 0 24px;
}

.Button {
  padding: 8px 14px;
}
}
